vec4 nois(in vec3 x) {
	vec3 p = floor(x);
    vec3 w = fract(x);
    vec3 u = w*w*w*(w*(w*6.0-15.0)+10.);
    vec3 du = 30.*w*w*(w*(w-2.0)+1.0);
    float a = hash3(p+vec3(0, 0, 0));
    float b = hash3(p+vec3(1, 0, 0));
    float c = hash3(p+vec3(0, 1, 0));
    float d = hash3(p+vec3(1, 1, 0));
    float e = hash3(p+vec3(0, 0, 1));
    float f = hash3(p+vec3(1, 0, 1));
    float g = hash3(p+vec3(0, 1, 1));
    float h = hash3(p+vec3(1, 1, 1));
    float k0 = a;
    float k1 = b - a;
    float k2 = c - a;
    float k3 = e - a;
    float k4 = a - b - c + d;
    float k5 = a - c - e + g;
    float k6 = a - b - e + f;
    float k7 = - a + b + c - d + e - f - g + h;
    return vec4(-1.0+2.0*(k0 + k1*u.x + k2*u.y + k3*u.z + k4*u.x*u.y + k5*u.y*u.z + k6*u.z*u.x + k7*u.x*u.y*u.z),
                2.0* du * vec3( k1 + k4*u.y + k6*u.z + k7*u.y*u.z, 
                                k2 + k5*u.z + k4*u.x + k7*u.z*u.x, 
                                k3 + k6*u.x + k5*u.y + k7*u.x*u.y));
}
vec4 fbm(in vec3 x, int octives) {
	float f = 1.98;
    float s = 0.6;
    float a = 0.0;
    float b = 0.3;
    vec3 d = vec3(0);
    float o = 0.6;
    mat3 m = mat3(o, 0, 0, 0, o, 0, 0, 0, o);
    for (int i = 0; i < octives; i ++) {
        vec4 n = nois(x);
        a += b*n.x;
        d += b*m*n.yzw;
        b *= s;
        x = f*m*x;
        m = f*m*m;
    }
    return vec4(a, d);
}
vec3 rus(vec2 seed) {
    float u = hash(seed);
    float v = hash(seed+2.);
    float theta = u * 2.0 * 3.1415;
    float phi = acos(2.0 * v - 1.0);
    float r = pow(hash(seed+10.), 0.33333333);
    float sinTheta = sin(theta);
    float cosTheta = cos(theta);
    float sinPhi = sin(phi);
    float cosPhi = cos(phi);
    float x = r * sinPhi * cosTheta;
    float y = r * sinPhi * sinTheta;
    float z = r * cosPhi;
    return vec3(x, y, z);
}
int sc = 7;
sphere scene[7];
bool hitSphere(vec3 center, float radius, ray r, float t_min, float t_max, out rec re, mater mat) {
    vec3 oc = r.origin-center;
    float a = dot(r.dir, r.dir);
    float b = dot(oc, r.dir);
    float c = dot(oc, oc) - radius*radius;
    float disc = b*b - a*c;
    if (disc > 0.) {
        float temp = (-b - sqrt(b*b-a*c)) / a;
        if (temp < t_max && temp > t_min) {
            vec3 p = r.origin+r.dir*temp;
            re.t = temp;
            re.p = p;
            re.normal = (p - center) / radius;
            re.mat = mat;
        	return true;
        }
        temp = (-b + sqrt(b*b-a*c)) / a;
        if (temp < t_max && temp > t_min) {
            vec3 p = r.origin+r.dir*temp;
            re.t = temp;
            re.p = p;
            re.normal = (p - center) / radius;
            re.mat = mat;
        	return true;
        }
    }
    return false;
}
bool hitScene(ray r, float tmin, float tmax, out rec hit) {
	bool hitIt = false;
    float closest = tmax;
    for (int i = 0; i < sc; i += 1) {
        if (hitSphere(scene[i].center, scene[i].radius, r, tmin, closest, hit, scene[i].mat)) {
        	hitIt = true;
            closest = hit.t;
        };
    }
    return hitIt;
}
void diffused(out ray r, vec2 seed, rec hit, inout vec3 c) {
    vec3 target = hit.p + hit.normal + rus(seed);
    r.origin = hit.p;
    r.dir = target - hit.p;
    c *= hit.mat.color;
}
void gloss(inout ray r, vec2 seed, rec hit, inout vec3 c) {
    r.origin = hit.p;
    r.dir = (reflect(r.dir, hit.normal)+(rus(seed)*hit.mat.ev))/hit.mat.ev;
    c *= hit.mat.color;
}
void emit(inout ray r, vec2 seed, rec hit, inout vec3 c) {
    c = hit.mat.color*10.;
}
vec3 color(vec3 ro, vec3 rd, vec2 seed) {
    vec3 c = vec3(1.0);
    vec3 roo = ro;
    vec3 rdd = rd;
    for (int i = 0; i < 100; i += 1) {
        rec hit;
        ray r = ray(roo, rdd);
        if (hitScene(r, 0.001, 10000.0, hit)) {
            ray rrr = r;
            if (hit.mat.mat == 0) {diffused(rrr, seed, hit, c);}
            if (hit.mat.mat == 1) {gloss(rrr, seed, hit, c);}
            if (hit.mat.mat == 2) {emit(rrr, seed, hit, c);}
            roo = rrr.origin;
            rdd = rrr.dir;
        } else {
            vec3 dir = normalize(rdd);
            float t = 0.5*(dir.y+1.);
            c *= ((1.-t)*vec3(1)+t*vec3(0.5, 0.7, 1.0))/1.;
            break;
        }
	}
    return c;
}
