#define WITH_CUBEMAP
#define MIX

//---------------------

#define PI 3.14159
#define EPS 0.001
#define MAX_DIST 20.

float sdTorus(vec3 p) {
  	return length(vec2(length(p.xz) - 1., p.y)) - 0.03;  
}

float sdOctahedron(vec3 p, float s) {
  	p = abs(p);
  	return (p.x + p.y + p.z - s) * 0.58;
}

float sdPlane(vec3 p, vec3 n, float off) {
  return dot(p, n) + off;
}

mat2 rotMat(float k) {
    float c = cos(k);
    float s = sin(k);
    return mat2(c, -s, s,  c);
}

vec3 swirl(vec3 p, float k) {
    float c = cos(k * p.x);
    float s = sin(k * p.x);
    return vec3(mat2(c, -s, s, c) * p.yz, p.x);
}

float calcDist(vec3 p) {
    float dTorus1 = sdTorus(0.3 * swirl(p.yxz, 3. * sin(iTime)));
    float dTorus2 = sdTorus(0.3 * swirl(p, 3. * sin(iTime)));

    p.xz *= rotMat(iTime);
    p.xy *= rotMat(PI / 6.);
	float dOctahedron = sdOctahedron(p, 1.);
    float dPlane = sdPlane(p, vec3(0, 1, 0), -0.35);

    #ifdef MIX
    return min(mix(dTorus1, dTorus2, sin(iTime) * 0.5 + 0.5), max(dOctahedron, dPlane));
    #else
    return min(dTorus1, max(dOctahedron, dPlane));
    #endif
}

float rayMarch(vec3 rayO, vec3 rayD) {
    float distFromO = 0.;
    for (int i = 0; i < 100; ++i) {
	    float dS = calcDist(rayO + rayD * distFromO);
        distFromO += dS;
        if (dS < EPS || distFromO > MAX_DIST) break;
    }
    return distFromO;
}

vec3 calcNormal(vec3 p) {
    float d = calcDist(p);
	return normalize(vec3(d - calcDist(p - vec3(EPS, 0,  0 )),
					  	  d - calcDist(p - vec3( 0, EPS, 0 )),
					   	  d - calcDist(p - vec3( 0,  0, EPS))));
}

vec3 calcLight(vec3 fragPos, vec3 lightPos, vec3 lightCol, vec3 camDir) {    
	vec3 normal = calcNormal(fragPos);
    vec3 lightDir = normalize(lightPos - fragPos);

    vec3 ambient = vec3(0.1);
    vec3 diffuse = vec3(max(dot(normal, lightDir), 0.0));
    vec3 specular = vec3(0.15 * pow(max(dot(-camDir, reflect(-lightDir, normal)), 0.), 16.));

    return lightCol * (ambient + diffuse + specular);
}

