    vec2 xy = (fragCoord.xy - iResolution.xy / 2.) / min(iResolution.x, iResolution.y);

    vec3 camPos = vec3(0, 0, -10);
	vec3 camDir = normalize(vec3(xy, 1.));

    float dist = rayMarch(camPos, camDir);

    vec3 col =  vec3(0.87, 0.92, 0.99) + vec3(0.009 / length(xy + vec2(0., 0.015)));

    if (dist < MAX_DIST) { 
        #ifdef WITH_CUBEMAP
        col = texture(iChannel0, reflect(camDir, calcNormal(camPos + dist * camDir))).xyz * vec3(0.95, 1.1, 1.4);
