    col += (rd.y*.5)*vec3(.1,.45,.32);
    col += (1.-sat(abs(rd.y*5.)))*vec3(0.4,0.2,0.5)*1.5*(1.-sat(abs(rd.x)));
    col += (1.-sat(abs(rd.x*3.)))*vec3(0.4,0.2,0.5);
    col *= 1.-sat((lenny(uv)-.5)*5.);
    col = vec3(sat(col.x),sat(col.y), sat(col.z));
    col += vec3(.1,.12,.2)*.5*pow(sat(d/20.),.6);
    col += (1.-sat(lenny(uv*.2)))*texelFetch(iChannel0, ivec2(5,0),0).x*vec3(.5,.3,.6);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;
    uv.y *= cos(uv.x*3.14159265*.5);
	vec3 acc;
    float accV;

    int aaLvl = 5;
    for (int i = 0; i<aaLvl; ++i)
    {
    	float dist = mix(2., 20., sin(iTime*.2)*.5+.5);
    	float spinSpd = 0.2;
        float off = float(i)*0.001*length(uv*5.);

            // Time
            if (iTime > 9.3) spinSpd *= -1.;
        	if (iTime > 27.5) spinSpd *= -1.;
        	if (iTime > 45.8) spinSpd *= -1.;
	if (iTime > 68.7) spinSpd *= -1.;
            if (iTime > 73.5) spinSpd *= -1.;
        if (iTime > 78.) spinSpd *= -1.;

        if (iTime > 96.) spinSpd *= -1.;
        if (iTime > 101.) spinSpd *= -1.;

        if (iTime > 114.) spinSpd *= -1.;
        if (iTime > 119.) spinSpd *= -1.;
        if (iTime > 155.5) spinSpd *= 0.;
        if (iTime > 165.) spinSpd = 0.2;
        if (iTime > 183.) spinSpd *= -1.;
        if (iTime > 192.) spinSpd *= -1.;
        if (iTime > 196.5) spinSpd *= -1.;
        if (iTime > 201.) spinSpd *= -1.;
        if (iTime > 210.5) spinSpd *= -1.;
        if (iTime > 224.) spinSpd *= -2.;
        if (iTime > 229.) spinSpd *= -2.;
        if (iTime > 233.) spinSpd *= -2.;
        if (iTime > 238.) spinSpd = 0.2;
        if (iTime > 256.) spinSpd *= 0.;
        if (iTime > 270.) spinSpd = 0.1;
        if (iTime > 274.5) spinSpd *= -1.;
        if (iTime > 279.) spinSpd *= -1.;
        if (iTime > 283.5) spinSpd *= -1.;
        if (iTime > 288.) spinSpd *= -1.;
            //
    	vec3 ro = vec3(dist*sin(spinSpd*iTime+off), mix(1.,3.,sat(sin(iTime*spinSpd)*.5+.5)), dist*cos(spinSpd*iTime+off));
    	vec3 ta = vec3(0., 1., 0.);
    	vec3 rdo = normalize(ta-ro);
    	vec3 rd = getCam(rdo, uv);//+aa[i]/(iResolution.xx));
    	float aaV = 1.;//1.-sat(length(uv)*.5);
    	vec3 col = rdr(uv, ro, rd)*aaV;
        //vec3 rd2 = getCam(rdo, uv*(1.-float(i)/400.));
        vec3 colBlur = col*aaV*3.;//rdr(uv, ro, rd2)*aaV*3.;
        accV+= aaV*2.;
		acc += col+colBlur;
    }
    vec3 col =acc/accV;

    vec3 col2 = mix(fwidth(length(col))*col, col, 1.-sat(lenny(uv*vec2(3.,1.))));
    col2 = pow(col2,vec3(1.15));

    float sndVal = texelFetch(iChannel0, ivec2(int((uv.x+.5)*15.),0),0).x;
    if (iTime > 82.7)
    col2 = mix(col2.yzx, col2,sat(sndVal*2.-1.));
    fragColor = vec4(col2,1.0);
}

