// Create the camera dir, camera code is 3D, not really
void getCameraDir( in vec2 uv, in vec3 pos, in vec3 lookAt, out vec3 rayDir )
{
	vec3 camPos = pos;
	vec3 camLookAt = lookAt;

    float ratio = iResolution.x / iResolution.y;


    const float fov = 80.;
    float halfWidth = tan(radians(fov) * .5);
    float halfHeight = halfWidth / ratio;

    vec3 w = normalize(camPos - camLookAt);
    vec3 upVector = vec3(0, 1, 0 );

    vec3 u = cross(upVector, w);
    vec3 v = cross(w, u);

    vec2 p = 2.* uv - 1.;
    vec3 dir = p.x * u* halfWidth  + p.y * v* halfHeight - w ;

    rayDir = normalize( dir );
}

void mainImage( out vec4 fragColor, in float2 fragCoord )
{    
    vec2 uv = fragCoord / iResolution.xy,
         M  = iMouse.xy / iResolution.xy;
    if (M==vec2(0)) M = vec2(.5);
  //fragColor = texture(iChannel0,uv).x*7.*vec4(1,-1,0,0); return;   // ( debug: show map )
  //float seed = float(iTime) + hash12( uv );

    float3 camPos = texelFetch( iChannel1, ivec2(0), 0 ).xyz, camDir;
    float3 camLookAt = vec3( M.x, .5-M.y, .5 );

    float3 Light =float3( .5+.25*cos(-3.*iTime), 0, .5+.25*sin(-3.*iTime) );    // light position

    vec4 outColor = vec4( 0 );   
    //vec3 camPos = vec3( 0.5, 0, 0.1), camDir;

    for ( int aa = 0; aa < AA*AA; aa ++ )                                       // antialiasing loop
    {
        vec2 offset = ( vec2(aa%AA, aa/AA ) /float(AA) - .5 ) / iResolution.xy; // AA offset
        getCameraDir( uv + offset, camPos, camLookAt, camDir );
        vec4 sdfMap;
        vec3 rayPos;
        float t = 0., eps = 1./700.;    
        for (int i = 0; i < 250; ++i)
        {
            rayPos = camPos + t * camDir;
            sdfMap = texture( iChannel0, rayPos.xz );
            sdfMap.xy = merge( sdfMap.xy, vec2( rayPos.y+.05, -1) );            // floor
            sdfMap.xy = merge( sdfMap.xy, vec2(-rayPos.y+.05, -2) );            // ceiling
            sdfMap.xy = merge( sdfMap.xy, vec2(length(Light-rayPos)-.01, -3));  // light
            if (sdfMap.x < eps)  break;
            t += sdfMap.x;                                 // sphere marching 
         // t += .005;                                     // 1 texel per step would be 1/700 = .0015
        }

        if ( sdfMap.x > eps ) continue;                    // escape to infinity
      //fragColor = vec4((3.+sdfMap.y)/5.); return;        // ( debug: show ids )
        int o = int(sdfMap.y);

        float2 uv = o < 0                                  // texture coords
            ? rayPos.xz                                    //   floor & ceiling
            : vec2(sdfMap.z,.5+.5*rayPos.y);               //   walls
        float3 N  = o < 0                                  // normal
            ? vec3(0, -sign(rayPos.y), 0 )                 //   floor & ceiling
            : vec3( cos(sdfMap.w), 0, sin(sdfMap.w) );     //   restore normale
        //  fragColor = vec4(.5+.5*N,0); return;           // ( debug: show normals )
        float d = length(camPos-rayPos),
            illu = 0.                                                         // ambiant
                 + 1.5* max(0., dot( N, normalize(Light-rayPos) ))            // light source
                 +  .6* max(0., dot( N, normalize(camPos-rayPos) )*.1/(d*d) );// head lamp
        outColor +=
            o==-3 ? vec4( 1 )                                                 // light source
          : o==-1 ? vec4( .5, .5, 1, 1 ) * illu * texture(iChannel3, uv )     // floor
          : o==-2 ? vec4( .5, .5, 1, 1 ) * illu * texture(iChannel2, 4.*uv )  // ceiling
          : o== 0 ? vec4( 1 , 0 , 0, 1 ) * illu * texture(iChannel2, vec2(6,12)*uv ) // walls
          : o== 1 ? vec4( 0 , 1 , 0, 1 ) * illu * texture(iChannel3, uv )     // column
          : vec4(0);
    }
    outColor /= float(AA*AA);
    fragColor = outColor;
}

