	fragColor = vec4(0.5);
    if (abs(uv.x-0.5) > 0.5 || abs(uv.y-0.5) > 0.5) return;
    bitstring board = vec2bitstring(texelFetch(iChannel0, ivec2(0,0),0));
    uv *= float(N);
	int i = int(uv.x);
    int j = int(uv.y);
    vec3 col = i%2 == j%2 ? vec3(0) : vec3(1);
    float pwidth = fwidth(uv.x);
    if (j == get(board,i)) {
        col = mix(vec3(1,0,0),col,
                  smoothstep(0.0,2.0*pwidth,
                             length(uv-vec2(i,j)-0.5)-0.4));   
    }
    fragColor = vec4(col,1.0);
}

