const int N = 12; // The size of the board, 16 is the largest supported.
const int M = 4; // Number of bits for each position, 32%M = 0 or NM <= 32
float SPEED = 2.0; // Timesteps per second, 0.0 for flat out.
int MAXITERATIONS = 100000; // Limit number of iterations to prevent shader timeout
#define bitstring uvec4
int get(bitstring s, int i);

// s represents a permutation of N items.

// Costas arrays: https://oeis.org/A008404
bool costas(bitstring s, int i) {
  if (i < 2) return true;
  int ival = get(s,i);
  for (int step = 1; step < i; step++) {
    int diff = ival-get(s,i-step);
    for (int j = i-1; j >= step; j--) {
      int diff2 = get(s,j)-get(s,j-step);
      if (diff == diff2) return false;
    }
  }
  return true;
}

// A permutation is valid if check(s,i) is
// true for all 0 <= i < N.
// eg. for derangements, check((1,3,0,2,4),i) 
// is true for i < 4 but false for i = 4.
// or for N-queens, get(s,i) is the row occupied by the
// queen in column i and check(s,i) is true if queen
// in column i is not under diagonal attack by any
// queen in column j < i.
bool check(bitstring s, int j) {
    // Uncomment another one of these lines for a different permutation problem.
    // (and set time back to 0 to ensure current state is valid). 
    return costas(s,j);
    //return true; // Factorial: https://oeis.org/A000142
    //return get(s,j) != j; // Derangement //https://oeis.org/A000166
    //return get(s,j) > j || get(s,get(s,j)) == j; // Involution: https://oeis.org/A000085
    //return get(s,j) != j && get(s,j) != (j+1)%N; // Mnage: https://oeis.org/A000179
    //return abs(j-get(s,j)) > 1; // Restricted range
    for (int i = 0; i < j; i++) {
        // https://oeis.org/A000170
        // n-queens - no bishop attack
        // permutation guarantees no rook attack
        if (abs(get(s,j)-get(s,i)) == abs(j-i)) {
            return false;
		}
    }
    return true;
}

bitstring vec2bitstring(vec4 s) {
    return floatBitsToUint(s);
}

vec4 bitstring2vec(bitstring s) {
    return uintBitsToFloat(s);
}

const uint MASK = (1U<<M)-1U;

int get(bitstring s, int i) {
    int word = M*i/32, offset = M*i%32;
    uint t = s[word];
    return int((t >> offset) & MASK);
}

bitstring set(bitstring s, int i, int x) {
    int word = M*i/32, offset = M*i%32;
    uint t = s[word];
    t &= ~(MASK << offset);
    t |= uint(x) << offset;
    s[word] = t;
    return s;
}

bitstring swap(bitstring s, int i, int j) {
    int t = get(s,i); 
    s = set(s,i,get(s,j)); 
	s = set(s,j,t);
	return s;
}

bitstring reverse(bitstring s,int i,int j) {
    j -= 1;
    while (i < j){
        s = swap(s,i,j);
        i += 1; j -= 1;
    }
    return s;
}

