// Given a permutation known to be valid at j < i,
// and assumed to be ascending order for j >= i,
// find the first non-valid value j >= i, and reverse
// the order of the elements after j (with the effect
// that permutations with that prefix are skipped).
int checkvalid(inout bitstring s, int i) {
    for ( ; i < N; i++) {
        if (!check(s,i)) {
            s = reverse(s,i+1,N);
            break;
        }
    }
    return i;
}

bitstring nextperm(bitstring s) {
    for (int count = 0; count < MAXITERATIONS; count++) {
        int i = N-1;
        // Find longest descending suffix
        while (i > 0 && get(s,i-1) >= get(s,i)) i--;
        if (i == 0) { 
            // whole sequence is descending
            // so change to all ascending
            s = reverse(s,0,N);
            // and find first invalid element
            // if all valid, return
            if (checkvalid(s,0) == N) return s;
        } else {
            // find element to increment
        	int j = N-1;
        	while (get(s,j) <= get(s,i-1)) j--;
        	s = swap(s,i-1,j);
            if (check(s,i-1)) {
	            // i-1 can't be last element
    	        // first permutation with those suffix elements
        		s = reverse(s,i,N);
            	// find invalid element & reorder
        		if (checkvalid(s,i) == N) return s;
        	}
        }
   }
   return bitstring(0); //timeout!
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	if (ivec2(fragCoord) == ivec2(1,0)) {
        float lastInterval = 0.0;
        if (iFrame != 0) {
            float lastTime = texelFetch(iChannel0, ivec2(1,0), 0).x;
            lastInterval = iTime-lastTime;
        }
        fragColor = vec4(iTime,lastInterval,0,0);
        return;
    }

    if (ivec2(fragCoord) == ivec2(0,0)) {
        bitstring board = vec2bitstring(texelFetch(iChannel0, ivec2(0,0),0));
        float lastTime = -1.0;
        // It seems that the buffer data can get cleared on a resize
        // without triggering the iFrame == 0 test. 
        if (iFrame == 0 || board == bitstring(0)) {
            for (int i = 0; i < 16; i++) {
            	board = set(board,i,i);
            }
            if (checkvalid(board,0) == N) lastTime = 0.0;
        } else {
            lastTime = texelFetch(iChannel0, ivec2(1,0), 0).x;
        }
        if (SPEED == 0.0 || floor(SPEED*lastTime) != floor(SPEED*iTime)) {
            board = nextperm(board);
        }
    	fragColor = bitstring2vec(board);
    }   
}

