float sdTilableCircle(vec2 p, vec2 o)
{
    //neighbour offsets
    vec2 o1 = vec2(-1,-1);
    vec2 o2 = vec2(-1,0);
    vec2 o3 = vec2(-1,1);
    vec2 o4 = vec2(0,-1);
    vec2 o5 = vec2(0,0);
    vec2 o6 = vec2(0,1);
    vec2 o7 = vec2(1,-1);
    vec2 o8 = vec2(1,0);
    vec2 o9 = vec2(1,1);

    float radius = .1;

    float d1 = length(p + o+o1) - radius;
    float d2 = length(p + o+o2) - radius;
    float d3 = length(p + o+o3) - radius;
    float d4 = length(p + o+o4) - radius;
    float d5 = length(p + o+o5) - radius;
    float d6 = length(p + o+o6) - radius;
    float d7 = length(p + o+o7) - radius;
    float d8 = length(p + o+o8) - radius;
    float d9 = length(p + o+o9) - radius;

    return min(min(min(min(min(min(min(min(d1,d2),d3),d4),d5),d6),d7),d8),d9);
}

float sdCrossSection(vec2 p)
{
    float d1 = sdTilableCircle(p,vec2(.63,.5));
    float d2 = sdTilableCircle(p,vec2(.1,.2));
    float d3 = sdTilableCircle(p,vec2(.4,0.9));
    float d4 = sdTilableCircle(p,vec2(.9,0.7));
    float d5 = sdTilableCircle(p,vec2(.3,0.5));
    float d6 = sdTilableCircle(p,vec2(.7,0.1));    

    return min(min(min(min(min(d1,d2),d3),d4),d5),d6);
}

vec2 GetNormal(vec2 p)
{
    float d = sdCrossSection(p);
    float eps = 0.0001;
    float dx = sdCrossSection(fract(p + vec2(eps,0) + .5)-.5);
    float dy = sdCrossSection(fract(p + vec2(0,eps) + .5)-.5);

    float ddx = dx-d;
    float ddy = dy-d;

    //return vec2(1,1);
    return normalize(vec2(ddx,ddy));
    //return n;
}

float rayMarch(vec2 ro, vec2 rd, out vec2 normal)
{
    float minDist = 0.00001;
    int maxSteps = 100;
    int s = 0;

    float rayDist = 0.;
    float surfaceDist = sdCrossSection(ro);
    //for(int i = 0; i < maxSteps; i++);
    while(s < maxSteps)
    {
        if(abs(surfaceDist) < minDist)
        {
            //ray hit
            normal = GetNormal(fract(ro + rd * rayDist + .5)-.5);
            return rayDist;
        }
        s++;
        rayDist += surfaceDist;
        surfaceDist = sdCrossSection(fract(ro + rd * rayDist + .5)-.5);
    }
    return 0.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 2.;
    uv = fract(uv);
	uv -= .5;

    vec2 n;
    float d = rayMarch(uv,normalize(vec2(-1,1)), n);
    n = n * .5 + .5;

    // Output to screen
    fragColor = vec4(n * (1.-d),0.,1.0);
}

float sdf(vec3 p)
{
    float m = 1.0;
    for (float i = 0.0; i < 4.0; i++)
    {
        vec4 sphere = vec4(cos(p.y * 4.0 + iTime * i * 2.0 + i * 2.0) * 0.15 + 3.0 * i - 4.0, sin(iTime * 15.0 + i * 5.0) * 0.5 + 1.25 + sin(p.x * 4.0 + iTime * 10.0 + i * 6.0) * 0.15 + 1.2, 6.0, 1.0);
        float distanceSphere = length(p - sphere.xyz) - sphere.w;
        m = min(m, distanceSphere);
    }
    float distancePlane = p.y + cos(p.x + iTime * 2.5) * sin(p.z + iTime * 32.0) * 0.5 + cos(p.z + iTime * 32.0) * 0.5 + 1.75;
    return min(m, distancePlane);
}

vec3 normal(vec3 p)
{
    vec2 delta = vec2(0.01, 0.0);
    float dist = sdf(p);
    return normalize(dist - vec3(
        sdf(p - delta.xyy),
        sdf(p - delta.yxy),
        sdf(p - delta.yyx)
    ));
}

float raymarch(vec3 origin, vec3 direction)
{
    float distanceOrigin = 0.0;

    for (int i = 0; i < 64; i++)
    {
        vec3 point = origin + direction * distanceOrigin;
        float distanceScene = sdf(point);
        distanceOrigin += distanceScene;
        if (distanceOrigin < 0.01 || distanceOrigin > 100.0) break;
    }

    return distanceOrigin;
}

vec3 light(vec3 p, vec3 pos, vec3 col, float s)
{
    vec3 l = normalize(pos - p);
    vec3 n = normal(p);

    float dist = distance(p, pos);
    float att = 1.0 / (dist + 0.15 * dist + 0.15 * dist * dist);

    float diffuse = clamp(dot(l, n), 0.0, 1.0);

    float d = raymarch(p + n * 0.2, l);
    if (d < length(pos - p)) diffuse *= 0.1;

    return col * texture(iChannel0, n.xz).rgb * smoothstep(0.0, 1.0, diffuse) * att * s;
}

vec3 lights(vec3 p)
{
    vec3 position = vec3(0.0, 6.0, 6.0);
    position.xz += vec2(cos(iTime * 2.0), sin(iTime * 2.0)) * 2.0;

    vec3 l = vec3(0.0);

    vec3 color = vec3(0.5);

    color.r = sin(iTime * 2.0 + 50.0) * 0.25 + 1.0;
    color.g = cos(iTime * 2.0 + 150.0) * 0.25 + 1.0;
    color.b = sin(iTime * 2.0 + 200.0) * 0.25 + 1.0;

    l += light(p, position, color, 16.0);
    l += light(p, vec3(-4.0, 4.0, -1.0), vec3(0.15, 0.15, 0.8), 18.0);
    l += light(p, vec3(4.0, 4.0, -1.0), vec3(0.15, 0.8, 0.15), 18.0);

    return l;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);

    vec3 ro = vec3(0.0, 2.5, 0.0);
    vec3 rd = normalize(vec3(uv.x - cos(iTime * 29.4) * 0.01, uv.y - sin(iTime * 34.0) * 0.01, 0.5));

    float dist = raymarch(ro, rd);

    vec3 p = ro + rd * dist;
    vec3 diffuse = lights(p);

    col = diffuse;

    fragColor = vec4(col, 1.0);
}

float Random(vec2 p) {
	p = fract(p*vec2(123.34, 456.21));
    p += dot(p, p+45.32);
    return fract(p.x*p.y);
}

float T(float a) {
    float random = Random(vec2(a*532.43, a*784.34));
    float interval = clamp(random, 0.3, 0.6);
	return clamp((sin(iTime*a*1.4*random)+1.)/2., 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);

    uv *= .85;

    // scaling
    uv.x *= 4.5;
    uv.y -= .5;

    // bars
    for(float i=.0; i<64.; i=i+2.){
        // thickness
    	float width = 0.08+sin(i)*(sin(iTime*Random(vec2(i, 654.543)))*.4+.5);  
    	float edge = 0.01*(sin(iTime*Random(vec2(i, 654.543)))*.4+.5);

        //bar
        float m1 = smoothstep(width+edge, width, abs((uv.x-3.2+(i/10.))));

        col += m1 * T(2.+(i*.02));
        col += m1*.1;

        //float m2 = smoothstep(width/10.+edge/10., width/10., abs((uv.y-.2+(i/20.))));
        //col -= m2*.1;
    }
    //col = clamp(col, 0., 2.);

    // colors
    col.r -= T(.2)*1.5;
    col.g -= T(.6)*1.5;
    col.b -= T(1.4)*1.5;

    col.rgb += T(uv.x)*.03;

    col -= vec3(.4);

	// vignette
    col *= smoothstep(-.95, .0, uv.y);
    col *= smoothstep(0., -.95, uv.y);
    col *= smoothstep(-5., 1., uv.x);
    col *= smoothstep(5., -1., uv.x);
    col += col*2.;

    fragColor = vec4(col,1.0);
}

float movingDisk(in vec2 p, in vec2 o, in float r, in vec2 v, in float dt) {
    vec2 p0 = p - o, p1 = v * dt;

    float a = dot(p1, p1);
    float b = dot(p0, p1);
    float c = dot(p0, p0) - r * r;

    float h = b * b - a * c;
    if (h > 0.0) {
        vec2 t = clamp((vec2(-1.0, 1.0) * sqrt(h) - b) / a, 0.0, 1.0);
        return t.y - t.x;
    }

    return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float unit = 2.0 / iResolution.y;
    vec2 ballPos = getBallPos();

    float distanceField = mapEnvironment(uv);
    fragColor = vec4(0.0, 0.0, smoothstep(unit, 0.0, distanceField), 1.0);
    if (iMouse.z > 0.0) {
        fragColor.rgb = mix(fragColor.rgb, vec3(1.0, 1.0, 1.0), 0.5 * sin(distanceField * 400.0));
    }

    fragColor.r += movingDisk(uv, ballPos, BALL_RADIUS, getBallVel(), 1.0);
}

