#define BALL_RADIUS 0.05

#define getBallPos() texelFetch(iChannel0, ivec2(0, 0), 0).xy
#define getBallVel() texelFetch(iChannel0, ivec2(1, 0), 0).xy

float mapEnvironment(in vec2 p) {
    vec2 q1 = abs(p) - vec2(0.75, 0.4);
    vec2 q2 = abs(p + vec2(0.35, 0.0)) - 0.25;
    float wall = -length(max(q1, 0.0)) - min(max(q1.x, q1.y), 0.0);
    float box = length(max(q2, 0.0)) + min(max(q2.x, q2.y), 0.0);
    float ball = length(p - vec2(0.4, 0.0)) - 0.2;
    return min(wall, min(box, ball));
}

vec2 getNormal(in vec2 p) {
    return normalize(vec2(mapEnvironment(p + vec2(0.001, 0.0)) - mapEnvironment(p - vec2(0.001, 0.0)),
                          mapEnvironment(p + vec2(0.0, 0.001)) - mapEnvironment(p - vec2(0.0, 0.001))));
}

