void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    ivec2 iFragCoord = ivec2(fragCoord);
    fragColor = vec4(0.0);
    if (iFrame == 0 && iFragCoord == ivec2(1, 0)) {
        fragColor = vec4(0.02, 0.01, 0.0, 0.0);
    }

    if (iFrame > 0) {
        if (iFragCoord.y == 0 && iFragCoord.x < 2) {
            vec2 ballPos = getBallPos();
            vec2 ballVel = getBallVel();

            ballPos += ballVel;

            float distToEnv = mapEnvironment(ballPos);
            if (distToEnv < BALL_RADIUS) {
                    vec2 hitNormal = getNormal(ballPos);
                    ballPos += hitNormal * (BALL_RADIUS - distToEnv);
                    ballVel = reflect(ballVel, hitNormal);
            }

            if (iFragCoord.x == 0) fragColor = vec4(ballPos, 0.0, 1.0);
            if (iFragCoord.x == 1) fragColor = vec4(ballVel, 0.0, 1.0);
        }
    }
}

