void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //const vec2 imageSize = 1024.0;
    vec2 imageSize = iResolution.xy;

    fragCoord -= 0.5f;

    vec2 uv_fullres = clamp( fragCoord / imageSize, 0.0, 1.0 );
    vec2 uv_halfres = floor( fragCoord / 2.0 ) * 2.0;
    uv_halfres += 1.0f;
    uv_halfres = clamp( uv_halfres / imageSize, 0.0, 1.0 );

    vec4 fullc = texture( iChannel0, uv_fullres ); fullc = pow3( fullc, 2.2 );

    vec4 test0 = texture( iChannel0, uv_halfres + vec2( 0.0, 0.0 ) / imageSize ); test0 = pow3( test0, 2.2 );
    vec4 test1 = texture( iChannel0, uv_halfres + vec2( 0.0, 1.0 ) / imageSize ); test1 = pow3( test1, 2.2 );
    vec4 test2 = texture( iChannel0, uv_halfres + vec2( 1.0, 0.0 ) / imageSize ); test2 = pow3( test2, 2.2 );
    vec4 test3 = texture( iChannel0, uv_halfres + vec2( 1.0, 1.0 ) / imageSize ); test3 = pow3( test3, 2.2 );

    vec4 p = texture( iChannel0, uv_halfres + vec2( 0.0, 1.0 ) / imageSize ); p = pow3( p, 2.2 );
    vec4 ps = texture( iChannel0, uv_halfres + vec2( 0.0, -1.0 ) / imageSize ); ps = pow3( ps, 2.2 );
    vec4 pe = texture( iChannel0, uv_halfres + vec2( 2.0,  1.0 ) / imageSize ); pe = pow3( pe, 2.2 );
    vec4 c  = texture( iChannel0, uv_halfres + vec2( 1.0,  0.0 ) / imageSize ); c = pow3( c, 2.2 );
    vec4 cw  = texture( iChannel0, uv_halfres + vec2( -1.0, 0.0 ) / imageSize ); cw = pow3( cw, 2.2 );
    vec4 cn  = texture( iChannel0, uv_halfres + vec2(  1.0,  2.0 ) / imageSize ); cn = pow3( cn, 2.2 );

    int xi = int( fragCoord.x ) % 2, yi = int( fragCoord.y ) % 2;

    if ( iMouse.z > 0.5 && iMouse.x < iResolution.x * 0.5 ) {
        if ( xi == 0 && yi == 0 ) {
            fragColor = test0;
        }
        if ( xi == 0 && yi == 1 ) {
            fragColor = test1;
        }
        if ( xi == 1 && yi == 1 ) {
            fragColor = test3;
        }
        if ( xi == 1 && yi == 0 ) {
            fragColor = test2;
        }
    } else if ( iMouse.z > 0.5 && iMouse.x >= iResolution.x * 0.5 ) {
        if ( xi == 0 && yi == 0 ) {
            fragColor = test0;
        }
        if ( xi == 0 && yi == 1 ) {
            fragColor = test0;
        }
        if ( xi == 1 && yi == 1 ) {
            fragColor = test0;
        }
        if ( xi == 1 && yi == 0 ) {
            fragColor = test0;
        }
    } else {

        fragColor = vec4( 0, 0, 0, 0 );

        if ( xi == 0 && yi == 0 ) {
            float diff1 = lumaDiff( cw.xyz, c.xyz ), diff2 = lumaDiff( ps.xyz, p.xyz );
            if ( diff1 < diff2 ) {
                fragColor = mix( cw, c, 0.5f );
            } else {
                fragColor = mix( ps, p, 0.5f );
            }
        }
        if ( xi == 0 && yi == 1 ) {
            fragColor = p;
        }
        if ( xi == 1 && yi == 1 ) {
            float diff1 = lumaDiff( p.xyz, pe.xyz ), diff2 = lumaDiff( cn.xyz, c.xyz );
            if ( diff1 < diff2 ) {
                fragColor = mix( p, pe, 0.5f );
            } else {
                fragColor = mix( cn, c, 0.5f );
            }
        }
        if ( xi == 1 && yi == 0 ) {
            fragColor = c;
        }
    }

    fragColor = pow3( fragColor, 1.0 / 2.2 );
}

