/*  
	http://math.ucsd.edu/%7Eaterras/bigbang.htm
	From Audrey Terras' Harmonic Analysis on Symmetric Spaces and Applications, 2nd edition, p. 316.
	Exercise 3.6.19 (Harold Stark): Computer-Generated Movie of a Non-Euclidean Shock Wave. 
	Stark created this movie on his Atari ST which is still operating.
*/

const int N = 2000;
const float y_start = 5.;
const float y_end = 10.;

const float width_screen = 10.;

const float time_reset = 2. * log2(float(N)) + log2(y_start) + log2(y_end);

// performs consecutive translations in a single step (faster, but does not show all reflections)
const bool merge_translations = false;

void circle_inversion(inout vec2 z) // z -> -1/z  ( = - conj(z) / |z|^2 )
{
	z.x = -z.x;
	z /= dot(z,z);
}

void horizontal_translation(inout vec2 z) // z -> z+-1
{
    if (merge_translations)
        z.x = fract(z.x + 0.5) - 0.5;
    else
        z.x = z.x < 0. ? z.x + 1. : z.x - 1.;
}

bool invert_circle(vec2 z)
{
    return dot(z, z) < 1.;
}

bool translate(vec2 z)
{
    return abs(z.x) > 0.5;
}

void plot(inout vec3 C, vec3 c, vec2 z, float r)
{
    if (dot(z, z) < r * r)
        C = c;
}

void mainImage(out vec4 C, in vec2 O)
{
    float time = exp2(mod(float(iFrame) / 60., time_reset)); // time flows exponentially in hyperbolic space
    float time_inv = 1. / time;

    float f = width_screen / iResolution.x;
    vec2 st = vec2(O - vec2(iResolution.x, 0) / 2.) * f;

    vec3 color = vec3(1);
    if(abs(abs(st.x)-0.5) < f && length(st.xy) >= 1.) // Left and Right border of fundamental domain
        color = vec3(0);
    else if(abs(length(st.xy)-1.) < f) // lower circle of fundamental domain
        color = vec3(0);
	else if(abs(st.x) <= 0.5 && length(st.xy) >= 1.)
        color = vec3(.95);

	for(int j=0; j<N; ++j)
    {
        float jf = float(j) / float(N);
        vec3 point_color = vec3(sin(jf*2.), jf, 1.-jf);

		vec2 h = vec2(jf-0.5, y_start);
		h.y *= time_inv;

        while(invert_circle(h) || translate(h)) // Applying gamma, i.e. put into fundamental domain
        {
            if(invert_circle(h))
            {
                plot(color, vec3(1,0,0), st - h, f);
                circle_inversion(h);
            }
            if(translate(h))
            {
                plot(color, vec3(1,0,1), st - h, f);
                horizontal_translation(h);
            }
        }
        plot(color, point_color, st - h, f);
	}

    C = vec4(color,1.0);
}

float Circle(vec2 uv, vec2 p, float r) 
{
    float d = length(uv - p);
     float c = smoothstep(r, r - 0.001, d);
