#define load(addr) texelFetch(iChannel0, addr, 0)
#define pixel2uv(pixel) ((pixel - 0.5 * iResolution.xy) / iResolution.y)
#define uv2pixel(uv) (uv * iResolution.y + 0.5 * iResolution.xy)
#define packCircle(packed, new, id) (new.x < packed.x ? vec4(new, id) : packed)

// https://www.shadertoy.com/view/4djSRW
float Hash11(in float x) {
    x = fract(x * 0.1031);
    x *= x + 33.33;
    x *= x + x;
    return fract(x);
}

vec2 Hash12(in float x) {
    vec3 p3 = fract(x * vec3(0.1031, 0.103, 0.0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy);
}

// vec3(signed distance, gradient)
// Buffering the distance field already lowers
// precision so buffering an exact gradient is
// helpful
vec3 sdgDisk(in vec2 p, in float r) {
    float d = length(p);
    return vec3(d - r, p / d);
}

void mainImage(out vec4 data, in vec2 addr) {
    vec2 bound = vec2(0.5 * iResolution.x / iResolution.y, 0.5);
    vec2 uv = pixel2uv(addr);
    data = vec4(0.0);

    if (iFrame > 0) {
        ivec2 iAddr = ivec2(addr);
        float seed = iDate.w;
        data = load(iAddr);

        // "Random" starting position
        vec2 start = pixel2uv(iResolution.xy * Hash12(seed));

        // Iteratively project onto the distance field's surface
        for (int i=0; i < 10; i++) {
            vec3 sdg = load(ivec2(uv2pixel(start))).xyz;
            sdg.x -= 0.01; // Slightly extra to be outside
            start -= sdg.yz * sdg.x;
            if (abs(sdg.x) < 0.001) break;
        }

        // Raymarch to the next surface
        vec2 dir = load(ivec2(uv2pixel(start))).yz;
        float t = 0.0;

        // Maximum distance the ray can travel (to the edge of the screen)
        vec2 isect = (bound * sign(dir) - start) / dir;
        float tMax = min(isect.x, isect.y);

        bool hit = false;
        for (int i=0; i < 100; i++) {
            vec2 p = start + dir * t;
            float d = load(ivec2(uv2pixel(p))).x;
            if (d < 0.001) { hit = true; break; }
            if (t > tMax) break;
            t += d;
        }

        if (hit) {
            vec2 center = start + dir * min(0.5 * t, tMax);
            float radius = load(ivec2(uv2pixel(center))).x;
            if (radius > 0.0) data = packCircle(data, sdgDisk(uv - center, radius), sin(seed));
        }
    }

    if (iFrame == 0 || iMouse.z > 0.0) { // Initialize with distance to boundaries
        vec2 b = abs(uv) - bound;
        if (b.x > b.y) data.xy = vec2(-b.x, -sign(uv.x));
        else data.xz = vec2(-b.y, -sign(uv.y));
        data.w = 0.0;
    }
}

