// SDFs
float sdCircle(in vec2 p, in float r) {
    return length(p) - r;
}

float sdBox(in vec2 p, in vec2 b) {
    p = abs(p) - b;
    return length(max(p, 0.0)) + min(0.0, max(p.x, p.y));
}

float sdTriangle(in vec2 p, in vec2 a, in vec2 b, in vec2 c) {
    vec2 ba = b - a, cb = c - b, ac = a - c;
    vec2 pa = p - a, pb = p - b, pc = p - c;

    float abc = ba.x * cb.y - ba.y * cb.x;
    float abp = ba.x * pa.y - ba.y * pa.x;
    float bcp = cb.x * pb.y - cb.y * pb.x;
    float cap = ac.x * pc.y - ac.y * pc.x;

    vec2 ae = pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    vec2 be = pb - cb * clamp(dot(pb, cb) / dot(cb, cb), 0.0, 1.0);
    vec2 ce = pc - ac * clamp(dot(pc, ac) / dot(ac, ac), 0.0, 1.0);

    float tri = sqrt(min(dot(ae, ae), min(dot(be, be), dot(ce, ce))));
    bvec3 n = lessThan(vec3(abp, bcp, cap), vec3(0.0));
    return all(n) || all(not(n)) ? -tri : tri;
}

#define color fragColor.rgb
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texture(iChannel0, fragCoord / iResolution.xy, 3.0);
    if (iFrame == 0) fragColor = vec4(1.0);

    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;

    float box = sdBox(uv - (iMouse.z > 0.0 ? mouse : vec2(cos(iTime), cos(iTime) * sin(iTime) + 0.75)), vec2(0.4));
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, box));

    float circle = sdCircle(uv - vec2(-1.25, -0.75), 0.4);
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, circle));

    float triangle = sdTriangle(uv - vec2(1.25, -0.75), vec2(-0.5, -0.333), vec2(0.5, -0.333), vec2(0.0, 0.666));
    color = mix(color, vec3(0.0, 0.0, 1.0), smoothstep(unit, 0.0, triangle));

    uv.x = abs(uv.x) - 2.5;
    float walls = sdBox(uv, vec2(0.1, 1.5));
    color = mix(color, vec3(0.0), smoothstep(unit, 0.0, walls));

    uv.x -= 0.6;
    uv.y = mod(uv.y + 0.5, 1.0) - 0.5;
    float dots = sdCircle(uv, 0.1);
    color = mix(color, vec3(1.0), smoothstep(unit, 0.0, dots));
}

