float spec( vec3 p, vec3 lp,vec3 rd,vec3 n )
{ 

    vec3 l = normalize(lp - p);
    vec3 r = reflect(-l, n);
    float spe =pow(clamp(dot(r, -rd), 0.0, 1.0), 20.0);

    return spe;
