// Author: Thomas Stehle
// Title: Sun Tracks
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License
//
// Inspired by this long exposure photograph by Martin Cann:
// https://twitter.com/MartinCann1/status/1408139994972278788

// Constants
const vec3 INNER = vec3(131, 118, 117) / 255.0;
const vec3 OUTER = vec3(  1,  34,  46) / 255.0;
const vec3 SUN   = vec3(224, 229, 180) / 255.0;
const vec3 GLOW  = vec3(247, 251, 238) / 255.0;

const int NLINES     = 100;
const float NLINES_F = float(NLINES);

// 1D hash for 1D input by David Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash(in float p) {
    p = fract(p * 0.011);
    p *= p + 7.5;
    p *= p + p;
    return fract(p);
}

// 1D hash for 2D input by David Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash21(in vec2 p) {
    vec3 p3 = fract(vec3(p.xyx) * 0.13);
    p3 += dot(p3, p3.yzx + 3.333);
    return fract((p3.x + p3.y) * p3.z);
}

// 1D hash for 3D input
float hash31(in vec3 p) {
    vec3 q = fract(p * 0.1031);
    q += dot(q, q.yzx + 33.33);
    return fract((q.x + q.y) * q.z);
}

// 2D value noise by Morgan McGuire
// https://www.shadertoy.com/view/4dS3Wd
float vnoise(in vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);

    // Four corners in 2D of a tile
    float a = hash21(i);
    float b = hash21(i + vec2(1.0, 0.0));
    float c = hash21(i + vec2(0.0, 1.0));
    float d = hash21(i + vec2(1.0, 1.0));

    // Smooth interpolation (smoothstep without clamping)
    vec2 u = f*f * (3.0 - 2.0*f);

    // Mix 4 coorners
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

// 2D Fractional Brownian motion based on value noise by Morgan McGuire
// https://www.shadertoy.com/view/4dS3Wd
float vfbm(in vec2 p) {
    float v = 0.0;
    float a = 0.5;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.5));
    const int NOCTAVES = 5;
    for (int i = 0; i < NOCTAVES; ++i) {
        v += a * vnoise(p);
        p = rot * p * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}

// OKLAB-based color mixing by iq
// https://www.shadertoy.com/view/ttcyRS
vec3 oklab_mix( vec3 colA, vec3 colB, float h ) {
    // https://bottosson.github.io/posts/oklab
    const mat3 kCONEtoLMS = mat3(
         0.4121656120,  0.2118591070,  0.0883097947,
         0.5362752080,  0.6807189584,  0.2818474174,
         0.0514575653,  0.1074065790,  0.6302613616);
    const mat3 kLMStoCONE = mat3(
         4.0767245293, -1.2681437731, -0.0041119885,
        -3.3072168827,  2.6093323231, -0.7034763098,
         0.2307590544, -0.3411344290,  1.7068625689);

    // rgb to cone (arg of pow can't be negative)
    vec3 lmsA = pow( kCONEtoLMS*colA, vec3(1.0/3.0) );
    vec3 lmsB = pow( kCONEtoLMS*colB, vec3(1.0/3.0) );

    // lerp
    vec3 lms = mix( lmsA, lmsB, h );

    // gain in the middle (no oaklab anymore, but looks better?)
    // lms *= 1.0+0.2*h*(1.0-h);

    // cone to rgb
    return kLMStoCONE*(lms*lms*lms);
}

float sdOrientedBox(in vec2 p, in vec2 a, in vec2 b, float th) {
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = (p-(a+b)*0.5);
          q = mat2(d.x,-d.y,d.y,d.x)*q;
          q = abs(q)-vec2(l,th)*0.5;
    return length(max(q,0.0)) + min(max(q.x,q.y),0.0);
}

vec2 distort(in vec2 p) {
    vec2 q = 0.8 * p + vec2(-0.2, 0.2);
    float amp = -0.4;
    float freq = 1.0 + 4.0 * q.y;
    float phase = 0.0;
    return vec2(q.x, q.y + amp * sin(freq * q.x + phase));
}

float suntracks(in vec2 p) {
    vec2 st = vec2(p.x, p.y * NLINES_F);
    st.y = max(st.y, 0.1 * NLINES_F);
    st.y = min(st.y, 0.55 * NLINES_F);

    vec2 gv = vec2(st.x, fract(st.y));
    float id = floor(st.y);
    int idx = int(id);

    float r1 = hash(13.1 * float(idx));
    float r2 = hash(37.3 * float(idx));

    float speed = 0.25 + 0.25 * r2;

    const float W = 0.25;
    float d = sdOrientedBox(gv, vec2(-1.0, 0.5), vec2(2.0, 0.5), W);
    float n = vfbm(vec2(30.0 * (gv.x + id) - speed * iTime, 0.0));
    d += 0.25 * r2 + smoothstep(0.55, 0.65, n);

    return d;
}

float foreground(in vec2 p) {
    vec2 q = vec2(p.x, p.y + 0.2 * vfbm(10.0 * p));
    const float W = 0.25;
    return sdOrientedBox(q, vec2(-1.0, 0.0), vec2(2.0, 0.0), W);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Coordinate normalization
    vec2 uv = fragCoord.xy / iResolution.xy;
    float ar = iResolution.x / iResolution.y;
    uv.x *= ar;

    // Background
    float bg = pow(10.0 * length(0.15 * uv - vec2(0.075, 0.1)), 1.5);
    vec3 col = oklab_mix(INNER, OUTER, clamp(bg, 0.0, 1.0));

    // Foreground
    float fgd = foreground(uv);
    fgd = smoothstep(0.15, 0.1, fgd);
    col = mix(col, vec3(0.05), fgd);

    // Distort
    vec2 p = distort(uv);

    // Fade out factors
    float fade = smoothstep(0.75, 0.25, length(p - vec2(0.5))); // Fade out to the sides

    // Sun tracks
    float td = suntracks(p);
    td  = smoothstep(0.5, -0.5, td) * fade;
    td *= smoothstep(0.05, 0.2, uv.y); // Additional fade out near bottom
    col = mix(col, SUN, clamp(td, 0.0, 1.0));

    // Gas tracks
    float n = vfbm(2.0 * p - vec2(0.15 * iTime, 0.0));
    n *= (1.0 - fgd);
    col = oklab_mix(col, SUN, pow(n, 3.0));

    // Glow
    float gl = vnoise(2.0 + sin(1.5 * iTime) * p);
    gl  = pow(gl, 2.0) * fade;
    gl *= smoothstep(0.7, 0.4, p.y) * smoothstep(-0.1, 0.2, p.y); // Additional fade in sun space
    gl *= smoothstep(0.05, 0.2, uv.y); // Additional fade in screen space
    col = mix(col, GLOW, clamp(gl, 0.0, 1.0));

    // Add animated noise
    float r = hash31(vec3(fragCoord.xy, fract(0.001 * iTime)));
    col.rgb += 0.1 * vec3(r - 0.5);

    // Vignetting
    // https://www.shadertoy.com/view/lsKSWR
    vec2 st = uv * (vec2(1.0, ar) - uv.yx);
    float vig = st.x * st.y * 10.0;
    col *= pow(vig, 0.2);

    // Final color
    fragColor = vec4(col, 1.0);
}

// All the distance functions from:https://iquilezles.org/articles/distfunctions
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)

#define MATERIAL_DIFFUSE0 0
#define MATERIAL_REFLECT 10

const float Epsilon = 1e-10;

// noise function from https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
vec2 fade(vec2 t) {return t*t*t*(t*(t*6.0-15.0)+10.0);}
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}

float cnoise(vec2 P){
  vec4 Pi = floor(P.xyxy) + vec4(0.0, 0.0, 1.0, 1.0);
  vec4 Pf = fract(P.xyxy) - vec4(0.0, 0.0, 1.0, 1.0);
  Pi = mod(Pi, 289.0); // To avoid truncation effects in permutation
  vec4 ix = Pi.xzxz;
  vec4 iy = Pi.yyww;
  vec4 fx = Pf.xzxz;
  vec4 fy = Pf.yyww;
  vec4 i = permute(permute(ix) + iy);
  vec4 gx = 2.0 * fract(i * 0.0243902439) - 1.0; // 1/41 = 0.024...
  vec4 gy = abs(gx) - 0.5;
  vec4 tx = floor(gx + 0.5);
  gx = gx - tx;
  vec2 g00 = vec2(gx.x,gy.x);
  vec2 g10 = vec2(gx.y,gy.y);
  vec2 g01 = vec2(gx.z,gy.z);
  vec2 g11 = vec2(gx.w,gy.w);
  vec4 norm = 1.79284291400159 - 0.85373472095314 * 
    vec4(dot(g00, g00), dot(g01, g01), dot(g10, g10), dot(g11, g11));
  g00 *= norm.x;
  g01 *= norm.y;
  g10 *= norm.z;
  g11 *= norm.w;
  float n00 = dot(g00, vec2(fx.x, fy.x));
  float n10 = dot(g10, vec2(fx.y, fy.y));
  float n01 = dot(g01, vec2(fx.z, fy.z));
  float n11 = dot(g11, vec2(fx.w, fy.w));
  vec2 fade_xy = fade(Pf.xy);
  vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade_xy.x);
  float n_xy = mix(n_x.x, n_x.y, fade_xy.y);
  return 2.3 * n_xy;
}

float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec2 combine(vec2 val1, vec2 val2, float k){
    vec2 res1 = val1;
    vec2 res2 = val2;
    res1.x = smin(val2.x,val1.x,k);
    res2.x = smin(val1.x,val2.x,k);
    return (val1.x < val2.x)?res1:res2;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float dot2( in vec3 v ) { return dot(v,v); }
float sdBoundingBox( vec3 p, vec3 b, float e)
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;

  return sqrt(min(min(dot2(max(vec3(p.x,q.y,q.z),0.0)),
                      dot2(max(vec3(q.x,p.y,q.z),0.0))),
                      dot2(max(vec3(q.x,q.y,p.z),0.0)))) 
         +min(0.0,min(min( max(p.x,max(q.y,q.z)),
                           max(p.y,max(q.z,q.x))),
                           max(p.z,max(q.x,q.y))));
}

vec2 GetDist(vec3 p) {
    float t = iTime*0.1;

    mat3 rot = matRotateZ(radians(-25.0)+t*2.0);
    vec3 prevP = p*rot;

    float d = 10000.0;
    p.z = mod(p.z,5.)-2.5;
    float maskd = sdBox(p,vec3(3.0,3.0,5.0));

    p = prevP;
    p.z+=iTime*5.0;
    p.xy = mod(p.xy,9.)-4.5;
    p.z = mod(p.z,8.)-4.;

    mat3 rot2 = matRotateX(radians(-25.0)+t)*matRotateY(radians(30.0)+t);

    for(float i = 1.; i<=5.; i++){
        p = abs(p);
        p -= (0.5)*i;
        p *= rot2;
        d = min(d,sdBoundingBox(p, vec3(1.9,1.9,2.),.05));
    }

    d = max(maskd,d);
    p = prevP;
    d = max(sdBox(p,vec3(5.0,5.0,19.0)),d);

    vec2 res = vec2(d, MATERIAL_REFLECT);

    vec2 model = res;
    return model;
}

vec2 RayMarch(vec3 ro, vec3 rd, float side) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;
        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 reflectMaterial(vec3 p, vec3 rd, vec3 n) {
    vec3 r = reflect(rd,n);
    vec3 col = r;

    float nn = cnoise(p.xy*3.0);
    col += nn*5.0;
    float k = r.y*2.5+2.5;
    col = mix(col*vec3(0.5),vec3(0.5),k);
    return col;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

vec3 diffuseMaterial(vec3 n, vec3 col) {
    float dif = dot(n,normalize(vec3(1,2,3)))*0.5+0.5;
    return vec3(dif)*col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -5.5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(-30.0));
    if(mod(iTime,20.0)<17.0){
        ro.xz *= Rot(iTime*.1+1.0);
    } else {
        ro.xz *= Rot(radians(0.0));
    }

    #endif

    vec3 rd = R(uv, ro, vec3(0,-0.3,0), 1.5);
    vec2 d = RayMarch(ro, rd, 1.);
    vec3 col = vec3(0.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd,n);

        int mat = int(d.y);
        if(mat == MATERIAL_DIFFUSE0){
            col = diffuseMaterial(n,vec3(1.0));
        } else if(mat == MATERIAL_REFLECT) {
            col = reflectMaterial(p,rd,n);
        }

    }

    // gamma correction
    col = pow( col, vec3(0.3545) );

    fragColor = vec4(col,1.0);
}

vec4 matcapSphere(vec2 uv)
{
    uv = (uv - 0.5) * 2.0;
    vec3 n = vec3(uv, sqrt(1.0 - clamp(dot(uv, uv), 0.0, 1.0)));
    // convert to color range
    vec3 color = 0.5 + 0.5 * n;
    return mix(vec4(0.5, 0.5, 1.0, 0.0), vec4(color, 1.0), smoothstep(1.01, 1.0, dot(uv, uv)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = matcapSphere(uv);
}

const float gridSize = 75.;
const float sphereSize = 0.92;
const float speed = 10.;

//https://www.shadertoy.com/view/4djSRW
vec3 hash32(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec3 lightDir = normalize(vec3(cos(iTime)*0.25,0.5,sin(iTime)*0.25));

    //slowly panning coordinates
    vec2 uv = (fragCoord+iTime*vec2(2.,1.)*speed)/gridSize;

    //a square that contains the sphere
    vec2 uv2 = (fract(uv)*2.-1.)/sphereSize;

    // a method to get sphere normals in orthographic projection
    // (if d >= 1. then we are not touching the sphere)  
    float d = dot(uv2,uv2);
    float z = (d>=1.) ? 0. : sqrt(1.-d);
    vec3 normals = vec3(uv2,z);

    //phong reflection model
    float specular = pow(max(2.*z*dot(lightDir,normals) -lightDir.z, 0.0),20.)*0.25;
    float diffuse = 0.5 + dot(normals,lightDir)*0.5;

    //here is the palette
    vec2 seed = floor(uv);
    vec3 rnd = hash32(seed);
    vec3 col = min(mix(0.1/(1.-rnd),rnd.zxy,diffuse),1.);
    //vec3 col = mix(min(0.1/(1.-rnd),1.),rnd.zxy,diffuse);
    //vec3 col = mix(rnd*rnd*rnd,rnd.zxy,diffuse).yzx;

    //antialiasing
    col *= min(15.*z*z,1.);

    // Output to screen
    fragColor = vec4(col+specular,1.0);
}

