void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.9);
    float t = iTime*3.5;

    // Pixel Normalizations //
    vec2 uv101 = fragCoord.xy/iResolution.xy,
         uv = (uv101 - 0.5) * vec2(iResolution.x / iResolution.y, 1);

    float rotate_radius = (cos(t/7.)*0.5+0.5)*0.3 + 0.7; 
    vec2 sphere_position = vec2(sin(t)*0.55, cos(t)*0.2-0.04);
    float sphere_size = ((cos(t)*0.5+0.5)*0.4*rotate_radius+0.6/rotate_radius)*0.45;

    if (sqrt(length( uv + sphere_position*rotate_radius )) < sphere_size) col = vec3(uv.xy/-3.+0.5, 1.);

    // Vignette //
    uv = uv101 * (1. - uv101);    
    col *= pow(uv.x*uv.y * 25., 0.07);

    fragColor = vec4(col,1.);
}

