vec2 map(vec3 pos, float sg)
{
    vec2 res = vec2(1e5,0.);
    pos.y+=1.25;;
    vec3 q = pos-vec3(0,2.5,0);

    vec3 b = pos-vec3(0,1.05,0);

    q.yx*=spin;
