    float d1 = box(q,vec3(1.5))-.0423;
    if(d1<res.x){
        res = vec2(d1,2.);
        hit=q;
    }

    b.xz=abs(abs(abs(b.xz)-12.)-6.)-3.;
    vec3 hitb = b;

    float pillar = cap(b,.3,1.5);
    vec3 tb =vec3(b.x,abs(b.y),b.z);
