const float PI = 3.1416;

vec2 hash2( vec2 p )
{
    // procedural white noise	
	return fract(sin(vec2(dot(p,vec2(127.1,311.7)),
                          dot(p,vec2(269.5,183.3))))*43758.5453);
}

//Derived from https://iquilezles.org/articles/voronoilines
vec3 voronoi( in vec2 x )
{
    vec2 n = floor(x);
    vec2 f = fract(x);

    //----------------------------------
    // first pass: regular voronoi
    //----------------------------------
	vec2 mg, mr;

    float md = 1.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = hash2( n + g );
		#ifdef ANIMATE
        o = 1.5 + 0.5*sin( iTime + 6.2831*o );
        #endif
        vec2 r = g + o - f;
        float d = dot(r,r);

        if( d<md )
        {
            md = d;
            mr = r;
            mg = g;
        }
    }

    //----------------------------------
    // second pass: distance to borders
    //----------------------------------
    md = 80.0;
    vec2 ml = vec2(0.0);
    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {
        vec2 g = mg + vec2(float(i),float(j));
		vec2 o = hash2( n + g );
		#ifdef ANIMATE
        o = .1 + 0.1*sin( iTime + 6.2831*o );
        #endif	
        vec2 r = g + o - f;

        if( dot(r-mr,r-mr)>0.00001 ) {
            vec2 l = normalize(r-mr);
            float d = dot( 0.05*(mr+r), l );
            if (md > d) { 
        		md = d;
                ml = l;
            }
        }           
    }

    return vec3(md, ml);
}



void voronoi_s(in vec2 x, inout vec2 n,  inout vec2 f, 
                          inout vec2 mg, inout vec2 mr) {

    n = floor(x);
    f = fract(x);

    float md = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = hash2( n + g );
        vec2 r = g + o - f;
        float d = dot(r,r);

        if (d < md) {
            md = d;
            mr = r;
            mg = g;
        }
    }   
}

vec3 voronoi_n(inout vec2 rd, inout vec2 n,  inout vec2 f, 
                              inout vec2 mg, inout vec2 mr) {
    float md = 1e5;
    vec2 mmg = mg;
    vec2 mmr = mr;
    vec2 ml = vec2(0.0);

    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {   
        vec2 g = mmg + vec2(float(i),float(j));
		vec2 o = hash2( n + g );
		vec2 r = g + o - f;

    	vec2 l = r-mmr;
 		if((dot(l, l) * dot(rd, l)) > 1e-5) {
            float d = dot(0.5*(mmr+r), l)/dot(rd, l);
            if (d < md) {
                md = d;
                mg = g;
                mr = r;
                ml = l;
            }
        }
    }

    return vec3(md, ml);
}


bool IRayAABox(in vec3 ro, in vec3 rd, in vec3 invrd, in vec3 bmin, in vec3 bmax, 
               out vec3 p0, out vec3 p1) 
{
    vec3 t0 = (bmin - ro) * invrd;
    vec3 t1 = (bmax - ro) * invrd;

    vec3 tmin = min(t0, t1);
    vec3 tmax = max(t0, t1);

    float fmin = max(max(tmin.x, tmin.y), tmin.z);
    float fmax = min(min(tmax.x, tmax.y), tmax.z);

    p0 = ro + rd*fmin;
    p1 = ro + rd*fmax;

    return fmax >= fmin;   
}

vec3 AABoxNormal(vec3 bmin, vec3 bmax, vec3 p) 
{
    vec3 n1 = -(1.0 - smoothstep(0.0, 0.03, p - bmin));
    vec3 n2 = (1.0 -  smoothstep(0.0, 0.03, bmax - p));

    return normalize(n1 + n2);
}

const vec3 background = vec3(0.04);
const vec3 scmin = -vec3(1.77, 1.0, 1.77);
const vec3 scmax = vec3(1.77, 1.5, 1.77);


float map(in vec2 p) {
    vec2 h = exp(-(p*p*0.5));
    return scmin.y + 0.1 + hash2(p).x * 0.5 
        + texture(iChannel0, vec2(hash2(p).x, 0.0)).r*2.0 + h.x*h.y*0.5;
}

// From https://iquilezles.org/articles/palettes
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 color(vec2 p) {
    return 1.5 *
           pal(3.434+(hash2(p).x*0.02), vec3(0.5,0.5,0.5), 
                                        vec3(0.5,0.5,0.5),
                                        vec3(1.0,0.7,0.4),
                                        vec3(0.0,0.15,0.20)  );
}

float voronoi_ao(in float h, inout vec2 n, inout vec2 f, inout vec2 mg) {
    float a = 0.0;

    for (int j = -1; j <= 1; ++j)
    for (int i = -1; i <= 1; ++i) 
    {
    	vec2 g = mg + vec2(float(i), float(j));
        a += max(0.0, map(n + g) - h);
    }

    return exp(-a*0.5) + 0.2;
    return max(0.0, 1.0 - a*0.2) + 0.2;
}

vec2 ShadowAndAmbient(in vec3 ro, in vec3 rd) {
    vec3 p0 = vec3(0.0), p1 = vec3(0.0);

    IRayAABox(ro, rd, 1.0/rd, scmin, scmax, p0, p1);

    if (length(ro - p1) < 0.01) return vec2(1.0);

    p0 = ro + rd*0.01;
    vec2 dir = normalize(rd.xz);
    float s = rd.y / length(rd.xz);

    vec2 mg = vec2(0.0), mr = vec2(0.0), n = vec2(0.0), f = vec2(0.0);
    voronoi_s(p0.xz*2.0, n, f, mg, mr);

    float h = map(n + mg);

    float a = voronoi_ao(ro.y, n, f, mg);
    vec3 dn = voronoi_n(dir, n, f, mg, mr);

    float rh = 0.0, prh = p0.y;

  	float dmax = length(p0.xz - p1.xz);
	float mh = 1.0;

    const int steps = 8;
    for (int i = steps; i > 0; --i) {
        dn.x *= 0.01;
        rh = p0.y + dn.x*s;

        if (dn.x > dmax || h > prh || h > rh) break; 

        prh = rh;

        h = map(n + mg);

        mh = min(mh, 14.0*(rh-h)/(dn.x*s));

        dn = voronoi_n(dir, n, f, mg, mr);
    }

    if (h > prh || h > rh) return vec2(0.0, a);

    return vec2(clamp(mh, 0.0, 1.0), a);
}

vec3 Shade(in vec3 p, in vec3 p0, in vec3 v, in vec3 n, in vec3 l, in vec2 c) {
    vec3 col = color(c);
    vec2 lc = vec2(max(0.0, dot(n,l))*0.9, 1.0);

    if (length(p - p0) < 0.01) return 3.0*col*(lc.x + lc.y)*0.5;

    vec2 sa = ShadowAndAmbient(p + n*0.02, l);
   	lc *= sa;
    return col*(lc.x + lc.y);
}

vec3 Render(in vec3 ro, in vec3 rd, in vec3 ld) {
    vec3 p0 = vec3(0.0), p1 = vec3(0.0);

    if (!IRayAABox(ro, rd, 1.0/rd, scmin, scmax, p0, p1)) return background;

    vec2 dir = normalize(rd.xz);
    float s = rd.y / length(rd.xz);

    vec2 mg = vec2(0.0), mr = vec2(0.0), n = vec2(0.0), f = vec2(0.0);
    voronoi_s(p0.xz*8.0, n, f, mg, mr);

    vec2 pmg = mg, pmr = mr;
    float h = map(n + pmg);

    vec3 dn = voronoi_n(dir, n, f, mg, mr);
    vec3 pdn = vec3(0.0, -AABoxNormal(scmin, scmax, p0).xz); 

    float rh = 0.0, prh = p0.y;

  	float dmax = length(p0.xz - p1.xz);

    const int steps = 1000;
    for (int i = steps; i > 0; --i) {
        dn.x *= 0.2;
        rh = p0.y + dn.x*s;

        if (dn.x > dmax || h > prh || h > rh) break; 

        prh = rh;

        pmg = mg; pmr = mr;
        h = map(n + pmg);

        pdn = dn;
        dn = voronoi_n(dir, n, f, mg, mr);
    }

    if (!(h > prh || h > rh)) return background;

    vec3 p = vec3(p0.xz + dir*pdn.x, p0.y + pdn.x*s).xzy;
    vec3 nor = vec3(normalize(-pdn.yz), 0.0).xzy;

    if (h < prh) {
    	float dt = abs((p0.y - h)/s);

        if (dt > dmax) return background;

        p = vec3(p0.xz + dir*dt, h).xzy;

        vec3 v = voronoi(p.xz*2.0)*0.5;
        nor = normalize(mix((vec3(normalize(v.yz), 0.0).xzy), 
                            vec3(0.0, 1.0, 0.0), smoothstep(0.0, 0.03, v.x)));
    }

    return Shade(p, p0, rd, nor, ld, n + pmg);

}

void CameraOrbitRay(in vec2 fragCoord, in float n, in vec3 c, in float d, 
                    out vec3 ro, out vec3 rd, out mat3 t) 
{
    float a = 1.0/max(iResolution.x, iResolution.y);
    rd = normalize(vec3((fragCoord - iResolution.xy*0.5)*a, n));

    ro = vec3(0.0, 0.0, -d);

    float ff = min(1.0, step(0.001, iMouse.x) + step(0.001, iMouse.y));
    vec2 m = PI*ff + vec2(((iMouse.xy + 0.1) / iResolution.xy) * (PI*2.0));
    m.y = -m.y;
    m.y = sin(m.y*0.5)*0.6 + 0.6;

    mat3 rotX = mat3(1.0, 0.0, 0.0, 0.0, cos(m.y), sin(m.y), 0.0, -sin(m.y), cos(m.y));
    mat3 rotY = mat3(cos(m.x), 0.0, -sin(m.x), 0.0, 1.0, 0.0, sin(m.x), 0.0, cos(m.x));

    t = rotY * rotX;

    ro = t * ro;
    ro = c + ro;

    rd = t * rd;

    rd = normalize(rd);
}

vec3 LightDir(in mat3 t) 
{
    vec3 l = normalize(vec3(1.0, 1.0, -1.0));
    return t * l;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 ro = vec3(0.0);
    vec3 rd = vec3(0.0);
    mat3 t = mat3(1.0);

    CameraOrbitRay(fragCoord, 1.0, vec3(0.0), 6.0, ro, rd, t);
	fragColor = vec4(pow(Render(ro, rd, LightDir(t)), vec3(0.5454)), 1.0);
}

// License CC0: Gooey KIFS fractal
// This weekend has been messing with 3D KIFS fractals
// I suppose it doesn't hurt with one more KIFS fractal on shadertoy

#define PI              3.141592654
#define TAU             (2.0*PI)
#define TIME            iTime
#define RESOLUTION      iResolution
#define ROT(a)          mat2(cos(a), sin(a), -sin(a), cos(a))
#define PCOS(x)         (0.5+0.5*cos(x))

#define TOLERANCE       0.00001
#define MAX_RAY_LENGTH  10.0
#define MAX_RAY_MARCHES 50
#define NORM_OFF        0.0001
#define N(a)            normalize(vec3(sin(a), -cos(a),  0.0))

const vec3  std_gamma  = vec3(2.2);
const float smoothing  = 0.125*0.5;

float tanh_approx(float x) {
//  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

// From: https://stackoverflow.com/a/17897228/418488
vec3 hsv2rgb(vec3 c) {
  const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 postProcess(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, 1.0/std_gamma);
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

float pmin(float a, float b, float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

vec3 pmin(vec3 a, vec3 b, float k) {
  vec3 h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

vec3 pabs(vec3 a, float k) {
  return -pmin(a, -a, k);
}

vec3 refl(vec3 p, vec3 n) {
  p -= n*pmin(0.0, dot(p, n), smoothing)*2.0;
  return p;
}

float sphered(vec3 ro, vec3 rd, vec4 sph, float dbuffer) {
    float ndbuffer = dbuffer/sph.w;
    vec3  rc = (ro - sph.xyz)/sph.w;

    float b = dot(rd,rc);
    float c = dot(rc,rc) - 1.0;
    float h = b*b - c;
    if( h<0.0 ) return 0.0;
    h = sqrt( h );
    float t1 = -b - h;
    float t2 = -b + h;

    if( t2<0.0 || t1>ndbuffer ) return 0.0;
    t1 = max( t1, 0.0 );
    t2 = min( t2, ndbuffer );

    float i1 = -(c*t1 + b*t1*t1 + t1*t1*t1/3.0);
    float i2 = -(c*t2 + b*t2*t2 + t2*t2*t2/3.0);
    return (i2-i1)*(3.0/4.0);
}

float torus(vec3 p, vec2 t) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

vec2 mod2(inout vec2 p, vec2 size) {
  vec2 c = floor((p + size*0.5)/size);
  p = mod(p + size*0.5,size) - size*0.5;
  return c;
}

float df(vec3 p) {
  const float zf = 2.0-0.3;
  const vec3 n0  = N((PI-acos(1.0/3.0))/2.0);
  const vec3 n1 = vec3(n0.x, n0.yz*ROT(2.0*PI/3.0));
  const vec3 n2 = vec3(n0.x, n0.yz*ROT(-2.0*PI/3.0));

  float a  = TIME*0.1;
  mat2 rxy = ROT(a);
  mat2 ryz = ROT(a*sqrt(0.5));
  float z = 1.0;

  float d = 1E6;

  const int mid = 2;
  const int end = 5;

  for (int i = 0; i < mid; ++i) {
    p.xy *= rxy;
    p.yz *= ryz;
  //  p = -pabs(p, smoothing); 
    p = refl(p, n2);
    p = refl(p, n0);
    p = refl(p, n1);
    p.x -= 0.3;
    p *= zf;
    z *= zf;
  }

  for (int i = mid; i < end; ++i) {
    p.xy *= rxy;
    p.yz *= ryz;
    p = -pabs(p, smoothing); 
    p = refl(p, n2);
//    p = refl(p, n0);
    p = refl(p, n1);
    p.x -= 0.3;
    p *= zf;
    z *= zf;
    vec3 pp = p;
    const float sz = 0.125;
    mod2(pp.yz, vec2(sz*3.0));
    float dd1 = length(pp) - sz*0.9;
    float dd2 = torus(pp.zxy, sz*vec2(1.0, 0.1));
    float dd = dd1;
    dd = min(dd, dd2);
    dd /= z;

    d = pmin(d, dd, smoothing/z);
  }

  return d;
}

float rayMarch(vec3 ro, vec3 rd, out int iter) {
  float t = 0.0;
  int i = 0;
  for (i = 0; i < MAX_RAY_MARCHES; i++) {
    float d = df(ro + rd*t);
    if (d < TOLERANCE || t > MAX_RAY_LENGTH) break;
    t += d;
  }
  iter = i;
  return t;
}

vec3 normal(vec3 pos) {
  vec2  eps = vec2(NORM_OFF,0.0);
  vec3 nor;
  nor.x = df(pos+eps.xyy) - df(pos-eps.xyy);
  nor.y = df(pos+eps.yxy) - df(pos-eps.yxy);
  nor.z = df(pos+eps.yyx) - df(pos-eps.yyx);
  return normalize(nor);
}

float softShadow(vec3 pos, vec3 ld, float ll, float mint, float k) {
  const float minShadow = 0.25;
  float res = 1.0;
  float t = mint;
  for (int i=0; i<24; i++) {
    float d = df(pos + ld*t);
    res = min(res, k*d/t);
    if (ll <= t) break;
    if(res <= minShadow) break;
    t += max(mint*0.2, d);
  }
  return clamp(res,minShadow,1.0);
}

vec3 render(vec3 ro, vec3 rd) {
  vec3 lightPos = vec3(1.0);
  float alpha   = 0.05*TIME;

  const vec3 skyCol = vec3(0.0);

  int iter    = 0;
  float t     = rayMarch(ro, rd, iter);

  float sr    = 0.35;
  float sd    = sphered(ro, rd, vec4(vec3(0.0), sr), t);

  vec3 gcol   = sd*1.5*vec3(2.0, 1.0, 0.75)*1.0;

  if (t >= MAX_RAY_LENGTH) {
    return gcol;
  }

  vec3 pos    = ro + t*rd;
  vec3 nor    = normal(pos);
  vec3 refl   = reflect(rd, nor);
  float ii    = float(iter)/float(MAX_RAY_MARCHES);
  float ifade = 1.0-tanh_approx(1.25*ii);
  float h     = fract(-1.0*length(pos)+0.1);
  float s     = 0.25;
  float v     = tanh_approx(0.4/(1.0+40.0*sd));
  vec3 color  = hsv2rgb(vec3(h, s, v));

  vec3 lv   = lightPos - pos;
  float ll2 = dot(lv, lv);
  float ll  = sqrt(ll2);
  vec3 ld   = lv / ll;
  float sha = softShadow(pos, ld, ll*0.95, 0.01, 10.0);

  float dm  = 4.0/ll2;
  float dif = pow(max(dot(nor,ld),0.0),2.0)*dm;  
  float spe = pow(max(dot(refl, ld), 0.), 20.);
  float ao  = smoothstep(0.5, 0.1 , ii);
  float l   = mix(0.2, 1.0, dif*sha*ao);

  vec3 col = l*color + 2.0*spe*ao*exp(-20.0*sd)*sha;
//  return vec3(ao);
  return gcol+col*ifade;
}

vec3 effect3d(vec2 p, vec2 q) {
  float z   = TIME;
  vec3 cam  = 1.5*vec3(1.0, 0.5, 0.0);
  float rt  = TAU*TIME/20.0;;
  cam.xy   *= ROT(sin(rt*sqrt(0.5))*0.5+0.0);
  cam.xz   *= ROT(sin(rt)*1.0-0.75);
  vec3 la   = vec3(0.0);
  vec3 dcam = normalize(la - cam);
  vec3 ddcam= vec3(0.0);

  vec3 ro = cam;
  vec3 ww = normalize(dcam);
  vec3 uu = normalize(cross(vec3(0.0,1.0,0.0)+ddcam*2.0, ww ));
  vec3 vv = normalize(cross(ww,uu));
  vec3 rd = normalize( p.x*uu + p.y*vv + 2.5*ww );

  return render(ro, rd);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 col = effect3d(p, q);

  col = postProcess(col, q);

  fragColor = vec4(col, 1.0);
}

float Circle(vec2 uv, vec2 p, float r, float blur) {
    float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);
    return c;
}
float Smile(vec2 uv, vec3 col, vec2 position) {
    float mask = Circle(uv, position, 0.3, 0.003);
    mask -= Circle(uv, position-vec2(-0.1, -0.08), 0.05, 0.003);

    float leftEye = Circle(uv, position-vec2(0.1, -0.07), 0.05, 0.003);
    leftEye -= Circle(uv, position-vec2(0.1, -0.06), 0.05, 0.003);
    col *= leftEye;
    mask -= leftEye;

    float smile = Circle(uv, position-vec2(0,0), 0.02, 0.003);
    smile -= Circle(uv, position-vec2(0,-0.02), 0.02, 0.003);
