//HI EVERYONE!

// Mercury SDF <3 - https://mercury.sexy/hg_sdf/
#define PI 3.14159265
#define TAU (2*PI)
#define PHI (sqrt(5)*0.5 + 0.5)
#define saturate(x) clamp(x, 0, 1)

float pModPolar(inout vec2 p, float repetitions) {
	float angle = 2.*PI/repetitions;
	float a = atan(p.y, p.x) + angle/2.;
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
	p = vec2(cos(a), sin(a))*r;
	// For an odd number of repetitions, fix cell index of the cell in -x direction
	// (cell index would be e.g. -5 and 5 in the two halves of the cell):
	if (abs(c) >= (repetitions/2.)) c = abs(c);
	return c;
}

#define M1 1.0
#define M2 2.0
#define M3 3.0

vec3 glow = vec3(0);

struct SceneResult
{
  float d;
  float cid;
  float mid;
};

struct MarchResult
{
  vec3 position;
  vec3 normal;
  SceneResult sres;
};

float ffts;
float ffti = 0.;
float time;

float sdBox(vec3 p, vec3 b)
{
  vec3 q = abs(p) - b;
  return length(max(q,0.)) + min(max(q.x,max(q.y,q.z)),0.);
}

float sdTriPrism( vec3 p, vec2 h )
{
  vec3 q = abs(p);
  return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

void rot(inout vec2 p, float a) {
    p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

// base from iq
SceneResult opU(SceneResult d1, SceneResult d2)
{
    if (d1.d < d2.d)
    {
        return d1;
    }
    return d2;
   // return (d1.d < d2.d) ? d1 : d2;
}

// 3D noise function (IQ)
float noise(vec3 p){
	vec3 ip = floor(p);
    p -= ip;
    vec3 s = vec3(7.0,157.0,113.0);
    vec4 h = vec4(0.0, s.yz, s.y+s.z)+dot(ip, s);
    p = p*p*(3.0-2.0*p);
    h = mix(fract(sin(h)*43758.5), fract(sin(h+s.x)*43758.5), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z);
}

SceneResult scene(vec3 p) {
  SceneResult r;
  rot(p.xy,ffti*20.);
  vec3 pp = abs(p);
  for (int i = 0; i < 2; ++i)
  {
    pp = abs(pp) - vec3(4.,4.,5.);
    rot(pp.xy,(time+20.*ffts)*.1);
    rot(pp.yz,time*.1);
  }
  float c = pModPolar(pp.xz,5.);
  pp -= vec3(5,0,0);
  rot(pp.xz,ffti*2.);

  vec3 ppp = p;
  float cc = pModPolar(ppp.xz,5.);
  ppp -= vec3(5.,cc*3.,0.);
  rot(ppp.yz,PI);
  rot(ppp.xz, -time);
  rot(ppp.xy,ffti*.20);

  SceneResult box;
  box.d = sdBox(pp,vec3(2.,1.+ffts*20.,1))+noise(pp);
  box.cid = c;
  box.mid = M1;

  SceneResult tp;
  tp.d = sdTriPrism(ppp,vec2(1.5+ffts*20.,.2));
