          vec3 n = norm(rp);
          if(d.y <= 1.){

            col = vec3(.001)*max(0.,dot(light-rp,n));

          break;
          } else if(d.y ==2.){
              //rd = reflect(rd,n+texture(texNoise,texture(iChannel1,vec2(.3)).r*0+rp.xz*.1).r);
              rd = reflect(rd,n+texture(iChannel1,vec2(.3)).r*2.);
              rp+=rd*.01;
          }else if (d.y==4.)
          {

              col = vec3(1.)*max(0.,dot(normalize(light-rp),n));

          } else if (d.y == 3.){
             col +=vec3(.1,.2,.1)/3.;
              if(!inside){

                rd = refract(rd,n,1./IOR);
                rp-=0.005*n;
                inside = true;
            } else {
                n = -n;
                vec3 _rd = refract(rd,n,IOR);
                if(dot(_rd,_rd)==0.){
                    rd = reflect(rd,n);
                    rp+=0.002*n;

                } else {

                    rd = _rd;
                    rp -= 0.005*n;
                    inside=false;

                }
            }
          }
      }
  }
  col +=acc*1.5;

	//col*=1.5;

    // Output to screen
    fragColor = vec4(pow(col,vec3(.75)),1.);
}

