// Utilities
#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))

// Constants (used in solving)
#define PI 3.14159265359
#define TAU 6.28318530718

// SDFs
float sdDisk(in vec2 p, in vec2 o, in float r) {
    return length(p - o) - r;
}

float sdLine(in vec2 p, in vec2 a, in vec2 b, in float t) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0)) - t;
}

float sdRing(in vec2 p, in vec2 o, in float r, in float t) {
    return abs(length(p - o) - r) - t;
}

// In: r1: float(radius 1), r2: float(radius 2), p: vec2(position of circle 2)
// Out: vec2(intersect angle 1, intersect angle 2)
// Return: bool(intersecting)
bool circleCircle(in float r1, in float r2, in vec2 p, out vec2 angles) {
    float sqDist = dot(p, p);

    float mid = atan(p.y, p.x);
    float offs = (sqDist + r1 * r1 - r2 * r2) / (2.0 * r1 * sqrt(sqDist));
    if (abs(offs) > 1.0) return false; // Not intersecting
    offs = acos(offs);

    angles = vec2(mid - offs, mid + offs);
    return true;
}

// In: a: vec2(origin), b: vec2(target), l1: float(link 1 length), l2: float(link 2 length)
// Out: vec4(left handed angles, right handed angles)
// Return: int(number of solutions)
int ikTwoLinkAngles(in vec2 a, in vec2 b, in float l1, in float l2, out vec4 angles) {
    vec2 ba = b - a, t;
    if (!circleCircle(l1, l2, ba, t)) return 0; // Solve with circle-circle intersection
    angles.xy = vec2(t.x, atan(ba.y - sin(t.x) * l1, ba.x - cos(t.x) * l1)); // Deduce second angle
    angles.zw = vec2(t.y, atan(ba.y - sin(t.y) * l1, ba.x - cos(t.y) * l1)); // Deduce second angle
    return 2;
}

// Out: vec4(lower bound 1, upper bound 1, lower bound 2, upper bound 2)
// Return: int(number of boundaries)
// Once a valid solution is chosen, the remainder can be solved with 2 link kinematics
// Interestingly similar to factoring polynomials eh? Except easier :D
int ikThreeLinkSolutionBounds(in vec2 a, in vec2 b, in float l1, in float l2, in float l3, out vec4 bounds) {
    float outer = abs(l3 + l2), inner = abs(l3 - l2);
    vec2 ba = b - a;
    int count = 0;

    float dist = length(ba);
    if (dist < outer - l1 && (dist < l1 - inner || dist > l1 + inner)) { // All angles are solutions
        bounds.xy = vec2(0.0, TAU);
        return 1;
    }

    if (circleCircle(l1, outer, ba, bounds.xy)) count++; // Outer bound of solution set
    if (circleCircle(l1, inner, ba, bounds.zw)) { // Inner bound of solution set
        if (count == 0) bounds.xy = vec2(bounds.w, bounds.z + TAU); // Change the interpolation direction
        else bounds = bounds.xzwy; // Sort
        count++;
    }

    return count;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Boilerplate
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(1.0);

    // Default before user interaction
    if (ivec2(iMouse.xy) == ivec2(0)) mouse = vec2(2.0, 0.5);

    vec2 a = vec2(0.0); // Origin
    vec2 b = mouse; // Target

    // Link lengths
    float l1 = 1.5;
    float l2 = 0.8;
    float l3 = 0.5;

    vec4 foo;
    if (ikThreeLinkSolutionBounds(a, uv, l1, l2, l3, foo) > 0) color.gb = vec2(0.75);

    // Bound calculation
    float R2inner = abs(l3 - l2); // Inner bound of last two links
    float R2outer = l3 + l2; // Outer bound of last two links

    float R3inner = l1 < R2inner ? abs(l1 - R2inner) : max(0.0, l1 - R2outer); // Inner bound of all three links
    float R3outer = l1 + R2outer; // Outer bound of all three links

    drawSDF(abs(sdDisk(uv, a, R3inner)), vec3(0.0, 0.0, 1.0));
    drawSDF(abs(sdDisk(uv, a, R3outer)), vec3(1.0, 0.0, 0.0));

    // Diagrams
    float inner = abs(l3 - l2), outer = abs(l3 + l2);
    float radius = 0.5 * (inner + outer), thickness = 0.5 * (outer - inner);
    drawSDF(sdRing(uv, b, radius, thickness), vec3(0.2, 0.8, 0.5)); // Fill
    drawSDF(abs(sdRing(uv, b, radius, thickness)), vec3(0.0)); // Outline
    drawSDF(abs(sdDisk(uv, a, l1)) - 0.01, vec3(1.0, 0.5, 0.0));

    // Boundary angles are crossings with the boundaries of the two final links
    vec2 anglesInner, anglesOuter;
    vec2 ba = b - a;

    if (circleCircle(l1, inner, ba, anglesInner)) {
        vec2 c1 = a + vec2(cos(anglesInner.x), sin(anglesInner.x)) * l1;
        vec2 c2 = a + vec2(cos(anglesInner.y), sin(anglesInner.y)) * l1;
        drawSDF(sdDisk(uv, c1, 0.05), vec3(0.2, 0.5, 0.8)); // Fill
        drawSDF(abs(sdDisk(uv, c1, 0.05)), vec3(0.0)); // Outline
        drawSDF(sdDisk(uv, c2, 0.05), vec3(0.8, 0.2, 0.5)); // Fill
        drawSDF(abs(sdDisk(uv, c2, 0.05)), vec3(0.0)); // Outline
    }

    if (circleCircle(l1, outer, ba, anglesOuter)) {
        vec2 c1 = a + vec2(cos(anglesOuter.x), sin(anglesOuter.x)) * l1;
        vec2 c2 = a + vec2(cos(anglesOuter.y), sin(anglesOuter.y)) * l1;
        drawSDF(sdDisk(uv, c1, 0.05), vec3(0.2, 0.5, 0.8)); // Fill
        drawSDF(abs(sdDisk(uv, c1, 0.05)), vec3(0.0)); // Outline
        drawSDF(sdDisk(uv, c2, 0.05), vec3(0.8, 0.2, 0.5)); // Fill
        drawSDF(abs(sdDisk(uv, c2, 0.05)), vec3(0.0)); // Outline
    }

    // Compute angles
    vec4 bounds;
    int numBounds = ikThreeLinkSolutionBounds(a, b, l1, l2, l3, bounds);
    if (numBounds > 0) {
        float transition = mix(1e-4, 1.0 - 1e-4, 0.5 + 0.5 * sin(iTime)); // Precision safety

        float minAngle = bounds.x, maxAngle = bounds.y;
        float angleOfChoice = mix(minAngle, maxAngle, transition); // Choose *arbitrary* angle

        vec4 otherAngles;
        vec2 p = a + vec2(cos(angleOfChoice), sin(angleOfChoice)) * l1;
        int numSolutions = ikTwoLinkAngles(p, b, l2, l3, otherAngles);
        if (numSolutions > 0) {
            vec3 angles = vec3(angleOfChoice, otherAngles.xy);

            // Joint positions
            vec2 v1 = a;
            vec2 v2 = v1 + vec2(cos(angles.x), sin(angles.x)) * l1;
            vec2 v3 = v2 + vec2(cos(angles.y), sin(angles.y)) * l2;
            vec2 v4 = v3 + vec2(cos(angles.z), sin(angles.z)) * l3;

            // More diagrams
            drawSDF(abs(sdDisk(uv, v2, l2)) - 0.01, vec3(0.0, 0.4, 0.4));
            drawSDF(abs(sdDisk(uv, v4, l3)) - 0.01, vec3(0.0, 0.4, 0.4));

            vec2 otherPos = v2 - reflect(v3 - v2, normalize(v4 - v2));
            drawSDF(sdDisk(uv, otherPos, 0.05), vec3(0.75)); // Fill
            drawSDF(abs(sdDisk(uv, otherPos, 0.05)), vec3(0.0)); // Outline

            // Links
            drawSDF(sdLine(uv, v1, v2, 0.05), vec3(0.0));
            drawSDF(sdLine(uv, v2, v3, 0.05), vec3(0.0));
            drawSDF(sdLine(uv, v3, v4, 0.05), vec3(0.0));

            // Joints
            drawSDF(sdDisk(uv, v1, 0.08), vec3(0.5));
            drawSDF(sdDisk(uv, v2, 0.08), vec3(0.5));
            drawSDF(sdDisk(uv, v3, 0.08), vec3(0.5));
            drawSDF(sdDisk(uv, v4, 0.08), vec3(0.5));
        }
    }

    fragColor = vec4(color, 1.0);
}

// Given a path I: [0, 1] -> R^n, and a function defined on 
// this path f: [0, 1] -> R^n ->R^m
// Consider then the extension
// F: R^n -> R^m defined by
// F(x) = C * integral_{i in I} f(i) / (dist(X - I(i))) d I(i) 
// C being a normalizing constant, usually the length of the path I in R^n.
// Then F is a continuous extension of f in the entire space!
//
// We are going to extend the normal map.
// If we have the circle S1 = {x : |x| = 1}, then for each point in S1, it's normal is itself
// That is: N(x) = x for every point x in S1. 
// On the other hand, for a square, the normal of a point x is (+1, 0), (-1, 0), (0, +1) or (0, -1)
// depending on which side of the square the point is.
// Regardless, we have a normal N defined on this path.
// We are going to extend this normal onto the entire plane and then light using common techniques!

vec4 GetLight(vec2 uv) // Calculates the light of a point given it's normal
{
    vec3 ligdir = normalize(vec3(cos(iTime), sin(iTime), -0.4 + 0.4*sin(iTime*0.5))); //Direction of the Light

    vec2 tex = float(uv.x <= 1.0) *
                    texture(iChannel0, uv - vec2(0.0, 1.0)).xy + float(!(uv.x <= 1.0))*
                    texture(iChannel1, uv - vec2(1.0, 1.0)).xy;

    vec3 normal = normalize(2.0*vec3(tex, 0.0) - 1.0); //Convert RGB Coordinates back into Normal vectors

    float intensity = dot(ligdir, normal); //Light it

    vec4 Color = normalize(vec4(1.0, 0.0, 0.5, 0.0));
    vec4 BColor = vec4(0.0, 0.0, 0.2, 0.0);

    return intensity*Color + (1.0-intensity)*BColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.0*fragCoord/iResolution.xy;
    fragColor = float(uv.y <= 1.0) * 
        (uv.x <= 1.0 ? texture(iChannel0, uv) : texture(iChannel1, uv - vec2(1.0, 0.0))) +  float(!(uv.y <= 1.0)) *// uv.y <= 1.0
        GetLight(uv); // 1.0 <= uv.y <= 2.0

    float border = 0.005;
    float condition = float(((uv.x <= 1.0 + border && uv.x >= 1.0 - border) || (uv.y <= 1.0 + border && uv.y >= 1.0 - border)));
    fragColor = condition *
                    vec4(0.0) + (1.0 - condition) *fragColor;
}

