// This buffer gets the normal UV map of a circle

#define PI 3.14159265359

vec2 Normal(vec2 pos) //Calculates the normal of a point in the S1 circunference
{
    return normalize(pos); // The normal of the S1 circunference is the identity
}
vec2 integral(vec2 pos)
{
    float interval = 0.01; //Interval of the ingral
    vec2 sum = vec2(0.0, 0.0); //Value we are going to return

    for(float alpha = 0.0; alpha < 2.0*PI; alpha += interval)
    {
        vec2 circlepos = 0.7*vec2(cos(alpha), sin(alpha)); // Position of the circle given angle alpha
        vec2 val = (1.0/(max(length(circlepos - pos), 0.001))) * Normal(circlepos); // Normal of point Circlepos multiplied by the weight it has on position pos.
        sum = sum + interval * val;
    }
    return (1.0/(4.0*PI))*sum; // We normalize it by the length of the path, which is precisely the perimeter of S1. That is, 2PI
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
