}
float wave(vec3 p, float t){
    float dist = length(p.xz)-mod(t+0.5,1.0)*55.0+0.5;
    dist = min(dist,0.0);
    float wave = 1.*sin(dist)*exp(-abs(length(dist*0.2)));
    wave*=max(0.0,1.0-fract(t+0.5)*2.0);
    return wave;
}
vec2 map(vec3 p){
    vec2 a = vec2(1);
    vec2 b = vec2(2);

    float t = iTime*0.25;

    vec3 po2 = p;
    p.y= ssRemap(p.y*0.7,-0.2,0.6,.3);

    p.y=mix(po2.y,p.y,sat(anim()));
    vec3 po = p;

    float balls = 10.0;
    float wav = 0.0;
    for(float i = 0.0; i<balls; i++){
        p = po2;

        t+=1.0/balls;

        float mag = 15.0;
        p.x+=rand(vec2(floor(t),i))*mag;
        p.z+=rand(vec2(floor(t),i*1.5))*mag; 

        //a.x = smin(a.x,ball(p,t),1.0); 

        b = vec2(ball(p,t),i+3.0);

        a=(a.x<b.x)?a:b;

        wav+=wave(p,t)*1.5;
    }

    t=iTime*4.5;
    p=po;
    wav+=sin(p.z*0.8+t)*0.1+sin(p.x*0.8+t)*0.1;
    wav+=sin(p.z*0.3-t)*0.2+sin(p.x*0.2-t*2.0)*0.2;

    b.x = p.y+wav;
    b.y = 2.0;
    a.x=smin(a.x,p.y+wav,0.4);

    a=(a.x<b.x)?a:b;

    return a;
}
