void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    vec3 ro = vec3(0,15,-30);
    ro.y+=sin(iTime)*3.0;

    ro.xz*=rot(iTime*0.2);

    vec3 lk = vec3(0,0,0);
    vec3 f = normalize(lk-ro);
    vec3 r = normalize(cross(vec3(0,1,0),f));
    vec3 rd = normalize(f*1.0+uv.x*r+uv.y*cross(f,r));

    vec3 p = ro;
    float dO, shad;
    vec2 d;

    for(float i =0.0; i<STEPS; i++){
        p = ro+rd*dO;
        d = map(p);
        dO+=d.x*0.8;

        if((d.x)<0.01){
            shad = i/STEPS;
            break;
        }
        if(dO>MDIST){
            dO=MDIST;
            p = ro+rd*dO;
            d.y=0.0;
            break;
        }
        shad = 1.0;
    }
    vec3 n = norm(p);
    //vec3 ld = vec3(1,1,1);
    vec3 ld=normalize(vec3(1,1.3,2));
    vec3 h = normalize(ld - rd);
    float spec = pow(max(dot(n, h),0.), 20.);
    shad = 1.0-shad;

    col = vec3(shad);

    float sss=0.;

    //sss stolen from nusan
    for(float i=1.; i<20.; ++i){
        float dist = i*2.;
        sss += smoothstep(0.,1.,map(p+ld*dist).x/dist);
    }

    vec3 al;
    if(d.y==2.0)al = vec3(0.220,0.741,1.000);
    if(d.y>2.0)al = 1.4*pal(d.y/10.0, vec3(0.5),vec3(0.5),vec3(1),vec3(0,0.33,0.66) );



    //I'm sorry for butchering these lighting equations, I have no idea what I am doing
    col = shad*vec3(sss)*al;
    col+=spec*0.3;


    //Sky
    p.y-=5.0;

    vec3 sky = mix(vec3(0.012,0.471,0.647),vec3(0.145,0.118,0.522),clamp(p.y*0.05,0.0,1.0));

    col = mix(col,clamp(sky*3.0,0.0,2.0),dO/MDIST);

    fragColor = vec4(col,1.0);
}

#define backbuffer iChannel1
#define fft iChannel0
#define ffts iChannel0
#define bass texture(ffts, vec2(.001)).r


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pt = uv - .5;
    vec2 ar = vec2(iResolution.y / iResolution.x, 1);
    pt /= ar;

    int n = int(100.*pow(bass,5.)) + 1;
    vec3 c = vec3(0.);

    for(int i=0; i<n; i++){
        pt *= .99;
        pt -= .001 * bass * vec2(1.,0.) * r2d(TAU*20.*bass);
        uv = (pt*ar) + .5;

        c += texture(backbuffer, uv).aaa;
    }
    c /= float(n);


    c = c / (1. + c);
    float lum = dot(c.rgb, vec3(.2126, .7152, .0722));
    float shad = smoothstep(.4, .01, lum);
    float high = smoothstep(.3, 1., lum);
    c = c * shad * vec3(.4, 1., 1.5) + c * (1.-shad*high) + c*high*vec3(.99,.6,.8);
    fragColor = vec4(c,1.0);
}

