#define time iTime


#define saturate(x) clamp((x), 0, 1)

const float PI = acos(-1.);
const float TAU = 2. * PI;


const vec3 up = vec3(0,1,0);



mat2 r2d(float t){
  float c = cos(t), s = sin(t);
  return mat2(c,s,-s,c);
}

mat3 r3d(float angle, vec3 axis){
      vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    mat3 m = mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
    return m;
}


float random(vec3 p){
    float noise = 0.;

    vec3 seed = vec3(-4. ,-2.,0.5);

    float amp = 1.;
    float gain = 0.5;
    float lacunarity = 1.4;

    float warp = 1.3;
    float warpTrk = .7;
    float warpTrkGain = 1.5;

    mat3 rotMatrix = r3d(.3, seed);

    for(int i = 0; i < 5; i++){
        p += sin(p.zxy*warpTrk)*warp;
        noise += sin(dot(cos(p.yzx), sin(p.zxy)))*amp;

        p *= rotMatrix;
        p *= lacunarity;

        warpTrk *= warpTrkGain;
        amp *= gain;
    }

    return noise*.5;
}

void chmin(inout vec4 a, vec4 b){
    a = a.x < b.x ? a : b;
}

float box(vec3 p, vec3 b){
  p = abs(p) - b;
  return min(0., max(p.x, max(p.y, p.z))) + length(max(p,0.));
}

float octahedron(vec3 p, float s)
{
    p = abs(p);
    float m = p.x + p.y + p.z - s;
    vec3 r = 3.0*p - m;
    // iq's original version
  	vec3 q;
    if( r.x < 0.0 ) {q = p.xyz;}
    else if( r.y < 0.0 ){ q = p.yzx;}
    else if( r.z < 0.0 ){ q = p.zxy;}
    else {return m*0.57735027;}
    float k = clamp(0.5*(q.z-q.y+s),0.0,s);
    return length(vec3(q.x,q.y-s+k,q.z-k));
}

float rectSDF(vec2 st, vec2 size){
  return max(abs(st).x * size.x, abs(st).y * size.y);
}

float crossSDF(vec2 st, float s){
  vec2 size = vec2(.25, s);
  return min(rectSDF(st, size.xy),
    rectSDF(st, size.yx));
}

