#define STEPS 250.0
#define MDIST 550.0
#define pi 3.1415926

#define pmod(p,x) (mod(p,x) - (x)*0.5)

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float smin(float a, float b, float k){
  float h = max(0.0,k-abs(a-b));
  return min(a,b)-h*h*0.25/k;
}
float anim(){
  return sin(iTime); 
}
float rand(vec2 a){
  return fract(sin(dot(a,vec2(43.234,21.4343)))*94544.3434343)-0.5;
}
float ssRemap(float t, float s1, float s2, float c){
  return 0.5*(s2-s1)*(t-asin(cos(t*pi)/sqrt(c*c+1.0))/pi)*s1*t;
}
float wave(vec3 p, float t){
  float dist = length(p.xz)-fract(t+0.5)*50.0;
  dist = min(dist,0.0);
  float wave = sin(dist)*exp(-abs(length(dist*0.2)));
  wave*=max(0.,1.0-fract(t-0.5)*2.0);
  return wave;
}
float ball(vec3 p, float t){
  float mag = 80.0;
  p.y += (fract(t)-0.5)*mag;
  float a = length(p)-3.0;
  return a;
}

vec2 map(vec3 p){
  vec3 po = p;

  float t= mod(iTime,300.0)*0.6;
  vec2 a = vec2(1);
  vec2 b = vec2(2);

  //Bending
  p.y = pmod(p.y,60.0);

  float th = atan(p.x,p.z)/(2.0*pi)+0.5;
  th*=300.0;
  float r = length(p.xz)-60.0;
  p.x = r;
  //p.z = th;
  p.xy*=rot(sin(t));
  //p.xy*=rot(p.z*0.01*sin(t));
  p.xy = abs(p.xy)-5.0;
  p.xy*=rot(-pi/4.0);
  p.y=mix(p.y,ssRemap(p.y,0.01,0.4,0.3),0.5+0.5*anim());

  vec3 po2 = p;
  float count = 6.0;
  float wav =0.0;
  for(float i =0.0; i < count; i++){
    p = po2;
    t+=1.0/count;
    float mag = 10.0;
    p.x+=rand(vec2(floor(t),i))*mag;
    p.z+=(rand(vec2(floor(t),i*1.5)))*mag*30.0;
    a.x = smin(a.x,ball(p,t),0.5);
    wav +=wave(p,t)*1.5;
  }
  p = po2;
  t = mod(iTime,300.0)*4.0;
  wav+=sin(p.z+t)*0.1+sin(p.x+t)*0.1;
  wav+=sin(p.z*0.5-t)*0.2+sin(p.x*0.5-t)*0.2;

  //a.x = ball(p,t);
  b.x = p.y-wav;


  a=(a.x<b.x)?a:b;

  return a;
}

vec3 norm(vec3 p){
  vec2 e = vec2(0.01,0);
  return normalize(map(p).x-vec3(
  map(p-e.xyy).x,
  map(p-e.yxy).x,
  map(p-e.yyx).x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

  float t= mod(iTime,300.0);
  vec3 col = vec3(0.1);
  vec3 ro = vec3(0,70,-150);

  float drop = 0.0;
  drop = 0.0;
  ro.y-=drop;

  //ro.z*=sin(t);
  //ro.y += sin(t)*20.0;
  ro.xz*=rot(t*0.3);
  vec3 lk = vec3(0,0,0);

  lk.y-=drop;
  vec3 f = normalize(lk-ro);
  vec3 r = normalize(cross(vec3(0,1,0),f));
  vec3 rd = f*1.0+uv.x*r+uv.y*cross(f,r);

  vec3 p = ro;
  float dO, shad;
  vec2 d;

  for(float i = 0.0; i<STEPS; i++){
    p = ro+rd*dO;
    d = map(p);
    dO+=d.x*0.9;

    if(abs(d).x<0.01){
      shad = i/STEPS;
      break;
    }
    if(dO>MDIST){
      dO = MDIST;
      p = ro+rd*dO;
      d.y=0.0;
      break;
    }
  }
  shad = 1.0-shad;
  vec3 n = norm(p);
  vec3 ld = normalize(vec3(1,1,0));
  vec3 h = normalize(ld - rd);
  float spec = pow(max(dot(n,h),0.0),20.0);

  vec3 al = vec3(0);
  if(d.y==1.0) al = vec3(0,0.8,0.2);
  if(d.y==2.0) al = vec3(0,0,0.9)*1.5;
  p.y-=5.0;
  vec3 back = mix(vec3(0.1,0.1,0.5),vec3(0.0,0.4,0.6),clamp(p.y*0.05,0.0,1.0))*0.9;
  //back = vec3(0);

  col = shad*al;
  col+=spec*0.5;

  col = mix(col,back*2.0,(dO/MDIST));

  fragColor = vec4(col,0);
}

vec3 cam(vec3 ro, vec3 rd, vec2 uv) {
  vec3 f = normalize(rd - ro);
  vec3 l = normalize(cross(vec3(0,1,0), f));
  vec3 u = normalize(cross(f, l));

  return normalize(f + l * uv.x + u * uv.y);
}

float ni(float a){
    //return texture(texFFTIntegrated, a).x;
    return iTime;
}


void mo(inout vec2 p,vec2 d){p = abs(p)-d;if(p.y>p.x)p=p.yx;}
vec3 rep(vec3 p, vec3 c) { return mod(p + .5 * c, c) - .5 * c; }
vec3 repl(vec3 p,vec3 c,vec3 l){return p-c*clamp(round(p/c),-l,l);}
mat2 rot(float t) { return mat2(cos(t), sin(t), -sin(t), cos(t)); }
float sdBox(vec3 p, vec3 b) { vec3 q = abs(p) - b; return length(max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.); }
float sdSphere(vec3 p, float r) { return length(p) - r; }
float sdPlane(vec3 p, vec3 n, float h) { return dot(p,n) + h; }
float rnd(float p) { p = fract(p * .131); p *= p + 333.33; return fract(2. * p * p); }
float dirlight(vec3 n, vec3 ld) {
  ld = normalize(ld);
  return clamp(max(dot(n, ld), 0.), 0., 1.);
 }
float spec(vec3 p, vec3 n, vec3 ld, float k) {
    ld = normalize(ld);
    vec3 r = reflect(-ld, n);
    vec3 v = normalize(-p);
    return pow(max(dot(r,n), 0.), k);
}
vec3 palette(float t) {
    vec3 a= vec3(1);
    vec3 b= vec3(1);
    vec3 c = vec3(2);
    vec3 d = vec3(0,.1, .2);
    return a + b * cos(6.28318 * (c * t + d));
}

vec2 ground(vec3 p) {
    float plane = sdPlane(p, vec3(0,1,0), 0.);
    float cracks = texture(iChannel0, p.xz * 0.05).r;
    float crack = 0.28;

    return vec2(
    plane + texture(iChannel1, p.xz * .7).r * 0.03
      + texture(iChannel0, p.zx * 0.1).r * 1.
      + texture(iChannel0, p.xz * 0.01).r * 4.,

       cracks > crack && cracks < crack + 0.01 ? 4 : 0
    );
}


float bid;
float tunnel(vec3 p) {
    vec3 bp = p;
    bp.xy *= rot(bp.z * 0.1);
    mo(bp.xy, vec2(0.5));
    mo(bp.zy, vec2(0.5));
    bid = rnd(round(p.z + 0.5) * 120.);
    float b = sdBox(rep(bp + vec3(-1.5, 0, 0), vec3(0, 0, 2)), vec3(0.1, 1.5, 0.1));
    return b;
}


float sid;
vec2 sphrs(vec3 p) {
  p = p + vec3(0,0,-iTime - 7.);
  p.xz *= rot(iTime);
  p.yz *= rot(iTime);
  sid = rnd(round(p.x - 0.75) * 10. + round(p.y - 0.75) * 20. +  round(p.z - 0.75) * 10.);
  p = repl(p, vec3(.5), vec3(2));
  float s = sdSphere(p + vec3(sin(iTime * 10. + p.x / 10.) * 0.05), .2 - abs(sin(sid * 60. + iTime)) * .15);
  return vec2(s, fract(sid + iTime) < 0.9 ? 2 : 3);
}

vec2 map(vec3 p) {
    float m = 999.;
    float mm = 0.;

    vec2 g = ground(p);
    if (g.x < m) { m = g.x; mm = g.y; }

    float t = tunnel(p);
    if (t < m) { m = t; mm = 1.; }

    vec2 s = sphrs(p);
    if (s.x < m) { m = s.x; mm = s.y; }

    return vec2(m, mm);
}
vec3 norm(vec3 p) {
    float E = 0.01; vec2 k = vec2(1, -1);

    return normalize(
      k.xyy * map(p + k.xyy * E).x +
      k.yyx * map(p + k.yyx * E).x +
      k.yxy * map(p + k.yxy * E).x +
      k.xxx * map(p + k.xxx * E).x 
    );
}

vec3 bg(vec2 uv) {
    float h = uv.y;
    float n = texture(iChannel0, vec2(uv.x, uv.y - iTime * 0.05)).x;
    float s = texture(iChannel0, uv * 6. + vec2(sin(iTime) + iTime, cos(iTime))).x > 0.45 ? 1. : 0.;

    return vec3(n * h*2. * palette(uv.y / 2.1));
}


vec3 ld = vec3(0, 30, 10);
vec4 tr(vec3 ro, vec3 rd, vec2 uv) {
    float td = 1.;
    vec2 h;
    vec4 c = vec4(0);
    vec4 g = vec4(0);
    int bnc = 0;
    float en = 1.;

    for (int i = 0; i < 1000; i++) {
        vec3 ip = ro + rd * td;
        h = map(ip);
        td += h.x * 0.3;

        if (h.y == 1.) {
            g += vec4(palette(bid * (ni(0.15) * 1. + iTime / 2.)) * exp(-h.x * 10.) * .07, 1.);
        }

        if (h.y == 3.) {
          g += vec4(palette(sid / 10.) * exp(-h.x * 10.) * .07, 1.);
        }
        if (h.y == 4.) {
          g += vec4(palette(0.63 + sin(ip.x / 10. + iTime * 0.2)) * exp(-h.x * 10.) * (.5 + abs(sin(ip.x + ip.z + iTime)) * .5), 1.);
        }


        if (h.x < 0.01) {
          vec3 inorm = norm(ip);

          if (h.y == 0.) {
              c += vec4(dirlight(inorm, ld) * palette(ip.y * 0.2 + 0.45) * en * 0.7 + spec(ip, inorm, ld, 20.), 1.) * en;
           }

           if (h.y == 1.) {
                c += vec4(palette(bid * (ni(0.15) * 1. + iTime / 2.)) * en, 1.);
           }

           if (h.y == 2.) {
             c += vec4(0.2) * en;
             ro = ip;
             rd = reflect(rd, inorm);
             td = .1;
             bnc += 1;
             en = max(en - .7, 0.);
           }

           if (h.y == 3.) {
               c += vec4(palette(sid / 10.) * en, 1.);
            }

            if (h.y == 4.) {
                c += vec4(0.9);
            }
        }

        if (td > 200.) {
            return vec4(bg(uv) + vec3(g) * exp(-td * 0.05), td/1000.);
        }

        if (c.a >= 1. || bnc > 1 || en < 0. || td > 1000.) break;
    }

    return vec4((vec3(c) + vec3(g)) * exp(-td * 0.05), td/1000.);
}

