vec3 fold(vec3 p, float t)
{
    vec3 n=vec3(-.5,-cos(PI/t),0);
    n.z=sqrt(1.-dot(n,n));         // normalize
    for(float i=0.; i<t; i++){
        p.xy=abs(p.xy);
        p-=2.*min(0.,dot(p,n))*n;
    }
    return p;
}

float map(vec3 p)
{
	rot(p,vec3(cos(iTime*.3),sin(iTime*.5),.5*sin(iTime*.2)),iTime*.7);
    float t = float[](3.,4.,5.)[int(fract(iTime/8.)*3.)];
    p = fold(p,t);
    p.z-=1.;
    vec3 n=vec3(-.5,-cos(PI/t),0);
    n.z=sqrt(1.-dot(n,n));         // normalize
    return float[](
            dot(p, normalize(vec3(n.z,0,-n.x))),
            dot(p, normalize(vec3(0,n.z,-n.y))),
            length(p.xz)-.1,
            length(p.yz)-.1
        )[int(fract(iTime/2.5)*4.)];
}

vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.001*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<70;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
    }
    return far;
}

