float eps = .03;
float n = 11.0;

//circle need to perform POI detection
vec2 circle[16] = vec2[](vec2(0.0,3.0),
            vec2(-1.0,3.0),
            vec2(-2.0,2.0),
            vec2(-3.0,1.0),
            vec2(-3.0,0.0),
            vec2(-3.0,-1.0),
            vec2(-2.0,-2.0),
            vec2(-1.0,-3.0),
            vec2(0.0,-3.0),
            vec2(1.0,-3.0),
            vec2(-2.0,2.0),
            vec2(-1.0,3.0),
            vec2(0.0,3.0),
            vec2(1.0,3.0),
            vec2(2.0,2.0),
            vec2(1.0,3.0)
           );


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvStep = vec2(1.0)/iResolution.xy;

    float pixelColor = texture(iChannel0,uv).x;

    float poiVal = 0.0;

    //fast bail out if nothing found in the four corner
    for(int i = 0; i < 16; i+=4)
    {
        if(abs(texture(iChannel0,uv + circle[i]*uvStep).x - pixelColor) >= eps)
            poiVal += 1.0;

    }
    if( poiVal < 3.0)
    {
        fragColor = vec4(texture(iChannel0,uv));
        return;
    }

    //reset counter and add total for NMS
    poiVal = 0.0;

    //check in a cirlce of raduis 3 around the pixel
    for(int i = 0; i < 16; i++)
    {
        if (abs(texture(iChannel0,uv + circle[i]*uvStep).x - pixelColor) >= eps)
            poiVal += 1.0;
        else
        {
            if(poiVal >= n) continue;
            poiVal = 0.0;
        }
    }


    if(poiVal >= n)
        fragColor = vec4(vec3(1.0,0.0,0.0),1.0);
    else
        fragColor = vec4(texture(iChannel0,uv));

}

