// Signed distance to a disk

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and iquilezles.org/articles/distfunctions2d

//Shift points on one side of an axis
vec2 fold(vec2 p, vec2 axis, mat2 rotation){
    return dot(p, axis) > 0.0 ? rotation*p : p;
}

//Shift a point into the first quadrant (different than abs, but same in this context)
vec2 fold4(vec2 p){
    p = p.y < 0.0 ? -p : p;
    p = p.x < 0.0 ? vec2(p.y, -p.x) : p;

    return p;
}

vec2 fold12(vec2 p){
    //It's better to fold everything into the first quadrant first assuming
    //the number of slices is divisible by 4
    p = fold4(p);
    //Then the first quadrant is split into 3
    p = fold(p, vec2(-0.8660254, 0.5), mat2(0.5, -0.8660254, 0.8660254, 0.5));
    p = fold(p, vec2(-0.5, 0.8660254), mat2(0.8660254, -0.5, 0.5, 0.8660254));
    return p;
}

float sdCircle( in vec2 p) 
{
    p = fold12(p);
    return length(p - vec2(0.965926, 0.258819)*0.75) - 0.15;
}


