        //g = p1*(t*t*t) + p2*3.*(1.-t)*(t*t) + p3*3.*(1.-t)*(1.-t)*(t) + p4*(1.-t)*(1.-t)*(1.-t);

        col = drawLine(prev,g,p,col,segCol,segThickness);
        col = drawRing(segRingRadius, .003, p, g, col, segCircleCol);
        prev = g;
    }
    return col;
}


//draw the hull of a bezsier part
vec3 drawHull(float t, vec2 p1, vec2 p2, vec2 p3, vec2 p4, vec2 uv, vec3 col){

//all of this must happen for each new hull
    vec2 dLinePoint1 = p1*t + (1.0-t)*p2;
    vec2 dLinePoint2 = p2*t + (1.0-t)*p3;
    vec2 dLinePoint3 = p3*t + (1.0-t)*p4;

    vec2 ddPoint1 = dLinePoint1*t + (1.0-t)*dLinePoint2;
    vec2 ddPoint2 = dLinePoint2*t + (1.0-t)*dLinePoint3;

    vec2 dddPoint = ddPoint1*t + (1.0-t)*ddPoint2;

    // Time varying pixel color
    vec3 magik_farber = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)/4.);


    col = drawLine(p1,p2,uv,col,lineCol*0.5,0.004);
    col = drawLine(p2,p3,uv,col,lineCol*0.5,0.004);
    col = drawLine(p3,p4,uv,col,lineCol*0.5,0.004);
    col = drawLine(dLinePoint1,dLinePoint2,uv,col,lineCol*0.7,0.004);
    col = drawLine(dLinePoint2,dLinePoint3,uv,col,lineCol*0.7,0.004);
    col = drawLine(ddPoint1,ddPoint2,uv,col,lineCol,0.004);

    col = drawRing(0.01, 0.003, uv, p1, col, lineCol);
    col = drawRing(0.01, 0.003, uv, p2, col, lineCol);
    col = drawRing(0.01, 0.003, uv, p3, col, lineCol);
    col = drawRing(0.01, 0.003, uv, p4, col, lineCol);

    col = drawRing(0.01, 0.003, uv, dLinePoint1, col, circleCol);
    col = drawRing(0.01, 0.003, uv, dLinePoint2, col, circleCol);
    col = drawRing(0.01, 0.003, uv, dLinePoint3, col, circleCol);
    col = drawRing(0.01, 0.003, uv, ddPoint1, col, circleCol);
    col = drawRing(0.01, 0.003, uv, ddPoint2, col, circleCol);

    return col;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;

    float xOff =0.4, yOff=1.2, zOff=2.6, wOff=3.;
    float eps = 1./min(iResolution.x, iResolution.y);


    //Original Bezier
    vec2 p1 = vec2(-0.6, -0.3) + vec2(sin(iTime/2.+xOff),cos(iTime/2.+xOff))/5.;
    vec2 p2 = vec2(-0.5, 0.3) + vec2(sin(iTime+yOff),cos(iTime+yOff))/10.;
    vec2 p3 = vec2(0.5, 0.3) + vec2(sin(iTime+zOff),cos(iTime+zOff))/7.;
    vec2 p4 = vec2(0.1, -0.2) + vec2(sin(iTime/2.+wOff),cos(iTime/2.+wOff))/2.;


    //All the possible split values for only one split (0. to 1 )
    float splitPoint = fract(iTime)/numSegments;

    //an animated t so we see all the values and how the hull moves
    float t = sin(iTime/2.)*0.5+0.5;

    vec3 col = bgCol;

    // Time varying pixel color
    vec3 magik_farber = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)/4.);


    //Determining the Hull points of the first half of the bezier Curve
    //Matrix Representation

    //the cubic function
    vec4 T = vec4(1, t, t*t, t*t*t);

    //cooefficient matrix
    //nice looking upper triangular matrix there. 
    mat4 M = mat4(1,0,0,0, 
                  -3,3,0,0, 
                  3,-6,3,0, 
                  -1,3,-3,1);

    //I wonder what's gonna happen...             
    vec4 px = vec4(p4.x,p3.x,p2.x,p1.x);
    vec4 py = vec4(p4.y,p3.y,p2.y,p1.y);

    mat4 Mi = inverse(M);

    float z = 0.5+sin(iTime)*0.3;

    if(iMouse.z > 0.0){
        z = 1.0-iMouse.x/iResolution.x;
    }
    //the Z matrix that comes from going only from 0 to z instead of 0 to 1
    mat4 Z = mat4(1.,0.,0.,0.,
                  0.,z,0.,0.,
                  0.,0.,z*z,0.,
                  0.,0.,0.,z*z*z);

   //B(t) = vt*Z*M*vp = vt*M*Mi*Z*M*vp 
   //Q = Mi*Z*M   so...
   //B(t) = vt*M*Q*vp 
   //and the matrix form of a bezier curve is vt*M*vp 
   //so we jsut do vt*M*[Q*vp] and [Q*vp] are the new coordinates!

   vec4 vpx = px*(M*Z*Mi);
   vec4 vpy = py*(M*Z*Mi);

   vec2 pp1 = vec2(vpx.r,vpy.r);
   vec2 pp2 = vec2(vpx.g,vpy.g);
   vec2 pp3 = vec2(vpx.b,vpy.b);
   vec2 pp4 = vec2(vpx.a,vpy.a);


    col = drawHull(t,pp1,pp2,pp3,pp4,uv,col);
    col = drawSegments(uv, pp1, pp2, pp3, pp4, col);


    //
    //Repeat all of that but with the Zprimg matrix that descibes the other half

    // T prime is [1, (z+(1-z)*t), (z+(1-z)*t)*(z+(1-z)*t), (z+(1-z)*t)*(z+(1-z)*t)*(z+(1-z)*t))]

    //that because:
    //ok this is a bezier function with two variables
    //z is an offset, t is an input that will varie from 0 to 1 sa usual 
    //but it's contrained between z and 1
    // z +(1-z)*t the percentage of t = z + the percentage of t that 
    // for example, with z = 0.5 and t = 0.2: (0.5 + (1 - 0.5)*0.2 = 0.6
    //basically the prportion is thesame but it is scaled to the room beyond z
    //AND offset by z. Pretty cool.
    //Then this happends for every t term in the curve with the matching order(exponent)


    //now we take z out of t and give it it's own matrix by expanding every component
    //and using the terms as matrix entries and the order determins the row,
    //just like in the first Z matrix

    //and the exansion follows basically all the patterns from 1, to linear to quadratic to cubic

    // 1, =                              1,0,0,0

    // z+(1-z))=                         z, (1-z), 0, 0

    // z+(1-z))(z+(1-z))=                z*z, 2*z*(1-z), (1-z)*(1-z), 0

    // z+(1-z))*(z+(1-z))*(z+(1-z))) =   z*z*z, 3*z*(1-z)*(1-z), 3*(1-z)*z*z, (1-z)*(1-z)*(1-z)

    mat4 Zprime = mat4(1.,  z,        z*z,             z*z*z,
                       0.,  (1.-z),   2.*z*(1.-z),       3.*z*z*(1.-z),
                       0.,   0.,      (1.-z)*(1.-z),   3.*z*(1.-z)*(1.-z),
                       0.,   0.,      0.,              (1.-z)*(1.-z)*(1.-z));

    //the order of every column in this matrix is critical
    //it needs to be exactly as it would come out of an expansion from the binomial

    //Awesome, now calculate the points for the first half(should be second?)

    vpx = px*(M*Zprime*Mi);
    vpy = py*(M*Zprime*Mi);

    pp1 = vec2(vpx.r,vpy.r);
    pp2 = vec2(vpx.g,vpy.g);
    pp3 = vec2(vpx.b,vpy.b);
    pp4 = vec2(vpx.a,vpy.a);


    col = drawHull(t,pp1,pp2,pp3,pp4,uv,col);
    col = drawSegments(uv, pp1, pp2, pp3, pp4, col);

    //col = drawSegments(uv, p1, p2, p3, p4, col);
    col = drawRing(0.02+(0.01+sin(iTime*4.)*0.01), 0.003, uv, pp1, col, magik_farber);

    col = pow(col, vec3(0.75));
    //vignett from ApoorvalJ https://www.shadertoy.com/view/4lSXDm
    uv = fragCoord/iResolution.xy;
    uv *=  1.0 - uv.yx;
    float vig = uv.x*uv.y * 15.0; // multiply with sth for intensity

    vig = pow(vig, 0.15); // change pow for modifying the extend of the  vignette

    // Output to screen
    fragColor = vec4(col*vig*1.1,1.0);
}

