// sdf for a box
float sdBox(vec2 p, vec2 b)
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

// sdf for sphere
float sdSphere(vec2 p, float r)
{
    return length(p)-r;
}

// main sdf
float map(vec2 p)
{
    return sdBox(p, vec2(0.4, 0.3));
}

// get normal/gradient of sdf
vec2 normal(vec2 p)
{
    const float eps = 0.0001; // or some other value
    const vec2 h = vec2(eps,0);
    return normalize( vec2(map(p+h.xy) - map(p-h.xy),
                           map(p+h.yx) - map(p-h.yx) ) );
}

// line distance function
float sdLine( vec2 p, vec2 a, vec2 b )
{
    vec2 v = a, w = b;
    float l2 = pow(distance(w, v), 2.);
    if(l2 == 0.0) return distance(p, v);
    float t = clamp(dot(p - v, w - v) / l2, 0., 1.);
    vec2 j = v + t * (w - v);
    return distance(p, j);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // get uv, mouse pos
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 mouse = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    // visualize box sdf and isolines
	float d = map(p);
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

    // get gradient (normal) at mouse position
    vec2 norm = normal(mouse);

    // find circle centroid, show sphere
    float radius = 0.1;
    vec2 center = mouse - norm * (map(mouse) - radius);
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02, abs(sdSphere(p - center, radius))) );

    // visualize gradient as line
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02, sdLine(p, mouse, mouse + norm*0.5)) );

	fragColor = vec4(col,1.0);
}

