/// Possible things one could add to this:
/// * Rays should attenuate the further away they get from the camera.


// Based on what I read from: https://paroj.github.io/gltut/Illumination/Tut11%20BlinnPhong%20Model.html

//  Apparently, Blinn's model needs material.alpha to be higher than Phong's model
// to create the same effect. I concluded this by comparing this with the one which
// this shader is forked from

// Colors should be in standard rgb format: 0. < rgb < 1.

#define MAX_MARCH_STEPS (1<<7)
#define MAX_MARCH_DIST 100.
#define SURF_DIST_MARCH .01
#define EULER_APPROX_OFFSET .003

struct Sphere {
    vec3 o;
    float r;
};

struct PhongMaterial {
    vec3 albedo;
    float ks, kd, ka, alpha;
};

struct PointLight {
    vec3 pos;
    vec3 col;
};


const Sphere s = Sphere(vec3(0, 1, 6), 1.);
const PointLight light = PointLight(vec3(0, 5, 6),
                                    vec3(1.000,0.878,0.878));
const PhongMaterial sphereMaterial = PhongMaterial(vec3(0.9,.3,0.94), 
                                                   .33, .64, .3, 64.);
const PhongMaterial globalMaterial = PhongMaterial(vec3(.4, .9, 1.), 
                                                   .1, .7, .2, 8.);
const PhongMaterial mats[2] = PhongMaterial[](globalMaterial, 
                                              sphereMaterial);

const vec3 rayOrigin = vec3(0, 1, 1);



float SphereSDF(in vec3 p, in Sphere s) {
    return length(p - s.o) - s.r;
}

float SceneSDF(out int hitObject, in vec3 p) { // sdf for the scene.
    float sphereDist = SphereSDF(p, s);
    float planeDist = p.y; // ground

    float d = min(planeDist, sphereDist);
    hitObject = sphereDist == d ? 1 : 0;
    return d;
}

float RayMarch(out int hitObject, in vec3 ro, in vec3 rd) {
    float dO = 0.; // Distance I've marched from origin

    for (int i = 0; i < MAX_MARCH_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = SceneSDF(hitObject, p);
        dO += dS;  // Safe distance to march with
        if (dO > MAX_MARCH_DIST || // Far-plane clipping
            dS < SURF_DIST_MARCH)  // Did we hit anything?
            break;
    }

    return dO;
}

/*
vec3 GetNormalEulerTwoSided(in vec3 p) { // get surface normal using euler approx. method
    vec2 e = vec2(EULER_APPROX_OFFSET, 0);
    int _;

    vec3 left = vec3(SceneSDF(_, p),
                     SceneSDF(_, p - e.yxy),
                     SceneSDF(_, p - e.yyx)),
        right = vec3(SceneSDF(_, p + e.xyy),
                     SceneSDF(_, p + e.yxy),
                     SceneSDF(_, p + e.yyx));

    vec3 n = normalize(-left + right);
    return n;
}
*/

vec3 GetNormalEulerOneSided(in vec3 p) { // get surface normal using euler approx. method
    vec2 e = vec2(EULER_APPROX_OFFSET, 0);
    int _;
    vec3 center = vec3(SceneSDF(_, p)),
          right = vec3(SceneSDF(_, p + e.xyy),
                       SceneSDF(_, p + e.yxy),
                       SceneSDF(_, p + e.yyx));

    vec3 n = normalize(right - center);
    return n;
}

#define GetNormal GetNormalEulerOneSided
//#define GetNormal GetNormalEulerTwoSided


/*
  p  -> position of point to shade
  ro -> ray origin (position of the camera)
*/
vec3 PhongIllumination(in vec3 p, in vec3 ro, in int hitObject) {
    vec3 lightPosOffset = vec3(sin(2. * iTime), 0, cos(2. * iTime)) * 3.;
    vec3 lightPos = light.pos + lightPosOffset;
    // PhongMaterial mat = (hitObject == 1) ? sphereMaterial : globalMaterial; // bugs are great!

    vec3 l = normalize(lightPos - p); // light vector
    vec3 n = GetNormal(p); // get normal of p
    vec3 r = reflect(l, n);
    vec3 v = normalize(ro - p);

    vec3 h = normalize(l + v); // the `half-angle` vector

    float dif  = clamp(dot(l, n), 0., 1.);
    float spec = clamp(dot(h, n), 0., 1.);  // also called `blinn term`

    // shadow stuff
    vec3 pOffset = n * SURF_DIST_MARCH * 1.2; // move the point above a little
    int _;
    float d = RayMarch(_, p + pOffset, l);
    if (d < length(lightPos - p)) { // If true then we've shaded a point on some object before, 
                                    // so shade the currnet point as shodow.
        dif *= .3; // no half-shadow because the light source is a point.    
        spec = 0.; // shadows don't have specular component, I think.
    }

    // Acutal Phong stuff
    vec3 ambientDiffuse = light.col * mats[hitObject].albedo;
    vec3 light1DiffuseComponent = dif * light.col;
    vec3 light1SpecularComponent = vec3(pow(spec, mats[hitObject].alpha));

    vec3 col = mats[hitObject].ka * ambientDiffuse + 
               mats[hitObject].kd * light1DiffuseComponent + 
               mats[hitObject].ks * light1SpecularComponent;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5 ) / iResolution.y; // center around origin

    // simplest camera
    vec3 ro = rayOrigin;
    vec3 rd = normalize(vec3(uv.xy, 1));


    // RayMarching stuff
    int object;
    float d = RayMarch(object, ro, rd);
    vec3 p = ro + rd * d;

    vec3 col = PhongIllumination(p, ro, object);
    fragColor = vec4(col,1.0);
}

/*

    Faceted Torus Rendering
    -----------------------

    There are countless hand drawn geometric images online, and a lot of 
    tutorials showing people how to draw them. Most involve multiple 
    simplistic repetitive steps and measurements, which make their 
    construction well suited to a computer.

    The downside is that computers do such a good job that the results tend 
    to look too flawless, which means some of the artistry gets lost in 
    translation. Ironically, we then have to complicate things to put the 
    flaws back in. In this case, I've done that via a basic post-processing
    algorithm. It works, but there are better ones out there. Flockaroo has 
    some great examples of various rendering styles for anyone interested.

    This particular geometric rendering is a faceted twisted torus and is 
    simple to produce. In fact, anyone with basic geometric knowledge can do 
    it easily: Start off with an n-gon (the default is an octagon), then 
    render some radial quad strips for each side using basic vector addition 
    and trigonometry. How one goes about it is up to the individidual, but 
    you can check to see how I've done it below.

    Obviously, this is a 2D rendering of a 3D object, but it's possible to
    render a 3D version in the same style, which I plan to put together in 
    due course. I'll leave the short version to the code golfers. :)




    Other 2D polygon-based sketch examples:


    // Very watchable, and with virtually no code. 
    Cube Circle Sketch - Shane
    https://www.shadertoy.com/view/3dtBWX


*/

// Background: Plain: 0, Sunset: 1, Light blue: 2.
#define BACKGROUND 0

// Sketch only.
//#define SKETCH

// Offset scribbled edges.
//#define OFFSET_EDGES

// Vertices.
//#define VERTICES

// Face center marking.
//#define FACE_CENTER

// Light panels... Yeah, it's a bit much, but I had to try. :)
//#define PANELS

// Rotate the torus about the XY plane.
//#define ROTATE

// N-gon degree. The default is octagonal, but numbers 4 to about 16
// work well enough.
#define N 8


void mainImage(out vec4 fragColor, in vec2 fragCoord){

    // Aspect correct screen coordinates.
    float iRes = min(iResolution.y, 800.);
	vec2 uv = (fragCoord - iResolution.xy*.5)/iRes;

    // Scaling and translation.
    float gSc = 1.;// + sin(iTime/3.)*.25;

    // Smoothing factor.
    float sf = gSc/iRes;

    // Scaling, translation, etc.
    vec2 p = uv*gSc; 

    // Coordinate perturbation. There's small rigid one to enhance the hand-drawn look, and
    // a larger animated one to wave the paper around a bit.
    vec2 offs = vec2(fbm(p*16.), fbm(p*16. + .35));
    vec2 offs2 = vec2(fbm(p*1. + iTime/4.), fbm(p*1. + .5 + iTime/4.));
    const float oFct = .007;
    const float oFct2 = .04;
    p -= (offs - .5)*oFct;
    p -= (offs2 - .5)*oFct2;



    // Set the background to something neutral.
    float lgt = dot(rot2(-3.14159/6.)*uv, vec2(.5)) + .5;
    #if BACKGROUND == 0
    vec3 col = mix(vec3(.6, .55, .45), vec3(.9, .85, .8), lgt);
    #elif BACKGROUND == 1
    vec3 col = mix(vec3(.7, .2, .3), vec3(1, .7, .3), lgt);
    col = mix(col, col.yzx, .15);
    #else
    vec3 col = vec3(.65, .85, 1)*mix(.8, 1.2, lgt);
    #endif


    // Edge width. 
    const float ew = .00165;  

    // N-Gon degree.
    const float fN = float(N);

    // Center to vertex vector.
    vec2 vi = vec2(-.15, 0); 

    // Quad side length vector, used to construct the faces.
    vec2 sL = vec2(0, abs(vi.x*sin(6.2831/fN/2.)*2.));


    // Whole object, hit polygon and line distance fields.
    float obj = 1e5, polyMin = 1e5, line = 1e5;

    // Offset edges.
    float edge = 1e5;

    // The object vertices.
    #ifdef ROTATE
    vec2 q = rot2(iTime/8.)*p;
    #else
    vec2 q = p;
    #endif

    // Hit quad vertices.
    vec2[4] pV;
    // Hit quad ID.
    vec2 id;


    // Quad circular strip for this edge.
    for(int i = min(0, iFrame); i<N; i++){

        // First quad for this particular edge.
        vec2[4] vR;

        vR[0] = rot2(-6.2831/fN*float(i) + 3.14159/fN)*vi; 
        vR[1] = vR[0]+ rot2(3.14159/fN)*sL;
        vR[2] = vR[1] + sL; 
        vR[3] = vR[0] + sL;

        // Edging out quad by quad in a circular fashion.
        for(int j = 0; j<N - 1; j++){

            // Quad value.
            float qud = quad(q, vR);

            // The combined quads, or the whole object.
            obj = min(obj, qud);
            if(qud<polyMin){

               // Update the minimum quad distance.
               polyMin = qud;

               // Quad ID.
               id = vec2(i, j);

               // Hit vertices.
               pV = vR;

            }


            // Drawing the helper grid lines.
            //if(j%2==0) line = min(line, abs(length(q) - length(vR[2])));
            vec2  nrm = normalize(vR[2] - vR[3]);
            if(j%2==0) line = min(line, sdLine(q, vR[2] - nrm, vR[3] + nrm));
            //nrm = normalize(vR[0] - vR[1]);
            //if(j%4==2) line = min(line, sdLine(q, vR[0] - nrm, vR[1] + nrm)); 

            // Offset edges, if chosen.
            #ifdef OFFSET_EDGES
            // Offset polygon edges.
            for(int l = 0; l<3; l++){
                for(int k = 0; k<4; k++){
                    float a = 1./float(l + 1);
                    vec2 pk = pV[k];
                    vec2 pk1 = pV[(k + 1)%4];

                    vec2 offs1 = (vec2(hash21(pk + .45*a), hash21(pk + .73*a)) - .5);
                    vec2 offs2 = (vec2(hash21(pk1 + .29*a), hash21(pk1 + .87*a)) - .5);
                    vec2 pA = pk - offs1*.0125;
                    vec2 pB = pk1 - offs2*.0125;
                    vec2 nm = normalize(pB - pA);
                    edge = min(edge, sdLine(q, pA - nm*.01, pB + nm*.01));

                }
            }
            #endif

            // Produce the vertices for the next quad in the strip. As you can see, 
            // each quad starts with the last vertices from the prevous quad, and the 
            // new end vertices are created by adding a rotated side edge. 
            vR[0] = vR[3];
            vR[1] = vR[2];
            vR[2] = vR[1] + rot2(-3.14159/fN*float(j+1))*sL;
            vR[3] = vR[0] + rot2(-3.14159/fN*float(j+1))*sL;            

        }

        // Rotate the side length vector to the right position for the next inner vertex.
        sL = rot2(-6.283/fN)*sL; 

        // Fake break to counter unrolling and slow compile times.
        if(length(sL)>1e5) break;

    }




    // Coloring and shading, based on polygon ID (side band number, or position within the band).
    //float hlfN = float((N - 1)/2);
    //float fi = float(N/2) + 1. - id.x;
    //float sh = mod(fi, float(N/2))/float(hlfN)*.5 + .5;

    float rt = 0.;
    #ifdef ROTATE
    rt = iTime/8.;
    #endif
    float sh = sin((float(N) - id.x)*6.2831/fN*1.3 - rt)*.25 + .75;
    sh *= id.y/(fN - 2.)*.5 + .5;

    // Polygon color.
    vec3 polyCol = (.55 + .45*cos(-6.2831*sh/3.5 + vec3(0, 1, 2) + 2.2))*1.5;//vec3(sh, sh*sh, sh*sh*sh)*1.5;
    //vec3 polyCol = col*vec3(sh*.9 + .1)*1.35;
    //polyCol = mix(polyCol.zyx, polyCol.yxz, .85);
    polyCol = mix(polyCol, polyCol.yzx, .05);
    //polyCol = mix(polyCol.yxz, polyCol.zyx, .95);


    /*
    // Holes.
    polyMin = abs(polyMin + ew*6.) - ew*6.;
    polyMin = abs(polyMin + ew*6.) - ew*6.;
    obj = max(obj, polyMin);
    */


    // Object drop shadow and outside edges.
    col = mix(col, vec3(0), (1. - smoothstep(0., sf*24.*iRes/450., obj))*.35);
    col = mix(col, polyCol/32., (1. - smoothstep(0., sf, obj - ew*3.))*.85);




    #ifdef OFFSET_EDGES
    col = mix(col, polyCol, (1. - smoothstep(0., sf, polyMin)));
    #else
    col = mix(col, polyCol/3., (1. - smoothstep(0., sf, polyMin)));
    col = mix(col, polyCol, (1. - smoothstep(0., sf, polyMin + ew*2.)));
    /*
    // More decorated faces... I wasn't feeling it. :)
    col = mix(col, polyCol/8., (1. - smoothstep(0., sf, polyMin)));
    col = mix(col, mix(min(polyCol*2., 1.5), vec3(1), .35), (1. - smoothstep(0., sf, polyMin + ew*3.)));
    col = mix(col, vec3(0), (1. - smoothstep(0., sf, polyMin + ew*6.)));
    col = mix(col, polyCol, (1. - smoothstep(0., sf, polyMin + ew*8.)));
    */
    #endif

    #ifdef PANELS
    // Lit face windows.
    col = mix(col, polyCol*2., (1. - smoothstep(0., sf*12., polyMin + ew*28.)));
    col = mix(col, polyCol/2., (1. - smoothstep(0., sf, abs(polyMin + ew*12.) - ew)));
    col = mix(col, polyCol/2., (1. - smoothstep(0., sf, abs(polyMin + ew*22.) - ew)));
    #endif

    /*
    // Triangulate. Interesting, but a bit of depth is lost somehow.
    float line2 = 1e5;
    if(length(pV[0] - pV[2])<length(pV[1] - pV[3])) line2 = min(line2, sdLine(q, pV[0], pV[2]));
    else line2 = min(line2, sdLine(q, pV[1], pV[3]));
    line2 -= ew*.67;
    col = mix(col, polyCol/32., (1. - smoothstep(0., sf, line2)));
    */

    #ifdef VERTICES
    // Render vertex points.
    float vert = min(length(q - pV[0]), length(q - pV[1]));
    vert = min(vert, min(length(q - pV[2]), length(q - pV[3])));
    vert -= ew*5.;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf, vert)));
    col = mix(col, polyCol.xzy*.7, (1. - smoothstep(0., sf*2., vert + ew*4.)));
    #endif


    #ifdef FACE_CENTER
    // Face center points.
    vec2 cntr = (pV[0] + pV[1] + pV[2] + pV[3])/4.;
    float cVert = length(q - cntr) - ew*4.;
    col = mix(col, vec3(0), (1. - smoothstep(0., sf, cVert)));
    col = mix(col, polyCol.xzy*.7, (1. - smoothstep(0., sf*2., cVert + ew*3.)));
    #endif

    #ifndef ROTATE
    #ifndef SKETCH
    // Fake construction lines when the object isn't rotating.
    line -= ew;
    line = max(line, obj - .4);
    float alpha = mix(.15, .15, 1. - smoothstep(0., sf*2., obj));
    col = mix(col, polyCol/32., (1. - smoothstep(0., sf, line))*alpha);
    #endif
    #endif

    #ifdef OFFSET_EDGES
    col = mix(col, polyCol/3., 1. - smoothstep(0., sf, edge - ew/3.));
    #endif

    // Just a cheap way to seperate the foreground object coordinates from
    // those in the background.
    p = mix(p, q, 1. - smoothstep(0., sf*2., obj));


    // Subtle pencil overlay... It's cheap and definitely not production worthy,
    // but it works well enough for the purpose of the example. The idea is based
    // off of one of Flockaroo's examples.
    vec2 qq = p*8.;//*gSc;
    //vec2 qq = p*8.;
    #ifdef SKETCH
    int drkLns = 1;
    #else
    int drkLns = 0;
    #endif
    vec3 colP = pencil(col, qq*iRes/450., drkLns);
    #ifdef SKETCH
    // Just the pencil sketch. The last factor ranges from zero to one and 
    // determines the sketchiness of the rendering... Pun intended. :D
    col = colP;
    //col = mix(dot(col, vec3(.299, .587, .114))*vec3(1), colP, .6);
    #else
    col = mix(col, 1. - exp(-(col*2.)*(colP + .15)), .85); 
    #endif
    //col = mix(col, colP, .85);
    //col = mix(min(col, colP), max(col, colP), .35); 



    // Cheap paper grain... Also barely worth the effort. :)
    vec2 pp = p;//floor(q*1024.);
    vec3 rn3 = vec3(hash21(pp), hash21(pp + 2.37), hash21(pp + 4.83));
    vec3 pg = .9 + (rn3.xyz*.35 + rn3.xxx*.65)*.2;
    col *= min(pg, 1.); 




    // Rough gamma correction and output to screen.
    fragColor = vec4(sqrt(max(col, 0.)), 1);
}

