float line( in vec2 p, in vec2 a, in vec2 b ){
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 f, in vec2 u ){
    vec2 uv = u / R;
    vec2 uv2 = vec2(u.xy - 0.5*R.xy)/R.y;

    vec3 col = vec3(0);

    uv += iTime*.031;

    vec2 id = floor(uv*110.)*.03;
    vec2 id2 = floor(uv*20.);

    vec3 tex = texture(iChannel0, id).xyz;

    col = tex;

    if(cos(id2.x*10. + id2.y*8. + iTime) > 0.)
        col = mix(col.rrr, col, .5+.5*cos(length(uv)*4. - iTime));

    col *= (1.-step(.44, abs(uv2.y)));
    f = vec4(col, 1.0);
}

