#define R iResolution.xy

// Constants
const float pi = 3.1415;
const float BPM = 175.0;


const float pitchWheel = 4.0;

const float swing = -0.4;


// noise
vec2 hash22(vec2 p){
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

// Metronome sound
float blip(float t, float hz, float k){
    return (1. - exp(-t*40.)) * sin(pi*2.*hz*t) * exp(-t * k);
}



// A beat
float b(float t){
    return mod(t, 60. / BPM);
}



// Various durations of time
float bar_l = 240. / BPM; // Bar
float beat_l = 60. / BPM; // beat
float half_l = 30. / BPM; // half beat
float quarter_l = 15. / BPM; // quarter beat


// Get the correct time for a note based on its time offset
// q = note start time (bar, beat, half beat, quarter beat)
// nBeats = number of beats in pattern
// t = time
float noteTime(vec4 q, float nBeats, float t){
    float start = t - (bar_l*q.x + beat_l*q.y + half_l*q.z + quarter_l*q.w);
    return mod(max(start, 0.), nBeats*60. / BPM);
}


// Loop a pattern
// q = pattern start time (bar, beat, half beat, quarter beat)
// loop = number of times to loop pattern
// nb = number of beats in pattern
// t = time
float seq(vec4 q, float loop, float nb, float t){
    float start = (bar_l*q.x + beat_l*q.y + half_l*q.z + quarter_l*q.w);
    float dur = nb*loop*60. / BPM;

    if(t > start && t < start + dur)
        return t - start;
    else
        return 0.;
}




// Distortions
float dist1(float s, float d){
 	return clamp(d * s, -1., 1.);   
}
vec2 dist2(vec2 s, float d){
 	return clamp(d * s, vec2(-1.0), vec2(1.0));   
}



// Attack and decay from exponential and sqrt funcs
float env(float t, float atk, float rel){
    return (1. - exp(-t*atk)) * exp(-t*rel);
}

// Linear envelope thing
// t = time
// p = (attack, sustain, release)
float envelope(float t, vec3 p){
    float a = t / p.x;
    float b = (p.y + p.x + p.z - t);
    return max(min(min(a, 1.), b), 0.);
}



// Note stuff
const float baseFreq = 110.0; // A1
const float d12thRootOf2 = pow(2.0, 1.0 / 12.0);

// Return the note's frequency
float n(float k){
    return baseFreq * pow(d12thRootOf2, k + pitchWheel);
}



// Waveforms
float sine(float t, float hz){
    return sin(hz*t*pi*2.);
}

float triangle(float t, float hz){
    return (2. / pi) * asin(sin(2.*pi*t*hz));
}

float tri(float t, float hz){
    return (2. / pi) * asin(sin(2.*pi*t*hz));
}

float sqr(float t, float hz){
    return sign(sin(2.*pi*t*hz)) * 0.5;
}

float saw(float t, float hz){
    return 2.*mod(pi*.5*(hz*t) / pi, 1.) - 1.;
}

// Stereo versions
vec2 sqr_2(float t, float nt, float sep){
    return vec2(sqr(t, n(nt)), sqr(t, n(nt) + sep));
}

vec2 tri_2(float t, float nt, float sep){
    return vec2(tri(t, n(nt)), tri(t, n(nt) + sep));
}

vec2 sine_2(float t, float nt, float sep){
    return vec2(sine(t, n(nt)), sine(t, n(nt) + sep));
}

vec2 saw_2(float t, float nt, float sep){
    return vec2(saw(t, n(nt)), saw(t, n(nt) + sep));
}




// Chords
vec2 minor_tri(float t, float nt, float sep){
    vec2 ms = vec2(0);

    ms += .5*tri_2(t, nt - 12., sep);

    ms += tri_2(t, nt, sep);
    ms += tri_2(t, nt + 3., sep);
    ms += tri_2(t, nt + 7., sep);

    return ms / 4.;
}

vec2 major_tri(float t, float nt, float sep){
    vec2 ms = vec2(0);

    ms += .1*tri_2(t, nt - 12., sep);

    ms += tri_2(t, nt, sep);
    ms += tri_2(t, nt + 4., sep);
    ms += tri_2(t, nt + 7., sep);

    return ms / 4.;
}








// Synths


// Major triad thingy
vec2 synth_2(float k, float dur, vec4 q, float nBeats, float t){
    vec2 ms = vec2(0);
    float t0 = noteTime(q, nBeats, t);

    ms += major_tri(t0, k, 0.75);

    return ms * smoothstep(0.0, .05, t0) * smoothstep(2.0, .0, t0);

}


// lead saw / triangle thingy
vec2 synth_5(float k, float dur, vec4 q, float nBeats, float t){
    vec2 ms = vec2(0);
    dur = (dur * 60.) / BPM;

    float t0 = noteTime(q, nBeats, t);

    ms += tri_2(t0, k, 6.);
    ms += .2*saw_2(t0, k + 12., 6.);

    return ms * smoothstep(0.0, .1, t0) * smoothstep(dur + .25, dur, t0);

}

// Saw lead 
vec2 synth_6(float k, float dur,float oct, vec4 q, float nBeats, float t){
    vec2 ms = vec2(0);
    dur = (dur * 60.) / BPM;

    float t0 = noteTime(q, nBeats, t);

    ms += saw_2(t0, k + 24., 2.);
    //ms += .35*saw_2(t0, k + 12., 3.);

    //return ms * smoothstep(0.0, .1, t0) * smoothstep(1.8, 0., t0);
    return ms * smoothstep(0.0, .2, t0) * smoothstep(dur + .25, dur, t0);

}

// Subby bass 
vec2 synth_3(float k, float dur, vec4 q, float nBeats, float t){
    vec2 ms = vec2(0);
    dur = (dur * 60.) / BPM;

    float t0 = noteTime(q, nBeats, t);

    ms += .2*tri_2(t0, k, 0.4);
    ms += sine_2(t0, k - 12., 0.);

    return ms * smoothstep(0.0, .01, t0) * smoothstep(dur + .01, dur, t0);
    //return ms * smoothstep(0.0, .4, t0) * smoothstep(2.2, .0, t0);
}




// Drums
vec2 snare(vec4 q, float nBeats, float t){
    float t0 = noteTime(q, nBeats, t);

    vec2 s = exp(-t0*16.) * hash22(iSampleRate * vec2(t0, t0*1.423)) * 2.0 - 1.0; 
    s = dist2(s, 1.13) * .85;
    s += 1.3*dist1(exp(-t0*24.) * sine(t0, 200.), 4.0);
    s += .1*dist1(exp(-t0*24.) * sine(t0, 150.), 3.0);
    return s * 0.25;
}


vec2 kick(vec4 q, float nBeats, float t){
    float t0 = noteTime(q, nBeats, t);

    vec2 s = vec2(0);
    s += 1.5*dist2(exp(-t0*22.) * vec2(sine(t0, 72.)), 4.7);
    s += 4. * exp(-t0*15.) * vec2(sine(t0, 70.));
    return s * .15;
}

vec2 hat(vec4 q, float nBeats, float t){
    float t0 = noteTime(q, nBeats, t);

    vec2 s = envelope(t0, vec3(.1, .01, .01)) * hash22(iSampleRate * vec2(t, t*1.423)) * 2.0 - 1.0; 

    //s = dist2(s, 1.1) * .85;
    return s * 0.4;
}

