vec2 snd(float t){
    vec2 ms = vec2(0);

    ms += .3*pattern_chords_1(seq(vec4(0., 0., 0., 0.), 32., 16., t), 16.);

    ms += .35*pattern_bass_1(seq(vec4(4., 0., 0., 0.), 32., 16., t), 16.);

