    ms += .7*pattern_drums_kick(seq(vec4(4., 0., 0., 0.), 32., 16., t), 8.);
    ms += .5*pattern_drums_snare(seq(vec4(4., 0., 0., 0.), 32., 16., t), 8.);

    return ms * .5;
}

