float beckmann_D2(vec3 m, vec3 n, float a){
float cost = max(dot(n,m),0.);
float a1 = 1./(3.14159*a*a*pow(cost,4.));
float b1 = exp((cost*cost - 1.)/(a*a*cost*cost));
return max(a1*b1,0.001);
}

float beckmann_pdf2(vec3 m, vec3 n, float a){
float cost = max(dot(n,m),0.);
float a1 = 1./(3.14159*a*a*pow(cost,3.));
float b1 = exp((cost*cost - 1.)/(a*a*cost*cost));
return max(a1*b1,0.0001);
}

vec3 beckmann_S(vec3 n, inout uint r, float a){
        float r1 = rndf(r);
        float r2 = rndf(r);

        float theta = atan(sqrt(-a*a*log(1.-r1)));
        //float theta = acos(sqrt((1.-r1)/(r1*(a*a-1.)+1.) ));
        float phi = 2.*3.14159*r2;

        float x = cos(phi)*sin(theta);
        float y = sin(phi)*sin(theta);
        float z = cos(theta); 

        vec3 W = (abs(n.x)>0.99)?vec3(0.,1.,0.):vec3(1.,0.,0.);
        vec3 N = n;
        vec3 T = normalize(cross(N,W));
        vec3 B = cross(T,N);
        return normalize(x*T + y*B + z*N);
}

float beckmann_pdf(vec3 m, vec3 n, float a){
float angle = acos(max(dot(m,n),0.));
float a1 = 1./(3.14159*a*a*pow(max(dot(m,n),0.),3.)); // should be the pdf over a solid angle
float b1 = exp(-((tan(angle)*tan(angle))/(a*a)));
return max(a1*b1,0.0001);
}

float ggx_G(vec3 h, vec3 n, vec3 wi, vec3 l, float a){
float g1 = (2.*max(dot(n,h),0.)*max(dot(n,-wi),0.))/max(dot(-wi,h),0.);
float g2 = (2.*max(dot(n,h),0.)*max(dot(n,l),0.))/max(dot(-wi,h),0.);
float G = min(1.,min(g1,g2));
return G;
}

float ggx_G2(vec3 h, vec3 n, vec3 wi, vec3 l, float a){
float top = 2.*max(dot(n,-wi),0.);
float bottom = max(dot(n,-wi),0.)+sqrt(a*a + (1.-a*a)*pow(max(dot(n,-wi),0.),2.));
return top/bottom;
}

vec3 ggx_F(vec3 Fo, float cost){
return Fo + (1.-Fo)*pow(1.-cost,5.);
}

vec3 sampleSun(vec3 n, inout uint r, float theta){
        float diff = 1.-cos(theta);
        float z = cos(theta) + rndf(r)*diff;
        float angle = rndf(r)*3.14159*2.;
        float radius = sqrt(1. - z*z);
        float x = cos(angle) * radius;
        float y = sin(angle) * radius;

        vec3 W = (abs(n.x)>0.99)?vec3(0.,1.,0.):vec3(1.,0.,0.);
        vec3 N = n;
        vec3 T = normalize(cross(N,W));
        vec3 B = cross(T,N);
        return normalize(x*T + y*B + z*N);
}
vec3 lerp(vec3 a, vec3 b, float t){
return a + (b-a)*t;
}
float lerp(float a, float b, float t){
return a + (b-a)*t;
