        //sidey.xz = rot(sidey.xz,-20.);
        sidey.yz = rot(sidey.yz,rota);
        sidey.xy = rot(sidey.xy,rotb);

        float ang = rndf(r)*2.0*3.14159;
        float dist = min(length((vec2(0.0,-0.4)-uv)*0.2),1.);
        float scale = sqrt(rndf(r))*radius;
       //vec3 d = normalize(vec3(uv.x, 1.0, uv.y));
       //vec2 aa = normalize(vec2(0.,1.));
        vec2 offset = vec2(cos(ang), sin(ang))*scale;
        //if(offset.x > offset.y)offset.xy=offset.yx;
        //offset.x+=-1.;

       //offset*=radius;
       //NOT MY CODE////////////////////////
        vec3 focuspoint = p + ((d*focaldist) / dot(d,camdirection)); //these will lie on the focal plane
        /////////////////////////////////////
       // vec3 dd = vec3(uv.x,1.,uv.y);
       // dd.yz = rot(dd.yz, rota);
       // dd.xy = rot(dd.xy, rotb);
       //vec3 focuspoint = p+(dd*focaldist);
        //p.xz += offset;

        p = p + sidex*offset.x;
        p = p + sidey*offset.y;

        d = normalize(focuspoint - p);


