float GetNoise(vec2 uv){
    return fract(sin(uv.x+1000.0*uv.y+iTime*20.0)*10000.0)*noisePercent;    
}

float Get(float pixelPosition){
    return(mod(pixelPosition,GradientColorQuantity)/GradientColorQuantity);
}

float GetR(float pixelPosition)
{
    return Get(pixelPosition + GradientColorQuantity*1./3.);
}

float GetG(float pixelPosition)
{
    return Get(pixelPosition + GradientColorQuantity*2./3.);
}

float GetB(float pixelPosition)
{
    return Get(pixelPosition + GradientColorQuantity*3./3.);
}

vec3 GetNearstColorPalette(vec3 currentColor,vec3 colorPalette[16]){
    float nearstDistance = 10000.0;
    vec3 nearstColor;
    for(int i ; i<colorPalette.length() ; i++){
       vec3 newColor = colorPalette[i];
       float newDistance = distance(currentColor,newColor);
       if(newDistance<=nearstDistance){
           nearstColor = newColor;
           nearstDistance = newDistance; 
       } 
    }
    return nearstColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //default uv and aspectRatio
    vec2 uv = fragCoord/iResolution.xy;
    float aspectRatio = iResolution.x/iResolution.y;


    //grayscale gradient
    float x = Get(fragCoord.x);
    float y = Get(fragCoord.y);
    //float value = (x+y)/2.0;  // simple average
    //float value = x*y; // subtrative
    //float value = x+y; // aditive
    float value = ((x+y)/2.0+(x*y)/2.0);
    fragColor = vec4(value);


    //pixel color sample
    vec3 imageColor = texture(iChannel0,uv).rgb;    

    vec3 paletteColor = GetNearstColorPalette(imageColor,BeforeColorPalette);
    //calculate a gradient x and y
    vec3 stepEgdeX = vec3(GetR(fragCoord.x),GetG(fragCoord.x),GetB(fragCoord.x));
    vec3 stepEgdeY = vec3(GetR(fragCoord.y),GetG(fragCoord.y),GetB(fragCoord.y));

    // mix both gradiant 
    //vec3 valueEdge = (stepEgdeX + stepEgdeY)/2.0; // simple average
    //vec3 valueEdge = (stepEgdeX * stepEgdeY); // subtrative
    //vec3 valueEdge = (stepEgdeX + stepEgdeY); // aditive

    vec3 valueEdge = ((stepEgdeX + stepEgdeY) + (stepEgdeX * stepEgdeY))/2.; // simple average of (subtractive and aditive)

    // dithering on the gradient
    vec3 newColor = step(valueEdge+GetNoise(uv),paletteColor); 


    vec3 afterColor = GetNearstColorPalette(newColor,AfterColorPalette); 

    fragColor = vec4(afterColor,1.0); // debug after palette effect

    //fragColor = vec4(newColor,1.0);      // debug the dithering

    //fragColor = vec4(imageColor,1.0);  // debug the image

    //fragColor = vec4(valueEdge,1.0);   // debug the gradient 
}

int   L = 10;  // LxL point distribution. left: Uniform right: Blue
float r = 1.;  // kernel radius 

#define hash(i)    fract(sin( iMouse.xy + float(i) * vec2(12.9898, 78.233) ) * 43758.5453)// hash 1→2
#define Pu(i)    ( hash(i) * vec2(-a,a) - vec2(0,1) )                   // uniform distrib
#define Pb(i)    ( vec2(i%L,i/L)/l  * a - vec2(0,1) + hash(i)/l )       // ~blue distrib 
#define Ks(P)      smoothstep(2.*r/l, 0., length(P-U) ) / (r*r)           // splat kernel
#define Kc(P)      smoothstep(3./R.y, 0., abs(length(P-U)-1./l) )         // circle kernel 
//#define Kc(P)    smoothstep(3./R.y, 0., abs(mod(length(P-U),.4/l)-.2/l) )*.02/length(P-U) // sin kernel  
#define  K(P)    ( mod(iTime,4.) < 2. ? Ks(P) : Kc(P) )                 // kernel

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y;
    float l = float(L), a = R.x/R.y; 

    O = vec4(0);
    if (U.x<0.)
        for(int i=0; i<L*L; i++)
            O += K(Pu(i));
    else
        for(int i=0; i<L*L; i++)
            O += K(Pb(i));

    O*=.5;    // to better see superimposition
}

