float hash21(vec2 p){ return fract(sin(dot(p,vec2(26.34,45.32)))*4324.23); }
mat2 rot(float a){ return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float vmax(vec3 p){ return max(max(p.x,p.y),p.z); }
float lsp(float begin, float end, float t) { return clamp((t - begin) / (end - begin), 0.0, 1.0); }

float box(vec3 p, vec3 b, in vec4 r )
{   r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
	vec3 d = abs(p) - b+vec3(r.x,0,0);
	return length(max(d,vec3(0))) + vmax(min(d,vec3(0)));
}
float box( in vec2 p, in vec2 b ){
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}
float box( in vec2 p, in vec2 b, in vec4 r ){
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}
mat2 r22;
// Letters from 2D to 3D extruded SDF's
float getP(vec2 uv)
{
    float letp = box(uv-vec2(0,.1),vec2(.15),vec4(.15,.15,.00,0));
    letp=abs(letp)-.05;
    letp=min(box(uv+vec2(.15, .0),vec2(.05,.3)),letp);
    return letp;
}
float getR(vec2 uv)
{
    float letr = box(uv-vec2(0,.1),vec2(.15),vec4(.15,.15,.00,0));
    letr=abs(letr)-.05;
    letr=min(box(uv+vec2(.15,.0),vec2(.05,.3)),letr);
    vec2 uu = uv+vec2(-.1, .175);
    uu.xy*=r22;
    letr=min(box(uu,vec2(.05,.175)),letr);
    letr=max(letr, -box(uv+vec2(-.1, .37),vec2(.17,.06)) ); 
    return letr;
}
float getI(vec2 uv)
{
    uv.y=abs(uv.y);
    float leti = box(uv,vec2(.05,.3));
    leti = min(box(uv-vec2(.0, .25),vec2(.20,.05)),leti);
    return leti;
}
float getD(vec2 uv)
{
    float letd = box(uv,vec2(.145,.25),vec4(.125,.125,.00,0));
    letd=abs(letd)-.05;
    letd=min(box(uv+vec2(.145, .0),vec2(.05,.3)),letd);
    return letd;
}
float getE(vec2 uv)
{
    uv.y=abs(uv.y);
    float lete = box(uv+vec2(.15,0),vec2(.05,.3));
    lete = min(box(uv+vec2(.05, .0),vec2(.125,.05)),lete);
    lete = min(box(uv-vec2(.02, .25),vec2(.175,.05)),lete);
    return lete;
}
float getO(vec2 uv)
{
    float leto = box(uv,vec2(.145,.25),vec4(.125));
    leto=abs(leto)-.05;
    return leto;
}
float getQ(vec2 uv)
{
    float letq = box(uv,vec2(.145,.25),vec4(.125));
    letq=abs(letq)-.05;
    vec2 uu = uv+vec2(-.1, .175);
    uu.xy*=r22;
    letq=min(box(uu,vec2(.05,.175)),letq);
    letq=max(letq, -box(uv+vec2(-.1, .37),vec2(.17,.06)) ); 
    return letq;
}
float getU(vec2 uv)
{
    float letu = box(uv-vec2(0,.1),vec2(.145,.35),vec4(0,.125,.00,.125));
    letu=abs(letu)-.05;
    letu=min(box(uv-vec2(.145, .0),vec2(.05,.3)),letu);
    letu = max(letu, -box(uv-vec2(.0, .8),vec2(.5,.5)) );
    return letu;
}
float opx(in float sdf, in float pz, in float h){
    vec2 w = vec2( sdf, abs(pz) - h );
  	return min(max(w.x, w.y), 0.) + length(max(w, 0.));
}

vec3 hitPoint,hit;
float tmod=0.,time=0.,ga1=0.,ga2=0.,ga3=0.,ga4=0.,ga5=0.,ga6=0.;
const float sz = 3.;
const float hf = sz*.5;

vec2 map (vec3 pos) {
	vec2 res = vec2(100.,-1.);

    vec3 q = pos;
    vec3 p = pos;

    float pr = 1e5;
    float pd = 1e5;
    for(int i=0;i<5;i++)
    {

        vec3 p2 = p+vec3(0,.3,0);
        vec3 p1 = p-vec3(0,.8,0);

        mat2 g1 = rot(ga2*PI2);
        mat2 g2 = rot(ga1*PI2);
        mat2 g0 = rot(ga3*PI2);
        mat2 g4 = rot(ga4*PI2);

        float oft = .2*sin(T+float(i)+ga3*PI);
        p1.xz*=g1;
        p1.yz*=g2;

        p2.xz*=g1;
        p2.yz*=g2;

        float toutline,loutline,tz,tx;
        if(i==0) { 
        vec3 tmq = p1+vec3(1.2,oft,0);
        vec3 tmx = p2+vec3(1.2,-oft,0);
        tmq.xz*=g4;
        tmx.xz*=g0;
        tx=tmq.z;
        tz=tmx.z;
        loutline=getQ(tmq.xy); 
        toutline=getP(tmx.xy); 
        }
        if(i==1) {
        vec3 tmq = p1+vec3(.6,oft,0);
        vec3 tmx = p2+vec3(.6,-oft,0);
        tmq.xz*=g4;
        tmx.xz*=g0;
        tx=tmq.z;
        tz=tmx.z;
        loutline=getU(tmq.xy);
        toutline=getR(tmx.xy);
        }
        if(i==2) {
        vec3 tmq = p1-vec3(.0,-oft,0);
        vec3 tmx = p2-vec3(.0,oft,0);
        tmq.xz*=g4;
        tmx.xz*=g0;
        tx=tmq.z;
        tz=tmx.z;
        loutline=getE(tmq.xy);
        toutline=mix(getO(tmx.xy),getI(tmx.xy),ga5);
        }
        if(i==3) {
        vec3 tmq = p1-vec3(.6,-oft,0);
        vec3 tmx = p2-vec3(.6,oft,0);
        tmq.xz*=g4;
        tmx.xz*=g0;
        tx=tmq.z;
        tz=tmx.z;
        loutline=getE(tmq.xy);
        toutline=mix(getU(tmx.xy),getD(tmx.xy),ga5);
        }
        if(i==4) {
        vec3 tmq = p1-vec3(1.2,-oft,0);
        vec3 tmx = p2-vec3(1.2,oft,0);
        tmq.xz*=g4;
        tmx.xz*=g0;
        tx=tmq.z;
        tz=tmx.z;
        loutline=getR(tmq.xy);
        toutline=mix(getD(tmx.xy),getE(tmx.xy),ga5);
        }

        pr = min(opx(abs(toutline)-.015,tz,.075),pr);
        pr = min(opx(abs(loutline)-.015,tx,.075),pr);

    }

    if(pr<res.x){
        res = vec2(pr,2.);
    	hit=p;
    } 

    float wall = q.z+1.75;
    if(wall<res.x) {
        hit=vec3(1.-q.x,q.yz);
        res = vec2(wall,4.);
    }
    return res;
}

// Tetrahedron technique @Shane
vec3 normal(vec3 p, float t)
{
    const vec2 h = vec2(1.,-1.)*.5773;
    vec3 n = vec3(0);
    vec3[4] e4 = vec3[4](h.xyy, h.yyx, h.yxy, h.xxx);

    for(int i = min(0, iFrame); i<4; i++){
	    n += e4[i]*map(p + e4[i]*t*MIN_DIST).x;
        if(n.x>1e8) break; // Fake non-existing conditional break.
    }
    return normalize(n);
}

vec2 marcher(vec3 ro, vec3 rd, int maxsteps, float sg){
	float d = 0.;
    float m = 0.;
    for(int i=0;i<maxsteps;i++){
    	vec2 ray = map(ro + rd * d);
        if(ray.x<MIN_DIST*d||d>MAX_DIST) break;
        d += ray.x*.9;
        m  = ray.y;
    }
	return vec2(d,m);
}

//@iq of hsv2rgb
vec3 hsv2rgb( in vec3 c ) {
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(1.0), rgb, c.y);
}

