        {
            float h = map(p + l*t).x;
            if( h<MIN_DIST ) { shdw = 0.; break; }
            shdw = min(shdw, 16.*h/t);
            t += h;
            if( shdw<MIN_DIST || t>32. ) break;
        }
        ga5=1.-ga5;
        diff = mix(diff,diff*shdw,.75);

        vec3 view = normalize(p - ro);
        vec3 ret = reflect(normalize(lpos), n);
        float spec =  0.5 * pow(max(dot(view, ret), 0.), (m==2.||m==4.)?24.:64.);

        // materials and reflections
        if(m==2.) {
            vec3 mp = hitPoint*.5;
            vec3 clr = hsv2rgb(vec3(hitPoint.x*.175,1.,.5));
            C+=spec+(vec3(.9)*diff);
            C=mix(C,hsv2rgb(vec3(fresnel,1.,.5)),fresnel);
            ref = clr-fresnel;
        }

        if(m==4.) {

            hitPoint.x-=T*1.5;
            hitPoint.y+=.4*sin(T*1.4+hitPoint.x*1.3);
            vec2 uv =hitPoint.xy*.5;    
            float px = fwidth(uv.x);
            vec2 grid_uv = fract(uv)-.5;
            vec2 grid_id = floor(uv);

            float chk = mod(grid_id.y + grid_id.x,2.) * 2. - 1.;
            float hs = hash21(grid_id);
            if(hs>.5) grid_uv.x*=-1.;

            vec2 d2 = vec2(length(grid_uv-.5), length(grid_uv+.5));
            vec2 gx = d2.x<d2.y? vec2(grid_uv-.5) : vec2(grid_uv+.5);

            float circle = length(gx)-.5;
            float circle2 =abs(abs(circle)-.05)-.025;
            circle2=smoothstep(-px,.001+px,circle2);
            circle=(chk>0.^^ hs>.5)?smoothstep(-px,.001+px,circle):smoothstep(.001+px,-px,circle);
            circle= min(circle2,circle);
            vec3 fl = hsv2rgb(vec3(.09+hitPoint.y*.16,1.,.5));
            h =mix(vec3(.9),fl, circle);
            ref = vec3(1.-circle)-fresnel;
            C+=spec+((h*diff)*.4);

        }

        ro = p+n*.01;
        rd = reflect(rd,n);
        ga5=1.-ga5;
    } 

    return vec4(C,alpha);
}

void mainImage( out vec4 O, in vec2 F )
{   

    r22=rot(0.38397243543);
    time = T;
    tmod = mod(time, 14.);
    float t1 = lsp(2.0, 4.0, tmod);
    float t2 = lsp(6.0, 10.0, tmod);

    float t3 = lsp(6.0, 8.0, tmod);
    float t4 = lsp(8.0, 10.0, tmod);

    float t7 = lsp(0.6, 4.0, tmod);
    float t8 = lsp(10., 12., tmod);

    float t9 = lsp(3.0, 4.0, tmod);
    float t0 = lsp(8.0, 9.0, tmod);

    ga1 = (t1-t2);
    ga2 = (t3-t4);
    ga3 = (t1-t3);
    ga4 = (t7-t8);
    ga5 = (t9-t0);

    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec3 ro = vec3(0,.25,2.25);
    vec3 rd = normalize(vec3(uv,-1));

    mat2 ry = rot(.15*sin(time*.03));
    ro.xz *= ry;
    rd.xz *= ry;

    vec3 C = vec3(0);
    vec3 ref=vec3(0), fil=vec3(1);
    float d =0.;
    float numBounces = 2.;
    for(float i=0.; i<numBounces; i++) {
        vec4 pass = render(ro, rd, ref, i==numBounces-1., d, uv);
        C += pass.rgb*fil;
        fil*=ref;
    }
    // gamma
    C = pow(C, vec3(.4545));
    O = vec4(C,1.0);
}

// "Flaming Asteroids" by dr2 - 2021
