// Not sure about the scientific accuracy... (flame idea from nimitz's "Re-entry")

float PrSphDf (vec3 p, float r);
float VPoly (vec3 p);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Maxv3 (vec3 p);
float BumpFbm3 (vec3 p);
float Hashfv3 (vec3 p);
vec3 Hashv3f (float p);
vec3 Hashv3v3 (vec3 p);
float Noisefv3 (vec3 p);
float Fbm2 (vec2 p);
float Fbm2e (vec2 p);
float Fbm3 (vec3 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec4 astCs;
vec3 astPos, aRand, sunDir;
float dstFar, tCur, flRad, flLen, grndScl;
const float pi = 3.1415927;

#define VAR_ZERO min (iFrame, 0)

vec3 ObjRot (vec3 p)
{
  p.xy = Rot2Cs (p.xy, astCs.xy);
  p.zy = Rot2Cs (p.zy, astCs.zw);
  return p;
}

vec3 ObjRotT (vec3 p)
{
  p.zy = Rot2Cs (p.zy, astCs.zw * vec2 (1., -1.));
  p.xy = Rot2Cs (p.xy, astCs.xy * vec2 (1., -1.));
  return p;
}

float ObjDf (vec3 p)
{ // (from "Dynamic Space Rocks")
  float d;
  p -= astPos;
  d = PrSphDf (p, 0.8);
  if (d < 0.2) {
    p = ObjRot (p);
    d -= 0.15 * VPoly ((2. + 0.3 * aRand.z) * p);
  }
  return 0.7 * d;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec2 CapsHit (vec3 ro, vec3 rd, float cRad, float cLen)
{
  float dIn, dOut, d, a, b, bs, w, s, ws, roz;
  dIn = dstFar;
  dOut = dstFar;
  a = dot (rd.xy, rd.xy);
  b = dot (rd.xy, ro.xy);
  w = b * b - a * (dot (ro.xy, ro.xy) - cRad * cRad);
  if (w > 0. && a > 0.) {
    ws = sqrt (w);
    d = - (b + ws) / a;
    s = ro.z + d * rd.z;
    if (d > 0. && abs (s) < cLen) dIn = d;
    else {
      roz = ro.z;
      ro.z -= cLen * sign (s);
      bs = dot (rd, ro);
      w = bs * bs - (dot (ro, ro) - cRad * cRad);
      if (w > 0.) {
        d = - bs - sqrt (w);
        if (d > 0.) dIn = d;
      }
      ro.z = roz;
    }
  }
  if (dIn < dstFar) {
    d = (- b + ws) / a;
    s = ro.z + d * rd.z;
    if (abs (s) < cLen) dOut = d;
    else {
      ro.z -= cLen * sign (s);
      bs = dot (rd, ro);
      w = bs * bs - (dot (ro, ro) - cRad * cRad);
      if (w > 0.) dOut = - bs + sqrt (w);
    }
  }
  return vec2 (dIn, dOut);
}

float FlmDens (vec3 p)
{ // (from "Energy Temple")
  float c;
  c = max (0., 1.3 * BumpFbm3 (0.2 * vec3 (p.xy / flRad, 0.35 * p.z + 2.5 * tCur)) - 0.3 +
     0.4 * smoothstep (-0.5 * flRad, 0.1 * flRad, p.z));
  return c * (1. - smoothstep (-0.3 * flRad, 0., length (vec3 (p.xy,
     p.z - clamp (p.z, - 2. * flLen, 0.))) - flRad * (1. + 0.8 * p.z / (2. * flLen)))) *
     smoothstep (0., flRad, p.z + 2. * flLen);
}

vec3 FlmCol (vec3 ro, vec3 rd, vec2 dCyl2, float dstObj)
{
  vec4 col4;
  vec3 q, c;
  float s, ds, f, fn, r;
  const float sMax = 50.;
  ds = min (2. * flRad, dCyl2.y - dCyl2.x) / sMax;
  s = dCyl2.x;
  ro.z -= flLen;
  q = ro + s * rd;
  r = length (q.xy) / flRad;
  q.xy = Rot2D (q.xy, 0.8 * r * (2. * smoothstep (0.4, 0.8, r) - 1.) * q.z);
  fn = FlmDens (q);
  col4 = vec4 (0.);
  for (float j = float (VAR_ZERO); j < sMax; j ++) {
    f = fn;
    s += ds;
    q = ro + (s + ds * Hashfv3 (16. * rd)) * rd;
    r = length (q.xy) / flRad;
    q.xy = Rot2D (q.xy, 0.8 * r * (2. * smoothstep (0.4, 0.8, r) - 1.) * q.z);
    fn = FlmDens (q);
    c = mix (mix (vec3 (1., 0.3, 0.1), vec3 (1., 1., 0.2), clamp ((0.3 / ds) * (f - fn), 0., 1.)),
       vec3 (0.9, 0.8, 0.3), smoothstep (-0.5 * flRad, 0.2 * flRad, q.z)) *
       (1. - smoothstep (-0.1, 0.1, s - dstObj));
    col4 = col4 + (1. - col4.a) * vec4 (c, 1.) * f / sMax;
    if (s > dstObj || col4.a > 1.) break;
  }
  return clamp (col4.rgb, 0., 1.);
}

float GrndDf (vec3 p)
{  // (from "Mars Copter")
  vec3 q;
  float d, h, a, r, s, f;
  q = p / grndScl;
  r = length (q.xz);
  d = p.y;
  if (r > 0.) {
    a = atan (q.z, - q.x) / (2. * pi) + 0.5;
    s = sqrt (r) / (2. * pi);
    f = 32.;
    h = 2.5 * s * mix (Fbm2e (f * vec2 (s, a + 1.)), Fbm2e (f * vec2 (s, a)), a);
    d = max (r - 20., q.y - mix (s, h, smoothstep (1.4, 2.2, r)) * smoothstep (1.4, 2.6, r));
  }
  return grndScl * d;
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = VAR_ZERO; j < 160; j ++) {
    p = ro + s * rd;
    h = GrndDf (p);
    if (h < 0.) break;
    sLo = s;
    s += max (0.001, h);
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = VAR_ZERO; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      if (GrndDf (p) > 0.) sLo = s;
      else sHi = s;
    }
    dHit = 0.5 * (sLo + sHi);
  }
  return dHit;
}

vec3 GrndNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = GrndDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 StarPat (vec3 rd, float scl) 
{ // (from "Star Globe")
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 8. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 ErCol (vec3 rd)
{ // (from "Moon Flight")
  vec3 erDir, col, vn;
  float erRad, bs, ts;
  erDir = normalize (vec3 (1., 0.1, 0.1));
  erRad = 0.03;
  col = vec3 (0.);
  bs = dot (rd, erDir);
  ts = bs * bs - 1. + erRad * erRad;
  if (ts > 0.) {
    ts = bs - sqrt (ts);
    if (ts > 0.) {
      vn = normalize ((ts * rd - erDir) / erRad);
      col = mix (vec3 (0.3, 0.4, 0.8), vec3 (1., 1., 0.95),
         smoothstep (0.2, 0.8, Fbm2 (6. * vn.yz + 7.1))) * (0.5 + 0.5 * max (- dot (vn, rd), 0.));
    }
  }
  return col;
}

vec3 GrndCol (vec3 ro, vec3 vn)
{
  vec3 col;
  col = vec3 (0.9, 0.7, 0.4) * (0.7 + 0.3 * Fbm2 (ro.xz));
  if (ro.y > -0.1) {
    col = mix (col, 0.8 * col, smoothstep (0.2, 0.7, 1. - vn.y));
    col = mix (col, 1.1 * col, smoothstep (0.6, 0.9, ro.y / grndScl +
       0.2 * sin (32. * atan (ro.z, - ro.x))));
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 roo, rdo, col, stCol, c, vn;
  vec2 dCyl2, vf;
  float dstObj, dstGrnd, tPos, tCyc, astDir;
  tCyc = 15.;
  tPos = mod (tCur, tCyc);
  roo = ro;
  rdo = rd;
  dstGrnd = GrndRay (ro, rd);
  if (dstGrnd < dstFar) {
    ro += dstGrnd * rd;
    col = GrndCol (ro, vn);
    vn = GrndNf (ro);
    vf = vec2 (0.5, 4. - 2. * smoothstep (0.7, 0.9, dstGrnd / (3. * grndScl)));
    if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
    col = col * (0.2 + 0.8 * max (dot (vn, sunDir), 0.));
    col *= 0.6 + 0.4 * smoothstep (1., 4., abs (abs (tPos - 0.5 * tCyc) - 0.5 * tCyc));
  } else {
    flRad = 1.2;
    flLen = 30.;
    aRand = Hashv3f (floor (tCur / tCyc) + 1.);
    astPos = vec3 (0., 15., 10. * (tPos - 0.5 * tCyc));
    astDir = (-0.35 + 0.7 * aRand.x + step (0.5, aRand.y)) * pi;
    astCs = vec4 (sin ((0.3 + 0.1 * aRand.x) * pi * tPos + vec2 (0.5 * pi, 0.)),
       sin ((0.2 + 0.1 * aRand.y) * pi * tPos + vec2 (0.5 * pi, 0.)));
    roo.xz = Rot2D (roo.xz, astDir);
    rd.xz = Rot2D (rd.xz, astDir);
    ro = roo;
    dstObj = ObjRay (ro, rd);
    if (dstObj < min (dstGrnd, dstFar)) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      vn = ObjRotT (VaryNf (16. * ObjRot (ro), vn, 8.));
      col = vec3 (1., 0.3, 0.) * (0.3 + 0.7 * abs (vn.z));
    } else col = vec3 (0.);
    ro = roo;
    ro -= astPos - vec3 (0., 0., flLen + 0.1 * flRad);
    dCyl2 = CapsHit (ro, rd, flRad, flLen);
    if (dCyl2.x < dstFar) {
      c = 3. * FlmCol (ro, rd, dCyl2, dstObj);
      if (dstObj < dstFar) col = mix (0.7 * col, c, step (0.5 * length (col), length (c)));
      else col += c;
      col *= smoothstep (0., 0.5, abs (abs (tPos - 0.5 * tCyc) - 0.5 * tCyc));
    }
    stCol = ErCol (rdo);
    if (length (stCol) < 0.03) stCol += StarPat (rdo, 12.);
    col = mix (col, stCol, step (Maxv3 (col), 0.4 * Maxv3 (stCol)));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.5 * pi;
  el = 0.05 * pi;
  if (mPtr.z > 0.) az += 0.4 * pi * mPtr.x;
  vuMat = StdVuMat (el, az);
  grndScl = 50.;
  ro = vuMat * vec3 (0., 0.2, -0.5) * grndScl;
  zmFac = 3.;
  dstFar = 10. * grndScl;
  rd = vuMat * normalize (vec3 (uv, zmFac));
  sunDir = normalize (vec3 (0., 1., -1.));
  col = ShowScene (ro, rd);
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float VPoly (vec3 p)
{
  vec3 ip, fp, a, g, w;
  ip = floor (p);
  fp = fract (p);
  a = vec3 (4.);
  for (float gz = -1.; gz <= 1.; gz ++) {
    for (float gy = -1.; gy <= 1.; gy ++) {
      for (float gx = -1.; gx <= 1.; gx ++) {
        g = vec3 (gx, gy, gz);
        w = g + 0.7 * Hashv3v3 (ip + g) - fp;
        a.z = dot (w, w);
        if (a.z < a.x) a.xy = a.zx;
        else a.y = min (a.z, a.y);
      }
    }
  }
  return sqrt (a.y - a.x);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float PerBumpf (float p)
{
  return 0.5 * smoothstep (0., 0.5, abs (fract (p) - 0.5));
}

vec3 PerBumpv3 (vec3 p)
{
  return 0.5 * smoothstep (0., 0.5, abs (fract (p) - 0.5));
}

float BumpFbm3 (vec3 p)
{ // (variation of triangular noise in "Misty Flight", adapted from nimitz's "Oblivion")
  vec3 q;
  float a, f;
  a = 1.;
  f = 0.;
  q = p;
  for (int j = 0; j < 4; j ++) {
    p += PerBumpv3 (q + PerBumpv3 (q).yzx);
    p *= 1.5;
    f += a * (PerBumpf (p.z + PerBumpf (p.x + PerBumpf (p.y))));
    q = 2. * q + 1.;
    a *= 0.75;
  }
  return f;
}

const float cHashM = 43758.54;

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

vec3 Hashv3f (float p)
{
  return fract (sin (p + vec3 (37., 39., 41.)) * cHashM);
}

vec3 Hashv3v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (dot (p, cHashVA3) + vec3 (0., cHashVA3.xy)) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (dot (p, cHashVA3) + vec4 (0., cHashVA3.xy, cHashVA3.x + cHashVA3.y)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2e (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 6; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.96875);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  }
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

float hash(float x) {
    return fract(x + 7.3215f * 1.8152f);
}

float hash2(vec2 a) {
    return fract((hash(a.y * 67.6417f) + hash(a.x * 54.3815f)) * 347.1158f);
}

float sqr(float x) {return x*x;}
float fastdist(vec2 a, vec2 b) {
    return sqr(b.x - a.x) + sqr(b.y - a.y);
}

vec3 rehash3(float x) {
    return vec3(fract((x + 0.583f * 59.3829f) * 394.3485f), fract((x + 0.892f * 83.6621f) * 145.3871f), fract((x + 0.763f * 47.9397f) * 957.9732f));
}

// x : distance (w/o sqrt) / y : hash
vec2 eval(float x, float y, int seed) {
    vec3 p[9];
    for (int _x = -1; _x < 2; _x++) for (int _y = -1; _y < 2; _y++) {
        vec2 _p = vec2(floor(x), floor(y)) + vec2(_x, _y);
        float h = hash2(_p + float(seed));
        p[_x + 1 + (_y + 1) * 3] = rehash3(h + float(seed)) + vec3(_p,0);
    }
    float m = 999999.9999f, z = 0.0f;
    for (int i = 0; i < 9; i++) {
        float d = fastdist(vec2(x, y), p[i].xy);
        if (d < m) { m = d; z = p[i].z; }
    }
    return vec2(m, z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.y * 5.0;

    vec2 n = eval(uv.x,uv.y,0);
    vec3 col = (1.0 - sqrt(n.x)) * rehash3(n.y);

    fragColor = vec4(col,1.0);
}

