//blur + tone pass


vec3 tone(vec3 x)
{
    return smoothstep(.1,.9, x) + x * 0.15;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = fragCoord/iResolution.xy;

    //set the scale of the blur based on the position of the screen
    float scale = length(uv - 0.5),steps = 30.;

    //store the blur color
    vec3 blur = vec3(0);

    //used to create blur effect by offseting texture coordinates
    vec2 offset = vec2(0);

    for(float i = 0.; i < steps; i++)
    {
        //decrease blur each step done
        scale *= 0.77;

        //add blur to thextrue
        blur += texture(iChannel0, uv+offset).rgb;

        //add to the offset
        offset -= normalize((2.* fragCoord -iResolution.xy)/iResolution.y) * scale/steps;

    }

    //normalize blur colors
    blur= (blur/(steps));

    //add tone to the blur
    vec3 color = tone(blur);

    //print to screen
    fragColor = vec4(color, 1.);
}

