mat3 raymat(vec3 ro, vec3 ta, float r)
{
    vec3 ww = normalize(ta -ro);
    vec3 uu = normalize(cross(ww, vec3(sin(r), cos(r), 0)));
    vec3 vv = normalize(cross(uu,ww));

    return mat3(uu, vv, ww);
}

vec4 map( vec3 o )
{
    float z = o.z;

    //loop we move away from the camera in a forward direction
    o.z = mod(o.z, 100.);

    //get the texture coordinates to apply the music pass
    vec2 texUV = (abs(vec2(o.x, o.z)) + 20.)/200.;

    //current music pass
    float musicVal =  texture(iChannel0, texUV).r;

    //set the color
    vec3 color = vec3(dot(texUV,texUV*2.), musicVal, 1.-o.z/200.) * 2. * musicVal * .75;

    //send the distance and 
    return vec4(color, o.y - musicVal * 50. - cos(o.z*0.15 + iTime));
}

vec4 raycast(vec3 ro, vec3 rd)
{
    float dist = 0.;
    for(int i = 0; i < 200; i++)
    {
        vec4 t = map(ro + rd * dist);

        if(t.w < 0.01)
            return t;

        dist += t.w;

        if(dist > 1000.)
            return vec4(0.);
    }


}

