vec4 sampleAvg(int size, int resdiv, vec2 uv) {

    vec4 sum = vec4(0.);
    int iter = 0;

    for (int y = -size/2; y <= size/2; y += resdiv) {
        for (int x = -size/2; x <= size/2; x += resdiv) {
            sum += texture(iChannel0, uv + vec2(x, y)/iResolution.xy);
            iter++; // figures out how many things get added
        }
    }

    return sum / vec4(iter);
}

