#define EPS 0.001
#define MAX_DIST 20.

vec3 rotateY(vec3 pos, float k) {
    float c = cos(k);
    float s = sin(k);
    return mat3(c,  0, -s,
                0,  1,  0,
                s,  0,  c) * pos;
}

vec3 changeOfBasis(vec3 pos, vec3 origin){
    vec3 zAxis = -origin;
    vec3 xAxis = normalize(rotateY(vec3(origin.x, 0, origin.z), 3.14 / 2.));
    vec3 yAxis = cross(xAxis, zAxis);

    return transpose(mat3(xAxis, yAxis, zAxis)) * pos;
}

float sdBox(vec3 pos) {
    vec3 d = abs(pos) - 0.8;
  	return min(max(d.x, max(d.y, d.z)), 0.) + length(max(d, 0.));
}

float sdSphere(vec3 pos) {
	return length(pos) - 1.;   
}

vec3 calcBoxPos() {
    float y = 1. - 4. * abs(round(iTime / 12.) - iTime / 12.);
    vec3 origin = vec3(sin(iTime / 0.3), y, cos(iTime / 0.3));
    origin.xz *= cos(y * 3.14 / 2.);
    return origin;
}

float calcDist(vec3 pos) {
    vec3 origin = calcBoxPos();

    float dBox = sdBox(changeOfBasis(1.5 * (pos + 3. * -origin), origin)) / 2. - 0.05;
    float dSphere = sdSphere(0.35 * pos);

    float morphK = 4.;
    return -log(exp(-morphK * dSphere) + exp(-morphK * dBox)) / morphK;
}

float rayMarch(vec3 rayO, vec3 rayD) {
    float distFromO = 0.;
    for (int i = 0; i < 100; ++i) {
	    float dS = calcDist(rayO + rayD * distFromO);
        distFromO += dS;
        if (dS < EPS || distFromO > MAX_DIST) break;
    }

    return distFromO;
}

vec3 calcNormal(vec3 pos) {
    float d = calcDist(pos);
	return normalize(vec3(d - calcDist(pos - vec3(EPS, 0,  0 )),
					  	  d - calcDist(pos - vec3( 0, EPS, 0 )),
					   	  d - calcDist(pos - vec3( 0,  0, EPS))));
}

vec3 calcLight(vec3 fragPos, vec3 lightPos, vec3 lightCol, vec3 camDir) {    
	vec3 normal = calcNormal(fragPos);
    vec3 lightDir = normalize(lightPos - fragPos);

    vec3 ambient = vec3(0.1);
    vec3 diffuse = vec3(max(dot(normal, lightDir), 0.0));
    vec3 specular = vec3(0.15 * pow(max(dot(-camDir, reflect(-lightDir, normal)), 0.), 16.));

    return lightCol * (ambient + diffuse + specular);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 xy = (fragCoord.xy - iResolution.xy / 2.) / min(iResolution.x, iResolution.y);

    vec3 camPos = vec3(0, 0, -10);
	vec3 camDir = normalize(vec3(xy, 1.));

    float dist = rayMarch(camPos, camDir);

    vec3 col = vec3(0.7, 0.6, 0.6);

    if (dist < MAX_DIST) { 
        col = calcLight(camPos + dist * camDir, 
                        vec3(5, 0, -15), 
                        vec3(0.9, 0.6, 0.6), 
                        camDir);
    } 

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= 1.0 - uv.yx;
    col *=  vec3(pow(uv.x * uv.y * 15., 0.1)); 

	fragColor = vec4(col, 1.);
}

// Drawing utilities
#define draw(d, c) color = mix(color, c, smoothstep(unit, 0.0, d))

// SDFs
float sdDisc(in vec2 p, in vec2 o, in float r) {
    return length(p - o) - r;
}

float sdLine(in vec2 p, in vec2 a, in vec2 b, in float t) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0)) - t;
}

vec4 weightsInQuad(in vec2 p, in vec2 a, in vec2 b, in vec2 c, in vec2 d) {
    vec2 pa = p - a, pb = p - b, pc = p - c, pd = p - d;
    vec2 ba = b - a, cb = c - b, dc = d - c, da = d - a;
    vec2 ca = c - a;

    // Total quad area (sum of two triangles forming the quad)
    float T = (abs(ba.x * ca.y - ba.y * ca.x) + abs(dc.x * ca.y - dc.y * ca.x)) * 0.5;

    float A = abs(ba.x * pa.y - ba.y * pa.x) * 0.5; // Area of triangle ABP
    float B = abs(cb.x * pb.y - cb.y * pb.x) * 0.5; // Area of triangle BCP
    float C = abs(dc.x * pc.y - dc.y * pc.x) * 0.5; // Area of triangle CDP
    float D = abs(da.x * pa.y - da.y * pa.x) * 0.5; // Area of triangle DAP

    // Since there aren't unique triangles opposite each vertex
    // I made the observation that the barycentric coordinate was
    // also 1-(A+B)/T where A and B were triangles connected to the
    // vertex so this might apply to a quad too. Summing this up for
    // each vertex results in 2 instead of 1 though so I then scaled
    // each of the weights by 1/2
    float wa = (1.0 - (D + A) / T);
    float wb = (1.0 - (A + B) / T);
    //float wc = (1.0 - (B + C) / T);
    //float wd = (1.0 - (C + D) / T);

    return vec4(wa, wb, 1.0 - wa, 1.0 - wb) * 0.5; // Change suggested by @Yusef28
}

vec3 tex(in vec2 uv) {
    return vec3(0.5 + 0.5 * mod(dot(floor(uv * 8.0), vec2(1.0)), 2.0), 0.0, 0.0);
}

void render(inout vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.5);

    vec2 a = vec2(-1.0, 1.0) + vec2(cos(iTime), sin(iTime)) * 0.5;
    vec2 b = vec2(-1.0, -1.0) + vec2(sin(3.0 * iTime), cos(3.0 * iTime)) * 0.2;
    vec2 c = vec2(1.0, -1.0) + vec2(cos(2.0 * iTime), sin(2.0 * iTime)) * 0.5;
    vec2 d = vec2(1.0, 1.0) + vec2(sin(iTime), cos(iTime)) * 0.2;

    vec4 t = weightsInQuad(uv, a, b, c, d);
    color = tex(vec2(0.0, 0.0) * t.x + vec2(0.0, 1.0) * t.y + vec2(1.0, 1.0) * t.z + vec2(1.0, 0.0) * t.w);

    draw(sdLine(uv, a, b, 0.005), vec3(1.0, 0.8, 0.0));
    draw(sdLine(uv, b, c, 0.005), vec3(1.0, 0.8, 0.0));
    draw(sdLine(uv, c, d, 0.005), vec3(1.0, 0.8, 0.0));
    draw(sdLine(uv, d, a, 0.005), vec3(1.0, 0.8, 0.0));

    draw(sdDisc(uv, a, 0.05), vec3(1.0));
    draw(sdDisc(uv, b, 0.05), vec3(1.0));
    draw(sdDisc(uv, c, 0.05), vec3(1.0));
    draw(sdDisc(uv, d, 0.05), vec3(1.0));

    fragColor += vec4(color, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0);
    render(fragColor, fragCoord);
    render(fragColor, fragCoord + vec2(0.5, 0.0));
    render(fragColor, fragCoord + vec2(0.5, 0.5));
    render(fragColor, fragCoord + 0.5);
    fragColor /= 4.0;
}

#define AA (8)

#define PI (3.14159265359)
#define TAU (PI * 2.0)

float moonRadius = 0.1;
float moonLitRadius = 0.11;
void sampleMoon(vec2 pos, inout vec3 color) {
    vec2 point = vec2(cos(fract(iTime / 16.0) * TAU), sin(fract(iTime / 16.0) * TAU)) * 0.8;
    if(length(pos - point) < moonRadius) {
        float offset = sqrt(moonLitRadius * moonLitRadius - moonRadius * moonRadius);
        vec2 lit = point - vec2(cos(fract(iTime / 16.0) * TAU), sin(fract(iTime / 16.0) * TAU)) * offset;
        if(length(pos - lit) < moonLitRadius) color = vec3(0.7, 0.9, 0.95);
        else color = vec3(0.2, 0.5, 0.6);
    }
}

float sunRadius = 0.15;
void sampleSun(vec2 pos, inout vec3 color) {
    vec2 point = vec2(cos(fract(iTime / 16.0 + 0.5) * TAU), sin(fract(iTime / 16.0 + 0.5) * TAU)) * 0.8;
    if(length(pos - point) < sunRadius) {
        color = vec3(0.9, 0.85, 0.6);
    }
}

float planetRadius = 0.4;
float planetLitRadius = 0.42;
void samplePlanet(vec2 pos, inout vec3 color) {
    vec2 point = vec2(0.0, 0.0);
    if(length(pos - point) < planetRadius) {
        float offset = sqrt(planetLitRadius * planetLitRadius - planetRadius * planetRadius);
        vec2 lit = point - vec2(cos(fract(iTime / 16.0) * TAU), sin(fract(iTime / 16.0) * TAU)) * offset;
        if(length(pos - lit) < planetLitRadius) color = vec3(0.3, 0.7, 0.45);
        else color = vec3(0.10, 0.4, 0.35);
    }
}

struct Outline {
    vec2 pos;
    float r;
    vec3 color;
    float width;
};

void outline(vec2 pos, Outline ol, inout vec3 color) {
    if(abs(length(pos - ol.pos) - ol.r) <= ol.width / 2.0) color = ol.color;
}

vec3 samplePoint(vec2 pos) {
    vec3 color = vec3(0.015);

    if(length(pos) < 1.1) {
        color = vec3(0.05, 0.08, 0.1);
    }
    outline(pos, Outline(vec2(0.0, 0.0), 1.2, vec3(0.05, 0.08, 0.1), 0.055), color);
    outline(pos, Outline(vec2(0.0, 0.0), 1.3, vec3(0.05, 0.08, 0.1), 0.03), color);
    outline(pos, Outline(vec2(0.0, 0.0), 1.37, vec3(0.05, 0.08, 0.1), 0.015), color);
    samplePlanet(pos, color);
    sampleMoon(pos, color);
    sampleSun(pos, color);

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Center is 0, 0
    vec2 pos = (fragCoord - iResolution.xy / 2.0) * 2.0 / iResolution.y * vec2(1, -1);

    // Supersampling
    vec3 color = vec3(0.0);
    for(int y = 0; y < AA; y++) {
        for(int x = 0; x < AA; x++) {
            vec2 subpos = pos + vec2(
                float(x + 1) / float(AA + 1) * 2.0 - 1.0,
                float(y + 1) / float(AA + 1) * 2.0 - 1.0
            ) / iResolution.y;
            color += samplePoint(subpos * 1.0);
        }
    }

    fragColor = vec4(color / float(AA * AA), 1.0);
}

#define PI 3.14159265359

/*** math heavy part for spherical harmonics ***/

#define SQRT2PI 2.506628274631

// factorial
float fac(int n) {
    float res = 1.0;
    for (int i = n; i > 1; i--)
        res *= float(i);
    return res;
}

// double factorial
float dfac(int n) {
    float res = 1.0;
    for (int i = n; i > 1; i-=2)
        res *= float(i);
    return res;
}

// fac(l-m)/fac(l+m) but more stable
float fac2(int l, int m) {
    int am = abs(m);
    if (am > l)
        return 0.0;
    float res = 1.0;
    for (int i = max(l-am+1,2); i <= l+am; i++)
        res *= float(i);
    if (m < 0)
        return res;
    return 1.0 / res;
}

// complex exponential
vec2 cexp(vec2 c) {
    return exp(c.x)*vec2(cos(c.y), sin(c.y));
}

// complex multiplication
vec2 cmul(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

// complex conjugation
vec2 conj(vec2 c) { return vec2(c.x, -c.y); }

// complex/real magnitude squared
float sqr(float x) { return x*x; }
float sqr(vec2 x) { return dot(x,x); }

// associated legendre polynomials
float legendre_poly(float x, int l, int m) {
    if (l < abs(m))
        return 0.0;
    if (l == 0)
        return 1.0;
    float mul = m >= 0 ? 1.0 : float((~m&1)*2-1)*fac2(l,m);
    m = abs(m);
    // recursive calculation of legendre polynomial
    float lp1 = 0.0;
    float lp2 = float((~m&1)*2-1)*dfac(2*m-1)*pow(max(1.0-x*x, 1e-7), float(m)/2.0);
    for (int i = m+1; i <= l; i++) {
        float lp = (x*float(2*i-1)*lp2 - float(i+m-1)*lp1)/float(i-m);
        lp1 = lp2; lp2 = lp;
    }
    return lp2 / mul;
}

// spherical harmonics function
vec2 sphere_harm(float theta, float phi, int l, int m) {
    float abs_value = 1.0/SQRT2PI*sqrt(float(2*l+1)/2.0*fac2(l,m))
                        *legendre_poly(cos(theta), l, m);
    return cexp(vec2(0.0,float(m)*phi))*abs_value;
}

// associated laguerre polynomial L_s^k(x) with k > 0, s >= 0
float laguerre_poly(float x, int s, int k) {
    if (s <= 0)
        return 1.0;
    float lp1 = 1.0;
    float lp2 = 1.0 - x + float(k);
    for (int n = 1; n < s; n++) {
        float lp = ((float(2*n + k + 1) - x) * lp2 - float(n+k)*lp1)/float(n+1);
        lp1 = lp2; lp2 = lp;
    }
    return lp2;
}

// radius dependent term of the 1/r potential eigenstates in atomic units
float radius_term(float r, int n, int l) {
    float a0 = 1.0; // atomic radius
    float rr = r / a0;
    float n2 = 2.0 / float(n) / a0;
    float n3 = n2 * n2 * n2;
    float p1 = sqrt(n3 * fac2(n, l) * float(n-l)/float(n));
    float p2 = exp(-rr/float(n));
    float p3 = pow(n2*r, float(l));
    float p4 = laguerre_poly(n2*r, n-l-1, 2*l+1);
    return p1 * p2 * p3 * p4;
}

vec2 hydrogen(vec3 pos, int n, int l, int m) {
    float r = length(pos);
    float sin_theta = length(pos.xy);
    float phi = sin_theta > 0.0 ? atan(pos.x, pos.y) : 0.0;
    float theta = atan(sin_theta, pos.z);//atan(sin_theta, pos.z);

    return sphere_harm(theta, phi, l, m) * radius_term(r, n, l);
}

/*** Now the rendering ***/

vec3 rotateX(vec3 pos, float angle) {
    return vec3(pos.x, cmul(pos.yz, cexp(vec2(0.,-angle))));
}

#define SELECT_GRID 7.0
void get_nlm(out int n, out int l, out int m, in vec2 fragCoord) {
    vec2 mouse = iMouse.xy/iResolution.xy;
    int t;

    bool selection = false;
    if (mouse.x + mouse.y > 0.0) {// && iMouse.z > 0.5) {
        vec2 coord = iMouse.z > 0.5 ? fragCoord : iMouse.xy;
        ivec2 cell = ivec2(coord/iResolution.y*SELECT_GRID);
        //t = (int(SELECT_GRID) - cell.y - 1) + cell.x * int(SELECT_GRID);
        t = cell.x;
        n = cell.y + 1;
        selection = t < n*(n+1)/2 || iMouse.z > 0.5;
    }
    if (!selection) {
        /*vec2 coord = fragCoord;
        ivec2 cell = ivec2(coord/iResolution.y*SELECT_GRID);
        cell.x += 0;
        t = (int(SELECT_GRID) - cell.y - 1) + cell.x * int(SELECT_GRID);*/
        t = int(iTime*0.5);

        if (t == 0)
            n = 1;
        else {
            float x = float(t);
            // see https://en.wikipedia.org/wiki/Tetrahedral_number
            n = int(ceil(pow(3.*x+sqrt(9.*x*x-1./27.), 1./3.) + pow(3.*x-sqrt(9.*x*x-1./27.), 1./3.) - 0.995));
        }
        t -= ((n*(n-1)*(2*n-1))/6+(n*(n-1))/2)/2;
    }

    l = int(floor(sqrt(0.25 + float(2*t)) - 0.5));
    m = t - int(floor(0.5*float(l + l*l)));
}

float spos(float x, float s) {
    return 0.5*(x*x/(s+abs(x))+x+s);
}
float smax(float a, float b, float s) {
    return a+spos(b-a,s);
}

#define SURFACE_LEVEL 0.3
float globalSdf(vec3 pos, out vec3 color, in vec2 fragCoord) {
    int n, m, l;
    get_nlm(n, l, m, fragCoord);

    // evaluate spherical harmonics
    vec2 off = cexp(vec2(0, iTime));

    vec2 H = hydrogen(pos*float(n*n+1)*1.5, n, l, m);
    if (m != 0) H = cmul(H, off);

    H *= float((l+1)*l+n*n)*sqrt(float(n)); // visual rescaling

    float crit2 = 0.3*(length(pos)+0.05);

    color = H.x > 0. ? vec3(1.0,0.6,0.15) : vec3(0.2,0.4,0.5);
    //color = vec3(max(vec3(0.02),(sin(float(n) + vec3(0., 2.1, 4.2)))));
    float d = (SURFACE_LEVEL - abs(H.x))*crit2;
    if (m == 0)
        return smax(d, 0.707*(pos.x+pos.y), 0.02);
    return d;

    float arg = atan(H.x, H.y);
    color = vec3(max(vec3(0.02),(sin(arg + vec3(0., 2.1, 4.2)))));
    return (0.20 - length(H))*crit2;
}

vec3 calculate_normal(in vec3 world_point, float sd, in vec2 fragCoord) {
    const vec3 small_step = vec3(0.001, 0.0, 0.0);
    vec3 col;
    float gradient_x = globalSdf(world_point + small_step.xyy, col, fragCoord) - sd;
    float gradient_y = globalSdf(world_point + small_step.yxy, col, fragCoord) - sd;
    float gradient_z = globalSdf(world_point + small_step.yyx, col, fragCoord) - sd;
    vec3 normal = vec3(gradient_x, gradient_y, gradient_z);
    return normalize(normal);
}

vec4 lighting(vec3 cp, vec3 color, vec3 normal, vec3 rdir) {
    // from https://www.shadertoy.com/view/ts3XDj
    // geometry
    vec3 ref = reflect( rdir, normal );

    // material		
    vec3 mate = color.rgb;

    float occ = clamp(length(cp)*0.7, 0.2, 0.5);//min(color.g, 1.0);//clamp(2.0*tmat.z, 0.0, 1.0);
    float sss = -pow(clamp(1.0 + dot(normal, rdir), 0.0, 1.0), 1.0);

    // lights
    vec3 lin  = 2.5*occ*vec3(1.0)*(0.6 + 0.4*normal.y);
         lin += 1.0*sss*vec3(1.0,0.95,0.70)*occ;	

    // surface-light interacion
    vec3 col = mate.xyz * lin;
    return vec4(col, 1.0);
}

#define NUMBER_OF_STEPS 128
#define MINIMUM_HIT_DISTANCE 0.005
#define MAXIMUM_TRACE_DISTANCE 6.0
vec4 raymarch(in vec3 rpos, in vec3 rdir, in vec2 fragCoord) {
    float t = 0.0;
    float closest_t = 0.0;
    float closest_t_r = MAXIMUM_TRACE_DISTANCE;
    float closest_t_r2 = MAXIMUM_TRACE_DISTANCE;
    float closest_t_r3 = MAXIMUM_TRACE_DISTANCE;
    vec4 col = vec4(0,0,0,0);
    for (int i = 0; i < NUMBER_OF_STEPS; i++) {
        vec3 cp = rpos + t * rdir;

        vec3 color = vec3(0.0);
		float sd = globalSdf(cp, color, fragCoord);

        if (abs(sd) < 0.7*MINIMUM_HIT_DISTANCE) {
            vec3 normal = calculate_normal(cp, sd, fragCoord);
            col = lighting(cp, color, normal, rdir);
            break;
        }

        closest_t_r3 = closest_t_r2;
        closest_t_r2 = closest_t_r;
        if (sd < closest_t_r) {
            closest_t = t;
            closest_t_r = sd;
        }

        if (t > MAXIMUM_TRACE_DISTANCE)
            break;

        t += sd;
    }
    if (abs(closest_t_r3) > MINIMUM_HIT_DISTANCE) {
        return col;
    }
    vec3 cp = rpos + closest_t * rdir;
    vec3 color = vec3(0.0);
    float sd = globalSdf(cp, color, fragCoord);
    vec3 normal = calculate_normal(cp, sd, fragCoord);
    float a = 1.0-abs(closest_t_r3)/MINIMUM_HIT_DISTANCE;
    vec4 col2 = lighting(cp, color, normal, rdir);
    col2.a = a;
    return mix(col, col2, a);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    float rot = 0.5*sin(iTime*0.5) * PI/3.0;

    if (iMouse.z > 0.5) {
        // selection on click
        uv = fract(fragCoord/iResolution.y*SELECT_GRID)*2.0-1.0;
        rot = -0.5;
    }

     // camera movement
	vec3 cam_pos = 3.0 * rotateX(vec3(0,1,0), rot);
    vec3 look_at = vec3(0);   
    vec3 look_up = vec3(0,0,1);
    // camera matrix
    vec3 ww = normalize(look_at - cam_pos);
    vec3 uu = normalize(cross(ww, look_up));
    vec3 vv = normalize(cross(uu, ww));
	// create perspective view ray
    vec3 rpos = cam_pos;
	vec3 rdir = normalize( uv.x*uu + uv.y*vv + 2.0*ww );

    vec4 col = raymarch(rpos, rdir, fragCoord);
    vec3 bg = vec3(0.3) * clamp(1.0-2.6*length(fragCoord/iResolution.xy-0.5)*0.5,0.0,1.0);
    col = vec4(mix(bg, col.rgb, col.a), 1.0);
    col = vec4(pow(clamp(col.rgb,0.0,1.0), vec3(0.4545)), 1.0);

    if (iMouse.z > 0.5) {
        // selection on click
        ivec2 select = abs(ivec2(fragCoord/iResolution.y*SELECT_GRID)-ivec2(iMouse.xy/iResolution.y*SELECT_GRID));
        if (select.x + select.y == 0) {
            // draw selection box
            vec2 absuv = abs(uv);
            vec2 cmp = min(absuv, vec2(0.9));
            float d = length(absuv - cmp);
            float fac = max(0.05 - abs(d - 0.05), 0.0)/0.025;
            ivec2 checkers = ivec2(round(uv * 3.5));
            fac *= float((checkers.x + checkers.y + 1)&1);
            col = mix(col, vec4(1.0), fac);
        }
    }

    fragColor = col;
}

#define ITERS 12
const float scale = 2.;
const float scale1 = 1.275;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/15.0;
    //uv.y += (iTime)/25.0;
    float s1 = scale1*scale+uv.y+iTime/4.0;
    for(int c=0;c<ITERS;c++){
        col_prev = col;
        for(int i=0;i<ITERS;i++)
        {
            uv= -fract(-uv-((vec2(uv.x/scale1-uv.y/scale1,uv.y/scale-uv.x/scale)/(scale))))/scale1;
            uv.x *= -scale1;
            uv = fract(uv.yx/s1)*s1;
            uv.y /= scale1;
        }
        col[2] = abs(fract(uv.y)-fract(uv.x));
        col = ((col+col_prev.yzx))/2.125;
	}
    fragColor = vec4(vec3(col*3.0),1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy) * 0.5f;

    float NoiseVal = fract(sin(dot(fragCoord * 2.0f, vec2(12.2929, 78.293))) + 39389.0f);

    //vec3 col = vec3(NoiseVal);
    // Output to screen
    float G = 0.1f/clamp( sin( iTime * uv.x ), 0.001f, 1.0f );

    vec3 col = vec3(fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453) * sin(iTime  + uv.y),
        fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453) * G,
            fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453) * sin(sin(iTime) + .5f * NoiseVal * uv.x));
    //col = vec3(  );
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mUV = iMouse.xy / iResolution.xy;

    //Pick the two exponents based on the mouse coordinates.
    vec2 exponents = mix(vec2(0.11111), vec2(5.0),
                         //Apply a curve so the small values are more spread out:
                         pow(mUV, vec2(2.0)));

    //Calculate the curve.
    float graphY = pow(uv.x, mix(exponents.x, exponents.y, uv.x));

    //Color the area under the curve black.
    fragColor = vec4(vec3(step(graphY, uv.y)), 1);
}

#define ITERS 12
const float scale = 2.;
const float scale1 = 1.15;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    vec2 uv = -(fragCoord.yx*10.0-iResolution.yx)/iResolution.y/15.0;
    //uv.y += (iTime)/25.0;
    float s1 = (iTime/4.-uv.x)/4.;
    for(int c=0;c<ITERS;c++){
        col_prev = col;
        for(int i=0;i<ITERS;i++)
        {
            uv= -fract(-uv-((vec2(uv.x/scale-uv.y/scale1,uv.y/scale-uv.x/scale1)/(scale))))/scale1;
            uv.x *= -scale1;
            uv = fract(-uv.yx)+fract(uv/(8.)+s1);
            uv.y /= scale1;
        }
        col[2] = abs(fract(uv.y)-fract(uv.x));
        col = ((col+col_prev.yzx))/2.125;
	}
    fragColor = vec4(vec3(col*3.0),1.0);

}

vec2 N22(vec2 p) {
    vec3 a = fract(p.xyx * vec3(123.34, 234.34, 456.65));
    a += dot(a, a + 34.45);
    return fract(vec2(a.x * a.y, a.y * a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    float t = iTime * .3;
    float minDist = 1.;

    uv *= 6.;
    vec2 grid_uv = fract(uv) - .5;  // .5 <= uv <= .5 for each grid cell
    vec2 grid_id = floor(uv);
    vec2 cell_id;

    for (int i = -1; i <= 1; i += 1) {
        for (int j = -1; j <= 1; j += 1) {
            vec2 grid_offset = vec2(i, j);

            vec2 n = N22(grid_id + grid_offset);
            vec2 position = grid_offset + sin(n * t) * .5;

            vec2 diff = grid_uv - position;
            float l2_dist = length(diff);
            float l1_dist = abs(diff.x) + abs(diff.y);

            // float dist = l1_dist;
            // float dist = l2_dist;
            float dist = clamp(-l1_dist * .7 + l2_dist * 1.5, 0., 1.);
            if (dist < minDist) {
                minDist = dist;
                cell_id = grid_id + grid_offset; // id of closest point to current pixel
            }
        }
    }

    vec3 col = clamp(vec3(minDist) * .3 + .5 * vec3(abs(cell_id.yxx)/5.), 0., 1.);
    fragColor = vec4(col, 1);
}

#define PI 3.1415926

const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;

const float speed = 100.0;


vec2 Keyboard(vec2 offset) {

    float velocity = 0.1; // This will cause offset to change by 0.01 each time an arrow key is pressed


    vec2 left = texelFetch(iChannel2, ivec2(KEY_LEFT, 0), 0).x * vec2(-1, 0);
    vec2 up = texelFetch(iChannel2, ivec2(KEY_UP,0), 0).x * vec2(0, 1);
    vec2 right = texelFetch(iChannel2, ivec2(KEY_RIGHT, 0), 0).x * vec2(1, 0);
    vec2 down = texelFetch(iChannel2, ivec2(KEY_DOWN, 0), 0).x * vec2(0, -1);

    offset += (left + up + right + down) * velocity;

    return offset;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    // get poins 
    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    // set surface formula
    vec3 v = vec3(p.x, p.y, sqrt(1.0 - p.x * p.x - p.y * p.y));
    // create normals 
    vec3 n = normalize(v);

    // https://en.wikipedia.org/wiki/Distortion_(optics)
