float CamDist = 1.;
float CamShake = 1.;

#define ViewerDist (CamDist)

#define tanFOVh (1.6)

void getCamQuatAndPos(out vec4 camQuat, out vec3 camPos)
{
    vec2 ang=vec2(iMouse.y/iResolution.y*12.8,iMouse.x/iResolution.x*12.8);
    vec3 coffs=vec3(0);
    vec4 r=sin(vec4(.7,1,1.3,1.7)*iTime*.5);
    if(iMouse.x<.5) { 
        ang=vec2(-1.57+sin(iTime*.1-1.0)*-1.,-sin(iTime*.07-1.)*3.);
        coffs=vec3(iTime*.2,0,0);
    }
    vec4 q=vec4(0,0,0,1);
    // calc rotation quaternion for scene rotation
    q=multQuat(q,axAng2Quat(vec3(1,0,0), ang.x));
    q=multQuat(q,axAng2Quat(vec3(0,0,1), ang.y));
    q=normalize(q+.01*r*CamShake);
    camQuat = inverseQuat(q);
    camPos = transformVecByQuat(vec3(0,0,ViewerDist+r.z*.025*CamShake),camQuat)+coffs;
}

void apply_trafo_r(inout vec3 pos, float transl)
{
    vec3 cp; vec4 cq;
    getCamQuatAndPos(cq,cp);
    pos=transformVecByQuat(pos,cq)+cp*transl;
}

void apply_trafo_r(inout vec3 pos) { apply_trafo_r(pos,1.0); }

void apply_frustum_r(inout vec3 pos, inout vec3 dir, vec2 fragCoord)
{
    dir=normalize(vec3((fragCoord-.5*iResolution.xy)/iResolution.x*2.,-1./tanFOVh));
    apply_trafo_r(dir,0.);
}


float distTorus(vec3 p, float R, float r)
{
    return length(vec3(p.xy-normalize(p.xy)*R,p.z))-r;
}

// iq's exponantial smooth-min funcs
float smin( float a, float b, float k )
{
    k=3./k;
    float res = exp2( -k*a ) + exp2( -k*b );
    return -log2( res )/k;
}

float smax(float a, float b, float k) { return -smin(-a,-b,k); }

// iq's polynomial smooth-min func
float smin_( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float smax_(float a, float b, float k) { return -smin_(-a,-b,k); }

float tri(float x) { return 1.-4.*abs(fract(x*0.159154943092+.25)-.5); }

float tooth(float x) {
    //return clamp(sin(x)*1.1,-1.,1.);
    return clamp(tri(x)*1.4,-1.,1.);
}

// iq's box-funcs
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdRect( vec2 p, vec2 b )
{
  vec2 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,q.y),0.0);
}

float distGear(vec3 p, float R, int numTeeth)
{
    float w=.2*R;
    float lp=length(p);
    float lpxy=length(p.xy);
    float d=10000.;
    float ang=atan(p.y,p.x);
    d=abs(p.z)-w*1.2/*+lpxy/R*w*.0*/;
    vec3 p2=p;
    float a=-.5*PI/float(numTeeth); // 1/4 of tooth angle
    vec2 cs=cos(a-vec2(0,PI2*.25));
    p2.xy=mat2(cs.x,cs.y,-cs.y,cs.x)*p.xy;
    p2.xy=abs(p2.xy);
    if(p2.x>p2.y) p2.xy=p2.yx;

    // --- spokes ---
    // edge cut
    //float d2=abs(p.z)-R*.05;
    //d2=smax(d2,-sdRect(abs(p2.xy)-vec2(.55)*R,vec2(.25*R))+.22*R,.05*R);
    //d2=smin(d2,.28*R+.2*abs(p.z)-abs(.45*R-lpxy),.05*R);
    //d=max(d,d2);
    // rect profile
    d=max(d,.28*R+.2*abs(p.z)-abs(.45*R-lpxy));
    d=smin(d,smax(abs(p2.x),abs(p2.z)*1.7,.006)-.09*R+.02*p2.y,.0035);
    // rotated rect profile
    //d=max(d,.28*R+.2*abs(p.z)-abs(.45*R-lpxy));
    //d=smin(d,abs(p2.x)+abs(p2.z)-.07*R,.0035);

    // --- teeth
    // ...by tooth func
    //d=max(d,length(p+vec3(p.xy/lpxy,0)*.1*R*tooth(ang*float(numTeeth)))-R);
    // ...by doing actual tooth (gives better distance field to avoid overstepping)
    float dang=a*2.; // 1/2 of tooth angle  (alternatingly with pos/neg flank)
    float tidx=floor((ang+.5*dang)/dang);
    float flank=mod(tidx,2.)*2.-1.;
    float ang0=(tidx)*dang; // center angle of tooth flank
    vec2 cs0=cos(ang0-vec2(0,PI2*.25));
    float Rfl=R*1.;
    float d2=length(p.xyz-vec3((cs0*(R*sqrt(2.)-Rfl)+flank*cs0.yx*vec2(-1,1)*Rfl)*sqrt(.5),0.))-Rfl;
    d2=min(d2,lp-R*.9);
    d2=max(d2,lp-R*1.1);
    d=max(d,d2);
    return d;
}

// iq's line distance
float distLine( vec3 p, vec3 a, vec3 b )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h );
}


float SideLength = 1.;

#ifndef GEAR_ANIM
uniform float GearAng;
#else
#define GearAng (.25*PI2*sin(iTime*PI2/3.)*1.)
#endif

// put num gears into -ang/2..ang/2 (first and last gear only half)
float distGearsAng(vec3 p, float R, float r, float ang, int num, float axang, float YOff, float soff)
{
    float GearR=SideLength*.09;
    float ph0=atan(GearR/R);
    float ph1=(ang-ph0-ph0)/float(num-2)/2.;
    float pang=atan(p.y,p.x);
    float ang2=-ang*.5;
    float ang1=ang2;
    int i;
    for(i=min(iFrame,0);i<num;i++) {
        if(pang>=ang2) {
            ang1=ang2;
            if      (i==num-2) ang2=ang*.5;
            else if (i==0)     ang2+=ph0+ph1;
            else               ang2+=ph1+ph1;
        }
        if(pang<ang2) {
            break;
        }
    }
    float s=float(i%2)*2.-1.;
    float rg1=((i==0)    ?GearR:R*tan(ph1))*.95;
    float rg2=((i==num-2)?GearR:R*tan(ph1))*.95;
    float d=10000.;
    vec3 pg;

    float raild=GearR*.5;

    int numTeeth=12;

    int n1,n2;
    float axang1,axang2;
    float gearAng1,gearAng2;
    gearAng1=gearAng2=GearAng;
    axang1=axang2=axang;
    vec4 axq=axAng2Quat(vec3(0,1,0),axang2);
    n1=n2=numTeeth;

    float zo,gao;
    pg=transformVecByQuat(p, axAng2Quat(vec3(0,0,1),-ang1));
    zo=sin((ang1-ang*.5)/ang*PI2)*GearR*YOff;
    pg.z+=zo*soff;
    gao=(ang1+ang*.5)/ang*PI2*GearR*YOff*.5/rg1;
    pg.x-=R;
    pg=transformVecByQuat(pg,axq);
    d=min(d,max(length(pg.xy)-.01*R,abs(pg.z)-raild));
    pg=transformVecByQuat(pg,axAng2Quat(vec3(0,0,1),(gearAng1+gao)*s));
    d=min(d,distGear(pg,rg1,n1));

    pg=transformVecByQuat(p, axAng2Quat(vec3(0,0,1),-ang2));
    zo=sin((ang2-ang*.5)/ang*PI2)*GearR*YOff;
    pg.z+=zo*soff;
    gao=(ang2+ang*.5)/ang*PI2*GearR*YOff*.5/rg2;
    pg.x-=R;
    pg=transformVecByQuat(pg,axq);
    d=min(d,max(length(pg.xy)-.01*R,abs(pg.z)-raild));
    //d=min(d,distLine(pg,vec3(0,0,-raild),vec3(0,0,raild))-.01*R);
    pg=transformVecByQuat(pg,axAng2Quat(vec3(0,0,1),(gearAng2+gao)*-s));
    d=min(d,distGear(pg,rg2,n2));

    //vec2 rail=cos(axang-vec2(0,PI*.5))*raild;
    //d=min(d,distTorus(p-vec3(0,0,1)*rail.x,R-rail.y,.01*R));
    //d=min(d,distTorus(p+vec3(0,0,1)*rail.x,R+rail.y,.01*R));
    //...is this faster than 2 the toruses above ?!
    vec2 cs=cos(-axang-vec2(0,PI*.5));
    vec2 p2=mat2(cs,cs.yx*vec2(-1,1))*vec2(length(p.xy)-R,p.z);
    p2.y=abs(p2.y);
    d=min(d,length(p2-vec2(0,raild))-.01*R);
    return d;
}

float Thick;

float AxAng;

#define T1 1
#define T2 2
#define O1 3
#define O2 4
#define O3 5

int mtl=0;


float distOctCell(vec3 p, float axAng, int dtnum) 
{
    float ZOff=0., soff=-1.;
    float s=1.;
    if (p.x-p.y<0.) s=-1.;
    if(abs(p.y)>abs(p.x)) { 
        p.xy=p.yx*vec2(1,-1); 
        if(mtl==O2) { p.y*=-1.; soff*=-1.; }
        if(mtl==O3) p.z*=-1.;
        if(mtl==O1) p.z*=-1.;
    }
    if(p.x<0.) { 
        if(mtl==O2) { p.z*=1.; soff*=-1.; }
        if(mtl==O3) p.z*=-1.;
        if(mtl==O1) p.z*=-1.;
    }
    p.x=-abs(p.x);
    float d=1000.;
    float R=SideLength/2./sqrt(3.);
    float r=Thick*SideLength;
    float d2;
    float x0=SideLength*.5;

    if(mtl==O2) { ZOff=.0792; }

    d2=distGearsAng((p-vec3(-x0,0,0)).xzy*vec3(1,-1,1),R,r,atan(sqrt(2.))*2.,5+dtnum,axAng*s,ZOff,soff);
    d=min(d,d2);
    return d;
}


float distTetrCell(vec3 p) 
{
    if(p.x<.0) { p.yz=p.zy*vec2(1,-1); }
    p.x=abs(p.x)*-1.;
    float d=1000.;
    float R=SideLength/2./sqrt(3.);
    float r=Thick*SideLength;
    float d2;
    float x0=SideLength*(.5/sqrt(2.));

    d2=distGearsAng((p-vec3(-x0,0,0)).xyz,R,r,atan(sqrt(.5))*2.,3,PI*.5,0.,1.); d=min(d,d2);
    return d;
}

float distLine(vec3 p, vec3 t)
{
    return length(p-dot(p,t)/dot(t,t)*t);
}


vec4 getRand(ivec3 v)
{
    return texelFetch(iChannel0,(v.xy+ivec2(7,17)*v.z)%256,0);
}

float PosCos=0.;

const vec4 qzp45=Q_Z_45;
const vec4 qxp45=Q_X_45;
const vec4 qxm45=-Q_X_45*vec4(1,1,1,-1);
const vec4 qyp90=Def_multQuat(Q_Z_45,Q_Y_90);
const vec4 qxp90=Def_multQuat(Q_Z_45,Q_X_90);

#define sel2(x1, x2, f)    ( mix(x1,x2,step(1.,f)) )
#define sel3(x1, x2, x3, f) ( sel2(sel2(x1,x2,f),x3,f-1.) )

float distTruch(vec3 p)
{
    p += PosCos*SideLength * ( cos(p.zxy*1.7/SideLength+0.*iTime) - .1*sin(p.zxy*1.7/SideLength*10.+0.*iTime) );
    mat3 m=mat3( 0,1,1, 1,0,1, 1,1,0 )*SideLength/sqrt(2.);
    vec4 q;
    vec3 v=inverse(m)*p;
    vec3 v0=floor(v);
    v-=v0;
    vec3 pc=m*(v0+.5);

    float d=10000.;
    float ro=sqrt(2.)/4.*SideLength;
    float d2;
    p-=pc;
    vec4 r=getRand(ivec3(v0));

    #ifndef AVOID_IFS
       // this is more overlookable, but...
       // ...windows wont compile this (guess angle is not coping with the many if's)

       //lower tetrahedron
       if      (v.x+v.y+v.z<1.) { q=qxm45; mtl=T1; d2=distTetrCell(transformVecByQuat(p+ro,q)); }
       //upper tetrahedron
       else if (v.x+v.y+v.z>2.) { q=qxp45; mtl=T2; d2=distTetrCell(transformVecByQuat(p-ro,q)); }
       //center octahedron
       else if (r.x>.66)        { q=qxp90; mtl=O1; d2=distOctCell (transformVecByQuat(p,q),PI/6.,0);  }
       else if (r.x>.33)        { q=qyp90; mtl=O2; d2=distOctCell (transformVecByQuat(p,q),PI*.5,-1); }
       else                     { q=qzp45; mtl=O3; d2=distOctCell (transformVecByQuat(p,q),PI/6.,0);  }
       d=d2;

    #else

       float region=floor(v.x+v.y+v.z);  // 0->tetra1, 1->octahedron, 2->tetra2

       mtl=(r.x<.333)?O2:((r.x<.666)?O1:O3);
       mtl=(region==0.)?T1:((region==2.)?T2:mtl);

       d=(region==1.)?
            distOctCell (transformVecByQuat(p,sel3(qyp90,qxp90,qzp45,r.x*3.)),PI/sel2(2.,6.,r.x*3.),(r.x<.333)?-1:0):
            distTetrCell(transformVecByQuat(p-ro*(region-1.),mix(qxm45,qxp45,region*.5)));

    #endif

    //#define PRISM_CELLS
    #ifdef PRISM_CELLS
    vec3 p0;
    p0=m*floor(v0+v+.5);
    d=min(d,distLine(p-p0,m*vec3(1,0,0))-.003);
    d=min(d,distLine(p-p0,m*vec3(0,1,0))-.003);
    d=min(d,distLine(p-p0,m*vec3(0,0,1))-.003);
    #endif
    return d;
}

float dist(vec3 p)
{
    float d=distTruch(p);
    return d;
}

vec3 getGradF(vec3 p, float eps) 
{ 
    vec2 d=vec2(eps,0); 
    float d0=dist(p);
    return vec3(dist(p+d.xyy)-d0,dist(p+d.yxy)-d0,dist(p+d.yyx)-d0)/eps; 
}

vec3 getGrad_(vec3 p, float eps) 
{ 
    vec2 d=vec2(eps*.5,0); 
    float d0=dist(p);
    return vec3(dist(p+d.xyy)-dist(p-d.xyy),dist(p+d.yxy)-dist(p-d.yxy),dist(p+d.yyx)-dist(p-d.yyx))/eps; 
}

// suggestion from Shane - windows compile improvements
// Normal function. It's not as fast as the tetrahedral calculation, but more symmetrical.
vec3 getGrad(in vec3 p, float eps) {

    vec2 e = vec2(eps*.5, 0);
    // This mess is an attempt to speed up compiler time by contriving a break... It's 
    // based on a suggestion by IQ. I think it works, but I really couldn't say for sure.
    float sgn = 1.;
    float mp[6];
    vec3[3] e6 = vec3[3](e.xyy, e.yxy, e.yyx);
    for(int i = min(iFrame, 0); i<6; i++){
		mp[i] = dist(p + sgn*e6[i/2]);
        sgn = -sgn;
        if(sgn>2.) break; // Fake conditional break;
    }

    return normalize(vec3(mp[0] - mp[1], mp[2] - mp[3], mp[4] - mp[5]));
}

void march(inout vec3 pos, vec3 dir)
{
    float eps=.0001;
    float dsum=0.;
    for(int i=min(0,iFrame);i<120;i++)
    {
        float d=dist(pos)*.66;
        pos+=dir*d;
        dsum+=d;
        if (d<eps*dsum*5. || dsum>10.0) break;
        if(i>1000) break; // Fake break. (not sure if necessary)
    }
}

#ifndef RandTex 
#define RandTex iChannel0
#endif

vec4 myenv(vec3 pos, vec3 dir, float period)
{
    vec4 r=vec4(0);
    float sc=.2*period;
    int num=8;
    for(int i=min(0,iFrame);i<num;i++) { r+=texture(RandTex,dir.xy/dir.z*sc+iTime*.003*step(0.,dir.z)); sc*=.5; }
    r/=float(num);
    vec3 col=mix(vec3(.6,.7,.8)+.6*r.y,r.zwx*vec3(.5,.3,0),1.-smoothstep(-.3,.3,dir.z));
    return vec4(col,1);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #ifdef BORDER_LOD_SPEEDUP
    if(fragCoord.y>Res.y*.5) { discard; return; }
    vec2 uv2=fragCoord/Res*2.-vec2(1,0);
    if(max(abs(uv2-.5).x,abs(uv2-.5).y)<.24) { discard; return; }
    fragCoord=(fragCoord.x<Res.x*.5)?fragCoord+.25*Res:(fragCoord-vec2(.5*Res.x,0))*2.;
    #endif

    vec3 light=normalize(vec3(0,0,1));
    vec3 pos,dir;
    pos=vec3(0);
    apply_trafo_r(pos);
    apply_frustum_r(pos,dir,fragCoord);

    vec3 pos0=pos;

    march(pos,dir);

    vec3 pos1=pos;

    vec3 n=normalize(getGrad(pos,.001));
    //vec3 c0=vec3(1,.85,.77)+.0;
    vec3 c0=vec3(1);
    //vec3 c0=vec3(1,.86,.8);

    #ifdef CELL_COLORS
       // tetrahedra configurations red, orange
       if (mtl==T1) c0=vec3(1,.5,0)*.4+.6;
       if (mtl==T2) c0=vec3(1,0,0)*.4+.6;
       // octahedra configurations green, blue, magenta
       if (mtl==O1) c0=vec3(.5,1,.5)*.4+.6;
       if (mtl==O2) c0=vec3(.5,.5,1)*.4+.6;
       if (mtl==O3) c0=vec3(1,0,1)*.4+.6;
    #endif

    vec4 r=texelFetch(iChannel0,ivec2(fragCoord)%256,0);

    #if 1
    float ao=1.;
    //ao*=clamp(dist(pos+n/ 10.)* 10.,0.,1.);
    //ao*=clamp(dist(pos+n/ 20.)* 20.,0.,1.);
    float sc=SideLength*.003;
    vec3 na=normalize(n)+.12*(r.xyz-.5);
    float f=.7;
    /*ao*=clamp(dist(pos+na*sc)/sc,0.,1.)*(1.-f)+f; sc*=2.; f=pow(f,.9);
    ao*=clamp(dist(pos+na*sc)/sc,0.,1.)*(1.-f)+f; sc*=2.; f=pow(f,.9);
    ao*=clamp(dist(pos+na*sc)/sc,0.,1.)*(1.-f)+f; sc*=2.; f=pow(f,.9);
    ao*=clamp(dist(pos+na*sc)/sc,0.,1.)*(1.-f)+f; sc*=2.; f=pow(f,.9);
    ao*=clamp(dist(pos+na*sc)/sc,0.,1.)*(1.-f)+f; sc*=2.; f=pow(f,.9);
    ao*=clamp(dist(pos+na*sc)/sc,0.,1.)*(1.-f)+f; sc*=2.; f=pow(f,.9);
    ao*=clamp(dist(pos+na*sc)/sc,0.,1.)*(1.-f)+f; sc*=2.; f=pow(f,.9);
    ao*=clamp(dist(pos+na*sc)/sc,0.,1.)*(1.-f)+f; sc*=2.; f=pow(f,.9);*/
    // suggestion from Shane - windows compile improvements
    // Looping the unrolled AO statements. 
    for(int i=min(0, iFrame);i<8;i++){
       ao*=clamp(dist(pos+na*sc)/sc,0.,1.)*(1.-f)+f; sc*=2.; f=pow(f,.9);
       if(ao>1e8) break; // Fake break.
    }
    ao=ao*.7+.3;
    #else
    float ao=1.;
    float sc=SideLength*.3,scmin=SideLength*.01;
    vec3 na=normalize(getGrad(pos,scmin*.2))+.12*(r.xyz-.5);
    float df=dist(pos+na*scmin*.2)/(scmin*.2);
    for(int i=min(0,iFrame);i<30;i++)
    {
        //na=normalize(getGrad(pos,sc*.2));
        //df=dist(pos+n*sc*.01)/(sc*.01);
        float ao2=clamp(dist(pos+na*sc)/(sc/df),0.,1.);
        ao*=mix(ao2,1.,
        //1.-sqrt(sc)*.2
        .85
        );
        sc*=.7;
        if(sc<scmin) break;
    }
    #endif

    vec3 lightCol=vec3(.97,1.03,1);

    fragColor.xyz=c0;
    fragColor.xyz*=(n*.03+.97);
    fragColor.xyz*=(clamp(dot(n,light),0.,1.)*.4+.6);
    fragColor.xyz*=ao;
    vec3 R=reflect(normalize(pos1-pos0),n);
    fragColor.xyz=fragColor.xyz*.85+.3*clamp(dot(R,light),0.,1.)*lightCol*c0;
    //fragColor.xyz=fragColor.xyz*.7+.3*myenv(vec3 (0), R, .1).xyz;
    vec3 dp=pos-pos0;
    vec3 dpn=normalize(dp);
    fragColor.xyz=mix(fragColor.xyz,mix(vec3(-.0,.05,.0),lightCol,clamp(dot(dp,light)/5.+.1,0.,1.))+dpn*.03,1.-exp(-length(pos-pos0)/5.));
    fragColor.xyz*=fragColor.xyz+.2;

    fragColor.w=length(dp);
}

